#define PI 3.14159265359
#define E 2.7182818284
#define GR 1.61803398875
#define MAX_DIM (max(iResolution.x,iResolution.y))

#define time (sin(float(__LINE__)*GR/PI+GR/E)*iTime/PI)

float saw(float x)
{
    x/= PI;
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}
vec4 saw(vec4 x)
{
    return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv0 = uv;
    uv = uv*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;


	float sum = 0.;
    float border = 1.;

    float bordered = smoothstep(0.4, 0.6, saw(time*E));

    const float min_outer_radials = 8.0;
    const float max_outer_radials = 32.0;


    float outer_count = min_outer_radials+(max_outer_radials-min_outer_radials)*saw(time);

    for(float i = 0.0; i < max_outer_radials; i+=1.0)
    {
        if(i > outer_count)
            break;
        float theta = float(i)/float(outer_count)*PI*2.0+time;
        vec2 p1 = vec2(cos(theta), sin(theta))*.75;

        const float min_inner_radials = 8.0;
        const float max_inner_radials = 32.0;

    	float inner_count = min_inner_radials+(max_inner_radials-min_inner_radials)*saw(time);

        for(float j = 0.0; j < max_inner_radials; j+=1.0)
        {
            if(j > inner_count)
                break;
        	float theta2 = float(j)/float(inner_count)*PI*2.0-time;
            vec2 p2 = p1+vec2(cos(theta2), sin(theta2));

            float d = 
                    (p2.y-p1.y)*uv.x-
                    (p2.x-p1.x)*uv.y+
                    p2.x*p1.y-p2.y*p1.x/
                    length(p2-p1);
            float maxd = .01;

            float vd = abs(d);

            if(vd < maxd)
            {
                float value = ((length(p1-uv)));
                sum += value*(border);
                border *= vd/maxd;
            }
        }
    }
    vec4 last = texture(iChannel0, uv0);
    float b = clamp((1.-border),0., 1.);
    fragColor = vec4((cos(iTime*PI+sum*PI*GR+vec3(0., 4.*PI/3.,2.*PI/3.))*.5+.5), 1.0)*b
        +last*(1.-pow(b, 4.)/GR)-1./60.;
}

