    map += draw(p);
    for(float f = 0.; f < max_iterations; f+=1.){
        float iteration = (f/max_iterations+1.);
        float angle = sin(time*iteration)/PI-1.*PI/4.;

        vec2 a = vec2(1., 1.);
        vec2 b = vec2(0., 1.);
        vec2 c = vec2(0., 0.);
        vec2 d = vec2(1., 0.);

        p = p*2.-1.;

        vec2 s = vec2(.75);
        vec2 o = vec2(.5, 0.);
        mat2 m = rotate(angle);

		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;

        a = a*m; b = b*m; c = c*m; d = d*m;
        a *= s; b *= s; c *= s; d *= s;
        a += o; b += o; c += o; d += o;


        /*
        //= a*.5+.5; b = b*.5+.5; c = c*.5+.5; d = d*.5+.5;
		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;
