        vec2 a2 = a*vec2(-1., 1.);
        vec2 b2 = b*vec2(-1., 1.);
        vec2 c2 = c*vec2(-1., 1.);
        vec2 d2 = d*vec2(-1., 1.);
        if(p.x > 0.)
        	p = (invBilinear( p, a, b, c, d ));
        else
        	p = (invBilinear( p, a2, b2, c2, d2 ));

        map += draw(p);
    }

    fragColor = vec4(flux(map*PI)*clamp(map, 0., 1.), 1.0 );
