float snoise(vec3 v)
  {
  const vec2 C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i = floor(v + dot(v, C.yyy) );
  vec3 x0 = v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  // x0 = x0 - 0.0 + 0.0 * C.xxx;
  // x1 = x0 - i1 + 1.0 * C.xxx;
  // x2 = x0 - i2 + 2.0 * C.xxx;
  // x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy; // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i);
  vec4 p = permute( permute( permute(
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 ))
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3 ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z); // mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ ); // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1),
                                dot(p2,x2), dot(p3,x3) ) );
  }

vec3 flux(float x)
{
    return vec3(cos(x*1.0)*.5+.5,
                cos(x*1.0+4.0*PI/3.0)*.5+.5,
                cos(x*1.0+2.0*PI/3.0)*.5+.5);
}
vec4 cometwars(vec2 uv)
{
    uv.y = uv.y*3.0-1.5;
    uv.x = uv.x*2.0-.5;

    uv.x *= uv.x > 0.0 ? .65 : 1.0;
    uv.x = uv.x < 0.0 ? uv.x*1.5 : pow(uv.x, .75)*1.25;

    float turns = 32.0;//uv.x < 0.0 ? 24.0: 32.0;
    float theta = atan(uv.y, uv.x);
    float r = length(uv)+sin(theta*turns)*.05;

    float spikyness = .75;

    float tailyness = clamp(uv.x+1.0, 0.0, 2.0)/2.5;
    float shortenyness = clamp(-uv.x, 0.0, 1.0);
    float bendsign = (theta > PI || theta < 0.0) ? -1.0: 1.0;

    theta += (tailyness+iTime*1.1375)*bendsign;

    r += saw(theta*turns)*spikyness+shortenyness;

    float edgefade = pow(saw(clamp(r/2.0-1.0, -0.5, .5)/.5*PI), .125);
    float rockyness = pow(clamp(1.0-length(uv)*GR, 0.0, 1.0), .75);
    float comet = rockyness+edgefade;//*saw(sin(atan(uv.y, uv.x)*turns/2.0-iTime*bendsign*15.6789)+pow(length(uv), 2.75)*turns*2.0-iTime*PI*15.0);

    vec4 specs = vec4(flux(2.0*PI*snoise(vec3(r*length(uv)-iTime*PI, theta*2.0, 0.0))), 1.0);
    if(1.0 > r)
        return vec4(0.3, 0.5, specs.b, specs.b)*comet+rockyness;//vec4(, 1.0)+clamp(.75-comet, 0.0, 1.0);
    else 
        return vec4(1.0-saw(clamp(r-.85, 0.0, .5)/.5*PI));
}

vec2 hash( vec2 p )
{
    p = vec2( dot(p,vec2(127.5,313.7)),dot(p,vec2(239.5,185.3)));
    return fract(sin(p)*43458.3453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 persp = uv*2.0-1.0;
    persp.x *=  iResolution.x/iResolution.y;
    persp += vec2(cos(iTime*1.123456789)*.35-.35, cos(iTime*1.6789)*.25);

    vec2 crosshair = vec2(-0.75, 0.0);

    vec2 duv = persp-crosshair;

    duv = RotateZ(duv, cos(iTime*.25)*PI);

    float theta = atan(duv.y, duv.x);
    float section = mod(theta, PI/2.0)/(PI/2.0);
    section = section < .15 || section >.85 ? 1.0-(1.0-abs(section-.5)/.5)/.3: 0.0;
    section = pow(section, .125);
 	float r = length(duv);

    float d = .45;
    float outerscalar = 1.25;
    float outerw = .05;

    float outer = r > d*outerscalar && r < d*outerscalar+outerw ? section*cos(clamp(r-d*outerscalar, 0.0, outerw)/outerw*PI-PI/2.0): 0.0;
    float grid = r > d ? outer : pow(cos(r/d*PI*9.0)*.5+.5, 2.0);
    grid += (1.0-clamp(abs(duv.x/outerw*2.0), 0.0, 1.0))*pow(clamp(d*1.75-abs(duv.y), 0.0, 1.0)/d, .125)+
        	(1.0-clamp(abs(duv.y/outerw*2.0), 0.0, 1.0))*pow(clamp(d*1.75-abs(duv.x), 0.0, 1.0)/d, .125);
    grid = pow(grid, 2.0);

    uv += vec2(cos(iTime*1.234), cos(iTime*1.456))*.05;
    vec4 c = cometwars(uv);
    uv -= vec2(iTime*.5, 0.0);
    float scale = 40.;
    float radius = 1.5;
    vec2 gridPosition = floor(uv * scale) / scale;
    vec2 randomOffset = hash(gridPosition) * 2. - 1.;
    vec2 localGridPositionCenter = fract(uv * scale) - .5;
    float stars = mix(0., 1., step(length(localGridPositionCenter + randomOffset * .5), .1));

    fragColor = vec4(0.3, 0.5, 1.0, 1.0)*c.b*(1.0-grid)+vec4(1.0, 0.0, 0.0, 1.0)*grid;
    fragColor += clamp(1.0-length(fragColor), 0.0, 1.0)*stars;
}

