// learned voronoi particle tracking from wyatt's ecosystem
void swap (inout vec4 a, vec2 U, vec2 r) {
    vec4 n = A(U+r);
    if (length(U-n.xy)<length(U-a.xy)){
      a = n;
    }
}

void mainImage( out vec4 Q, in vec2 U)
{
    // FIND NEAREST PARTICLE
    vec4 a = A(U);
    swap(a,U,vec2(1,0));
    swap(a,U,vec2(0,1));
    swap(a,U,vec2(-1,0));
    swap(a,U,vec2(0,-1));
    swap(a,U,vec2(1,1));
    swap(a,U,vec2(-1,-1));
    swap(a,U,vec2(-1,1));
    swap(a,U,vec2(1,-1));

    Q = a;
    Q.y -= 0.35;

    float randomX = iResolution.x * rand(iTime) * 3.; 
    // the *3. slows down the rate of adding particles ("slices") by sending 2/3 out of range
    float halfY = iResolution.y/2.;
    if (length(U-vec2(randomX, halfY))<length(U-a.xy)){
      Q = vec4(randomX, halfY, 0., 1.);
    }

    // Click to paint in color
    if ((iMouse.z>0.&&length(iMouse.xy-U)<1.)) {
        Q = vec4(U,pow(randomX,2.),1.);
    }   
}

