/******************************************************************************************

 deferred  saturday sketch #7
   Matthias Scherba (@szczm_)


 This is more of a technical sketch. It includes:
 - deferred rendering, with different materials (yes I tried thousands of lights,
   yes, it was satisfying, no, it didn't look good, shush)
 - approximated ACES tonemaping (credits go to Krzysztof Narkowicz)
 - a shadow mapped directional light
 - "flat" look (achieved by quantization of the normal)
 - proper bloom
 - nice color "grading"

 Some might say a few of these are unnecessarily redundant for ShaderToy. Yes, I know. (: :) (:

 The code is spread out between tabs for two reasons:
  - to declutter the common tab, and
  - to ensure separation between different stages of rendering

 As the shadow resolution depends on the buffer resolution, which depends on
 the viewport resolution, be sure to go fullscreen!


 I tried to comment the code nicely, but if you have any questions, leave a comment
 or hit me up on Twitter:

 twitter.com/szczm_


 FUTURE TO-DO MAYBE WHO KNOWS:
 - investigate occasional shadow clipping at some angles (weird shadow buffer bias?)
 - minor buffer artifacts/fireflies?

******************************************************************************************/

// Image tab: Apply final post processing and tone mapping.


#define LIT_BUFFER (iChannel0)
#define BLOOM_BUFFER (iChannel1)
#define SHADOW_BUFFER (iChannel2)
#define G_BUFFER (iChannel3)


float normpdf(in float x, in float sigma)
{
	return 0.39894 * exp(-0.5*x*x / (sigma*sigma)) / sigma;
}

// Credits: Krzysztof Narkowicz
vec3 acesApprox(vec3 v)
{
    v *= 0.6f;
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    time = iTime;

    vec2 coords = fragCoord / iResolution.xy;

#if defined(DEBUG_VIEW)
    coords = coords * 2.0 - 0.5;
#endif

    fragColor = texture(LIT_BUFFER, coords);

    vec3 bloom = vec3(0.0);

    const float SIGMA = 3.0;
    const float STRETCH = 2.5;
    const float LOD = 3.0;

    const int KERNEL_WIDTH = 10;
    const int KERNEL_HEIGHT = KERNEL_WIDTH / 2;

    const float BLOOM_INTENSITY = 0.015;


    // Calculate bloom
    for (int x = -KERNEL_WIDTH; x <= KERNEL_WIDTH; x++)
    {
        float xx = float(x) / float(KERNEL_WIDTH);
        xx = normpdf(xx, SIGMA);

        for (int y = -KERNEL_HEIGHT; y <= KERNEL_HEIGHT; y++)
        {
            float yy = float(y) / float(KERNEL_HEIGHT);
            yy = normpdf(yy, SIGMA);

            bloom += textureLod(BLOOM_BUFFER, coords + vec2(float(x), float(y)) / iResolution.xy * STRETCH, LOD).rgb * xx * yy;
        }
    }

    // Color correction
    fragColor.rgb = pow(fragColor.rgb, vec3(1.5, 1.5, 0.6));

    // Tonemapping
    fragColor.rgb = acesApprox(fragColor.rgb);

    // Apply PS2 era bloom
    fragColor.rgb += bloom * BLOOM_INTENSITY;

    // Vi~gnet~te
    fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 1.0, 0.5) * 0.4, 1.3 * pow(length(coords - 0.5), 3.0));


// Beware, traveler! Below be Ugly Debug Dragons!

#if defined(DEBUG_VIEW)
    vec2 debugCoords = mod(coords * 2.0, vec2(1.0));

    vec4 g_buffer = texture(G_BUFFER, debugCoords);
    vec3 bloom_buffer = texture(BLOOM_BUFFER, debugCoords).rgb;
    vec3 shadow_buffer = texture(SHADOW_BUFFER, debugCoords).rgb;

    vec3 viewDir = rayDirection(iResolution.xy, debugCoords * iResolution.xy);

    vec3 eye = getEye();
    mat4 viewToWorld = viewMatrix(eye, CAMERA_TARGET, UP);
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float depth;
    vec3 normal;
    Material material;

    unpackGbuffer(g_buffer, depth, normal, material);


    vec3 position = eye + depth * worldDir;


    float lightDist;
	vec3 lightPoint = worldPointToLightPoint(position, lightDist);

    float shadowDist;
    unpackShadow(texture(SHADOW_BUFFER, lightPoint.xy), shadowDist);

    float shadow = shadowDist - lightDist;
    shadow = step(0.0, shadow);

    if (clamp(lightPoint.x, 0.0, 1.0) != lightPoint.x
     || clamp(lightPoint.y, 0.0, 1.0) != lightPoint.y
     || shadowDist >= MAX_DIST_SHADOW - 1.0) // _why_
        shadow = 1.0;



    vec3 debugLeft = texture(LIT_BUFFER, debugCoords).rgb;
    debugLeft = mix(debugLeft, bloom_buffer, step(0.5, coords.y));

    vec3 debugRight = g_buffer.aaa;
    debugRight = mix(debugRight, material.albedo, step(0.5, coords.y));

    vec3 debugBottom = vec3(shadow);
    debugBottom = mix(debugBottom, vec3(shadowDist) / MAX_DIST_SHADOW, step(0.0, coords.x));
    debugBottom = mix(debugBottom, lightPoint, step(0.5, coords.x));
    debugBottom = mix(debugBottom, shadow_buffer, step(1.0, coords.x));

    vec3 debugTop = g_buffer.rgb;
    debugTop = mix(debugTop, 0.5 + 0.5 * normal, step(0.0, coords.x));
    debugTop = mix(debugTop, vec3(material.shininess), step(0.5, coords.x));
    debugTop = mix(debugTop, vec3(material.roughness / MAX_ROUGHNESS), step(1.0, coords.x));


    fragColor.rgb = mix(fragColor.rgb, debugLeft, step(0.0, -coords.x));
    fragColor.rgb = mix(fragColor.rgb, debugRight, step(1.0, coords.x));
    fragColor.rgb = mix(fragColor.rgb, debugBottom, step(-0.0, -coords.y));
    fragColor.rgb = mix(fragColor.rgb, debugTop, step(1.0, coords.y));

    return;
#endif
}

#undef LIT_BUFFER
#undef BLOOM_BUFFER
#undef G_BUFFER
#undef SHADOW_BUFFER

