float pi = 3.14159265358979323846264;

vec3 _00 = vec3(252.0, 64.0, 136.0) / 255.0;
vec3 _01 = vec3(255.0, 237.0, 12.0) / 255.0;
vec3 _02 = vec3(151.0, 25.0, 121.0) / 255.0;
vec3 _03 = vec3(80.0, 246.0, 219.0) / 255.0;
vec3 _10 = vec3(78.0, 249.0, 177.0) / 255.0;
vec3 _11 = vec3(80.0, 121.0, 250.0) / 255.0;
vec3 _12 = vec3(180.0, 69.0, 250.0) / 255.0;
vec3 _13 = vec3(244.0, 66.0, 100.0) / 255.0;

// from gold noise
float PHI = 1.61803398874989484820459 * 00000.1; // Golden Ratio   
float PI  = 3.14159265358979323846264 * 00000.1; // PI
float SQ2 = 1.41421356237309504880169 * 10000.0; // Square Root of Two

float rand(vec2 coordinate, float seed)
{
    return fract(tan(distance(coordinate*(seed+PHI), vec2(PHI, PI)))*SQ2);
}

float test(float i, float j)
{
    if(i == j)
        return 1.0;

    return 0.0;
}

vec3 rand_col(vec2 index)
{   
    vec3 rc = vec3(0.0);

    rc += _00 * test(index.y, 0.0) * test(index.x, 0.0);
    rc += _01 * test(index.y, 0.0) * test(index.x, 1.0);
    rc += _02 * test(index.y, 0.0) * test(index.x, 2.0);
    rc += _03 * test(index.y, 0.0) * test(index.x, 3.0);
    rc += _10 * test(index.y, 1.0) * test(index.x, 0.0);
    rc += _11 * test(index.y, 1.0) * test(index.x, 1.0);
    rc += _12 * test(index.y, 1.0) * test(index.x, 2.0);
    rc += _13 * test(index.y, 1.0) * test(index.x, 3.0);
    rc += _00 * test(index.y, 2.0) * test(index.x, 0.0);
    rc += _01 * test(index.y, 2.0) * test(index.x, 1.0);
    rc += _02 * test(index.y, 2.0) * test(index.x, 2.0);
    rc += _03 * test(index.y, 2.0) * test(index.x, 3.0);
    rc += _10 * test(index.y, 3.0) * test(index.x, 0.0);
    rc += _11 * test(index.y, 3.0) * test(index.x, 1.0);
    rc += _12 * test(index.y, 3.0) * test(index.x, 2.0);
    rc += _13 * test(index.y, 3.0) * test(index.x, 3.0);

    return rc;
}

vec4 eye(vec2 coord)
{
    // useful vars
    vec2 ii = floor(mod(coord, 4.0));
    vec2 uv = mod(coord, 1.0);
    vec2 aspect = iResolution.xy / iResolution.yy;

    vec2 ndc = uv * 2.0 - 1.0;
    vec2 ppc = ndc * aspect;
    vec2 ppn = normalize(ppc);

    vec2 index = (ii);

    vec3 col = vec3(0.0, 0.0, 0.0);

    // eye
    vec2 ebc = ndc;
    ebc.y = pow(abs(ebc.y), 1.0/1.5);
    float r = step(length(ebc), 0.7);
    vec3 eyelid = vec3(r, r, r);

    // eye ball
    float cd = 0.0;

    float t = iTime * 0.00001;
    float pt = t - 0.001;
    float nt = t + 0.001;

    float prx = floor(rand(index, pt) * 4.0);
    float pry = floor(rand(index, pt) * 4.0);

    float rx = floor(rand(index, t) * 4.0);
    float ry = floor(rand(index, t) * 4.0);

    if(length(index - vec2(rx, ry)) < 0.2 )
        cd = 1.0;

    float open = 1.0/2.2;
    float closed = 1.0/20.0;
    float closedness = mix(open, closed, cd);
    ebc.y = pow(abs(ebc.y), closedness);
    r = step(length(ebc), 0.7);
    vec3 eyeball = vec3(r, r, r);

    // pupil
    r = step(length(ppc), 0.2);
    vec3 pupil = vec3(r, r, r);
    vec3 pupil_col = vec3(length(ppc));

    // iris
    r = step(length(ppc), 0.4);
    vec3 iris = vec3(r, r, r);

    r = length(ppc);
    float at = atan(ppn.x, ppn.y) + sin(iTime);
    float mat = mod(at, pi / 10.0);

    //vec3 iris_col = mix(vec3(0.2, 0.5, 0.9), vec3(0.2, 0.9, 0.9), mat);

    vec3 iris_col = rand_col(index.yx);

    col = (eyeball * vec3(1.0, 1.0, 1.0)) * (1.0 - iris);
   	col += iris_col * iris * eyeball * (1.0 - pupil);
    col += pupil_col* pupil * eyeball;
    col += rand_col(index) * (1.0 - eyeball) * eyelid;

    return vec4(col, eyelid);
}

vec3 bg_func(vec2 uv)
{
    float x = uv.x + iTime;
    float y = uv.y;
    float yv = uv.y * 2.0 - 1.0;
    float m = 0.0;

    if(yv < 0.0)
    	m = step(mod(x + y, 0.4), 0.2);
    else
    	m = 1.0 - step(mod(x - y, 0.4), 0.2);

    vec3 bg3 = mix(_02, _03, sin(iTime*10.0));
    vec3 bg4 = mix(_12, _13, cos(iTime*10.0));

    vec3 bg2 = mix(mix(_00, _01, sin(iTime * 5.0) * 0.5 + 0.5), bg3, sin(iTime * 5.0));
    vec3 bg1 = mix(mix(_10, _11, cos(iTime * 5.0) * 0.5 + 0.5), bg4, sin(iTime * 5.0));

    vec3 bg = mix(bg1, bg2, m);

    return bg;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 _uv = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 texel = 1.0 / iResolution.xy;
    vec2 aspect = iResolution.xy / iResolution.yy;

    uv.x += sin(iTime * 10.0 + uv.y * 10.0) * 0.01;

    // eyes   
    vec2 num = vec2(1.0, 1.0) + vec2(floor(mod(iTime / 4.0, 8.0)));
    vec4 eyes = eye(uv * num + vec2(iTime, 0.0));

    // bg
	vec3 bg = bg_func(uv);

    // led / pixels
    float led_size = 0.00005;
    float edge = 0.04;
    vec2 led_size2 = vec2(led_size, led_size) / aspect;
    vec2 mm = mod(_uv * aspect, led_size);
    vec2 mv = smoothstep(0.0, led_size, mm);
    vec2 led = step(mv, vec2(edge) * aspect);
    float third = 1.2/3.0;
    float led_r = step(mv.x, third);
    float led_g = step(mv.x, third * 2.0) * (1.0 - led_r);
    float led_b = step(mv.x, third * 3.0) * (1.0 - led_r) * (1.0 - led_g);
    vec3 led_rgb = vec3(led_r, led_g, led_b) * clamp(1.0 - (led.x + led.y), 0.0, 1.0);

   	bg *= led_rgb;

    vec3 col = bg * (1.0 - eyes.a);
    col += eyes.a * eyes.rgb;

    //col = bg;

    // output
    fragColor = vec4(col.rgb, 1.0);
}

//
// Signed circumcircle
//
// NOTE:
//   I can canonically obtain "sign" based on "InCircle test",
//   but it doesn't give canonical AA friendly pixel value
//   since it is the volume of a tetrahedra in a quite unrelated 3d space.
//   So, "USE_IN_CIRCLE_TEST = false" switches to direct computation
//   of circumcircle and signed area of a triangle.
//

#define M_PI 3.14159

bool USE_IN_CIRCLE_TEST = true;
float IN_CIRCLE_TEST_VOLUME_SCALE = 1000.0;

float SCALE = 1.2;
float SCALE_TIME = .4;

float CHECKER_SCALE = 4.0;
vec3  CHECKER_COLOR0 = vec3(0.20);
vec3  CHECKER_COLOR1 = vec3(0.60);

float POINT_RADIUS = 3.0;
vec3  POINT_COLOR = vec3(0.0, 1.0, 1.0);

vec3  TRIANGLE_COLOR = vec3(0.0, 1.0, 1.0);
float TRIANGLE_ALPHA = 0.75;

vec3  DISK_COLOR = vec3(1.0);
float DISK_ALPHA = 0.75;

float AA = 2.0;

//
// Utils
//

vec2 unitVector2(float t) {
  return vec2(cos(2.0 * M_PI * t), sin(2.0 * M_PI * t));
}
float mix2(float f00, float f10, float f01, float f11, vec2 uv) {
  return mix(mix(f00, f10, uv[0]), mix(f01, f11, uv[0]), uv[1]);
}

void intersect_Line_Line(
    vec2 p1, vec2 v1, vec2 p2, vec2 v2,
    out float t1, out float t2) {
  // assume v1, v2: linear indep.
  // p1 + t1 v1 = p2 + t2 v2
  // <=>  (p1 - p2) + [v1, -v2] {t1, t2} = 0
  // <=>  {t1, t2} = inv([v1, -v2]) (-p1 + p2)
  vec2 t1t2 = inverse(mat2(v1, -v2)) * (-p1 + p2);
  t1 = t1t2[0];
  t2 = t1t2[1];
}

float distance_Point_LineSegment(vec2 p, vec2 q1, vec2 q2) {
  // < p - (q + t v), v> = 0  <=>  t = < p - q, v > / < v, v >

  vec2 v = q2 - q1;
  float dot_vv = dot(v, v);
  if (dot(v, v) < 0.0001) {
    return distance(p, q1);
  }
  float t = dot(p - q1, v) / dot_vv;
  float s = clamp(t, 0.0, 1.0);
  return distance(p, q1 + s * v);
}


//
// Noise
//

float hash11(float t) {
  return fract(sin(1.0 + t * 123456.789) * 123456.789);
}

float hash21(vec2 v) {
  return hash11(hash11(v[0]) + 2.0 * hash11(v[1]));
}

vec2 hash12(float v) {
  return vec2(hash11(v), hash21(vec2(v, 1.0)));
}

vec2 hashGradient2(vec2 uv) {
  return unitVector2(hash21(uv));
}

float gradientNoise2(vec2 uv) {
  vec2 uvi = floor(uv);
  vec2 uvf = uv - uvi;
  float f00 = dot(hashGradient2(uvi + vec2(0.0, 0.0)), uvf - vec2(0.0, 0.0));
  float f10 = dot(hashGradient2(uvi + vec2(1.0, 0.0)), uvf - vec2(1.0, 0.0));
  float f01 = dot(hashGradient2(uvi + vec2(0.0, 1.0)), uvf - vec2(0.0, 1.0));
  float f11 = dot(hashGradient2(uvi + vec2(1.0, 1.0)), uvf - vec2(1.0, 1.0));
  float t = mix2(f00, f10, f01, f11, smoothstep(vec2(0.0), vec2(1.0), uvf));
  return (t / 0.7 + 1.0) * 0.5;
}

vec2 someNoise(float hash_seed, float conti_seed) {
  // Taking hashed direction to break inherent velocity pattern of gradientNoise2
  vec2 p = hash12(hash_seed);
  vec2 dir1 = unitVector2(p[0]);
  vec2 dir2 = unitVector2(p[1]);
  vec2 v = vec2(
      gradientNoise2(123.456 * vec2(hash_seed) + 456.789 + conti_seed * dir1),
      gradientNoise2(456.123 * vec2(hash_seed) + 789.456 + conti_seed * dir2));

  // Tonemap [0, 1] to make movement more "dynamical"
  return smoothstep(0.0, 1.0, v);
}


//
// Misc
//

float signedDistanceToChecker(vec2 uv, float scale) {
  uv *= scale;
  vec2 uvi = floor(uv);
  vec2 uvf = uv - uvi;
  float dist = min(min(min(uvf.x, uvf.y), 1.0 - uvf.x), 1.0 - uvf.y);
  dist /= scale;
  bool is_even_spot = mod(uvi.x + uvi.y, 2.0) == 0.0;
  return is_even_spot ? -dist : dist;
}

float signedDistanceToTriangle(vec2 uv, vec2 p0, vec2 p1, vec2 p2) {
  vec2 v1 = p1 - p0;
  vec2 v2 = p2 - p0;
  float signed_area = cross(vec3(v1, 0.0), vec3(v2, 0.0)).z;
  if (abs(signed_area) <= 0.0000001) {
    return 1000.0;
  }

  // Barycentric coord (uv = p0 + s * v1 + t * v2)
  mat2 v1v2 = mat2(v1, v2);
  vec2 st = inverse(transpose(v1v2) * v1v2) * transpose(v1v2) * (uv - p0);
  bool is_inside = st.x >= 0.0 && st.y >= 0.0 && (st.x + st.y) <= 1.0;

  float dist = 1000.0;
  dist = min(dist, distance_Point_LineSegment(uv, p0, p1));
  dist = min(dist, distance_Point_LineSegment(uv, p1, p2));
  dist = min(dist, distance_Point_LineSegment(uv, p2, p0));

  return is_inside ? -dist : dist;
}

float signedDistanceToCircle(vec2 uv, vec2 center, float radius) {
  return distance(uv, center) - radius;
}

float inCircleTestVolume(vec2 q, vec2 p0, vec2 p1, vec2 p2) {
  mat3 circle_test_mat = mat3(
    (p0 - q).x, (p0 - q).y, dot(p0, p0) - dot(q, q),
    (p1 - q).x, (p1 - q).y, dot(p1, p1) - dot(q, q),
    (p2 - q).x, (p2 - q).y, dot(p2, p2) - dot(q, q));
  float circle_test_det = determinant(circle_test_mat);
  return circle_test_det;
}

vec2 circumcircleCenter(vec2 p0, vec2 p1, vec2 p2) {
  // assume non degenerate triangle
  vec2 q1 = (p0 + p1) / 2.0;
  vec2 q2 = (p0 + p2) / 2.0;
  vec2 u1 = p1 - p0;
  vec2 u2 = p2 - p0;
  vec2 v1 = vec2(cross(vec3(0.0, 0.0, 1.0), vec3(u1, 0.0)));
  vec2 v2 = vec2(cross(vec3(0.0, 0.0, 1.0), vec3(u2, 0.0)));
  float t1, t2;
  intersect_Line_Line(q1, v1, q2, v2, t1, t2);
  return q1 + t1 * v1;
}

// anti alias
float smoothBoundaryCoverage(float signed_distance, float boundary_width) {
  return 1.0 - smoothstep(0.0, 1.0, signed_distance / boundary_width + 0.5);
}

// view transform with aspect ratio preserved
mat3 invViewTransform(vec2 center, float scale_y) {
  vec2 Res = iResolution.xy;
  vec2 size = vec2(scale_y * Res.x / Res.y, scale_y);
  vec2 a = center - size / 2.0;
  float Sy = scale_y / Res.y;
  mat3 xform = mat3(
       Sy,   0,   0,
        0,  Sy,   0,
      a.x, a.y, 1.0
  );
  return xform;
}


//
// Main
//

void mainImage(out vec4 frag_color, vec2 frag_coord) {
  mat3 inv_view_xform = invViewTransform(vec2(0.5, 0.5), SCALE);
  float inv_view_scale = inv_view_xform[0][0];

  vec2 uv = vec2(inv_view_xform * vec3(frag_coord, 1.0));
  float t = SCALE_TIME * iTime;

  vec3 color;
  {
    // Draw checker
    float sd = signedDistanceToChecker(uv, CHECKER_SCALE);
    float coverage = smoothBoundaryCoverage(sd / inv_view_scale, AA);
    color = mix(CHECKER_COLOR0, CHECKER_COLOR1, coverage);
  }
  {
    // Prepare random 3 points
    vec2 ps[3];
    for (int i = 0; i <= 2; i++) {
      ps[i] = someNoise(float(i), t);
    }

    // Draw circumcircle
    if (USE_IN_CIRCLE_TEST) {
      float volume = inCircleTestVolume(uv, ps[0], ps[1], ps[2]);
      volume *= IN_CIRCLE_TEST_VOLUME_SCALE;
      float coverage = 1.0 - smoothstep(-1.0, 1.0, volume);
      color = mix(color, DISK_COLOR, DISK_ALPHA * coverage);

    } else {
      vec2 center = circumcircleCenter(ps[0], ps[1], ps[2]);
      float radius = distance(center, ps[0]);
      float sd = signedDistanceToCircle(uv, center, radius);

      float signed_area = cross(vec3(ps[1] - ps[0], 0.0), vec3(ps[2] - ps[0], 0.0)).z;
      sd *= sign(signed_area);

      float coverage = smoothBoundaryCoverage(sd / inv_view_scale, AA);
      color = mix(color, DISK_COLOR, DISK_ALPHA * coverage);
    }

    // Draw triangle
    {
      float sd = signedDistanceToTriangle(uv, ps[0], ps[1], ps[2]);
      float coverage = smoothBoundaryCoverage(sd / inv_view_scale, AA);
      color = mix(color, TRIANGLE_COLOR, TRIANGLE_ALPHA * coverage);
    }

    // Draw point
    {
      float min_distance = 1000.0;
      for (int i = 0; i <= 2; i++) {
        min_distance = min(min_distance, distance(ps[i], uv));
      }
      float coverage = smoothBoundaryCoverage(
          min_distance / inv_view_scale - POINT_RADIUS, AA);
      color = mix(color, POINT_COLOR, coverage);
    }
  }

  frag_color = vec4(color, 1.0);
}

//
// Delaunay triangulation
//

#define M_PI 3.14159

float SCALE = 5.0;
float SCALE_TIME = .3;

float POINT_RADIUS = 3.0;
float GRID_LINE_WIDTH = 1.0;
float QUAD_EDGE_WIDTH = 3.0;
float TRI_EDGE_WIDTH  = 2.0;

vec3 CLEAR_COLOR = vec3(0.15);
vec3 GRID_LINE_COLOR = vec3(0.3);
vec3 POINT_COLOR = vec3(0.0, 1.0, 1.0);
vec3 QUAD_EDGE_COLOR = vec3(1.0, 0.0, 1.0);
vec3 TRI_EDGE_COLOR  = vec3(1.0, 1.0, 0.0) * 0.8;
float TRI_EDGE_DEGENERACY_FACTOR_SCALE = 0.1; // heuristics to reduce sudden triangulation flip

float AA = 2.0;

//
// Utils
//

vec2 unitVector2(float t) {
  return vec2(cos(2.0 * M_PI * t), sin(2.0 * M_PI * t));
}
float mix2(float f00, float f10, float f01, float f11, vec2 uv) {
  return mix(mix(f00, f10, uv[0]), mix(f01, f11, uv[0]), uv[1]);
}

float distance_Point_LineSegment(vec2 p, vec2 q1, vec2 q2) {
  // < p - (q + t v), v> = 0  <=>  t = < p - q, v > / < v, v >

  vec2 v = q2 - q1;
  float dot_vv = dot(v, v);
  if (dot(v, v) < 0.0001) {
    return distance(p, q1);
  }
  float t = dot(p - q1, v) / dot_vv;
  float s = clamp(t, 0.0, 1.0);
  return distance(p, q1 + s * v);
}


//
// Noise
//

float hash11(float t) {
  return fract(sin(1.0 + t * 123456.789) * 123456.789);
}

float hash21(vec2 v) {
  return hash11(hash11(v[0]) + 2.0 * hash11(v[1]));
}

float hash31(vec3 v) {
  return hash11(hash11(v[0]) + 2.0 * hash11(v[1]) + 3.0 * hash11(v[2]));
}

vec2 hash22(vec2 v) {
  return vec2(hash21(v), hash31(vec3(v, 1.0)));
}

vec2 hashGradient2(vec2 uv) {
  return unitVector2(hash21(uv));
}

float gradientNoise2(vec2 uv) {
  vec2 uvi = floor(uv);
  vec2 uvf = uv - uvi;
  float f00 = dot(hashGradient2(uvi + vec2(0.0, 0.0)), uvf - vec2(0.0, 0.0));
  float f10 = dot(hashGradient2(uvi + vec2(1.0, 0.0)), uvf - vec2(1.0, 0.0));
  float f01 = dot(hashGradient2(uvi + vec2(0.0, 1.0)), uvf - vec2(0.0, 1.0));
  float f11 = dot(hashGradient2(uvi + vec2(1.0, 1.0)), uvf - vec2(1.0, 1.0));
  float t = mix2(f00, f10, f01, f11, smoothstep(vec2(0.0), vec2(1.0), uvf));
  return (t / 0.7 + 1.0) * 0.5;
}

vec2 someNoise(vec2 hash_seed, float conti_seed) {
  // Taking hashed direction to break inherent velocity pattern of gradientNoise2
  vec2 p = hash22(hash_seed);
  vec2 dir1 = unitVector2(p[0]);
  vec2 dir2 = unitVector2(p[1]);
  vec2 v = vec2(
      gradientNoise2(123.456 * hash_seed + 456.789 + conti_seed * dir1),
      gradientNoise2(456.123 * hash_seed + 789.456 + conti_seed * dir2));

  // Tonemap [0, 1] to make movement more "dynamical"
  return smoothstep(0.0, 1.0, v);
}

//
// Delaunay triangulation
//

void distanceToNoiseSitesTriangulation(
    vec2 uv, float noise_seed,
    out float min_distance_vertex,
    out float min_distance_quad_edge,
    out float min_distance_tri_edge,
    out float in_circle_test_det) {

  vec2 lattice_uv = floor(uv);
  min_distance_vertex = sqrt(2.0);
  min_distance_quad_edge = 1.0;
  min_distance_tri_edge = sqrt(2.0);

  // Cache sites coordinate
  vec2 sites[9];
  #define _ENCODE_IJ(i, j)       (3 * int(i + 1.0) + int(j + 1.0))
  #define READ_SITE(i, j)        sites[_ENCODE_IJ(i, j)]
  #define WRITE_SITE(i, j, site) sites[_ENCODE_IJ(i, j)] = site

  // iterate sites
  for (float i = -1.0; i <= 1.0; i++) {
    for (float j = -1.0; j <= 1.0; j++) {
      vec2 lattice_ij = lattice_uv + vec2(i, j);
      vec2 site_ij = lattice_ij + someNoise(lattice_ij, noise_seed);
      min_distance_vertex = min(min_distance_vertex, distance(uv, site_ij));
      WRITE_SITE(i, j, site_ij);
    }
  }

  // iterate quad edges
  // - these quads are not necessarily convex, but the probablity of such case should be quite low.
  //   or you could force convexity by weaking the noise factor (e.g. 0.5 * someNoise(...)).
  for (float k = -1.0; k <= 1.0; k++) {
    for (float l = -1.0; l <= 0.0; l++) {
      float d_kl = distance_Point_LineSegment(uv, READ_SITE(k, l), READ_SITE(k + 0.0, l + 1.0));
      float d_lk = distance_Point_LineSegment(uv, READ_SITE(l, k), READ_SITE(l + 1.0, k + 0.0));
      min_distance_quad_edge = min(min_distance_quad_edge, min(d_kl, d_lk));
    }
  }

  // iterate triangulation diagonal edges
  for (float k = -1.0; k <= 0.0; k++) {
    for (float l = -1.0; l <= 0.0; l++) {
      vec2 v0 = READ_SITE(k + 0.0, l + 0.0);
      vec2 v1 = READ_SITE(k + 1.0, l + 0.0);
      vec2 v2 = READ_SITE(k + 0.0, l + 1.0);
      vec2 v3 = READ_SITE(k + 1.0, l + 1.0);

      // Cf. Guibas 1985, Lemma 8.1 (https://doi.org/10.1145%2F282918.282923)
      mat3 in_circle_test_mat = mat3(
          (v1 - v0).x, (v1 - v0).y, dot(v1, v1) - dot(v0, v0),
          (v2 - v0).x, (v2 - v0).y, dot(v2, v2) - dot(v0, v0),
          (v3 - v0).x, (v3 - v0).y, dot(v3, v3) - dot(v0, v0));
      float in_circle_test_det_current = determinant(in_circle_test_mat);
      float d = in_circle_test_det_current > 0.0
          ? distance_Point_LineSegment(uv, v1, v2)  // edge: 10 <--> 01
          : distance_Point_LineSegment(uv, v0, v3); // edge: 00 <--> 11
      if (d < min_distance_tri_edge) {
        min_distance_tri_edge = d;
        in_circle_test_det = in_circle_test_det_current;
      }
    }
  }

  #undef _ENCODE_IJ
  #undef READ_SITE
  #undef WRITE_SITE
}

//
// Misc
//

// grid
float distanceToGrid(vec2 uv) {
  vec2 uvf = fract(uv);
  return min(min(min(uvf.x, uvf.y), 1.0 - uvf.x), 1.0 - uvf.y);
}

// anti alias
float smoothBoundaryCoverage(float b, float d, float w) {
  return 1.0 - smoothstep(0.0, 1.0, (d - b) / w + 0.5);
}


//
// Main
//

void mainImage(out vec4 frag_color, vec2 frag_coord) {
  float inv_view_scale = SCALE / iResolution.y;
  vec2 uv =  inv_view_scale * frag_coord;
  float t = SCALE_TIME * iTime;

  vec3 color = CLEAR_COLOR;
  {
    float d = distanceToGrid(uv);
    float coverage = smoothBoundaryCoverage(0.5 * GRID_LINE_WIDTH, d / inv_view_scale, AA);
    color = mix(color, GRID_LINE_COLOR, coverage);
  }
  {
    float d_vertex, d_quad_edge, d_tri_edge, in_circle_test_det;
    distanceToNoiseSitesTriangulation(
        uv, t, d_vertex, d_quad_edge, d_tri_edge, in_circle_test_det);

    float coverage_vertex = smoothBoundaryCoverage(
        POINT_RADIUS, d_vertex / inv_view_scale, AA);
    float coverage_quad_edge = smoothBoundaryCoverage(
        0.5 * QUAD_EDGE_WIDTH, d_quad_edge / inv_view_scale, AA);
    float coverage_tri_edge = smoothBoundaryCoverage(
        0.5 * TRI_EDGE_WIDTH, d_tri_edge / inv_view_scale, AA);
    float degeneracy_factor = smoothstep(
        0.0, TRI_EDGE_DEGENERACY_FACTOR_SCALE, abs(in_circle_test_det));

    color = mix(color, QUAD_EDGE_COLOR, coverage_quad_edge);
    color = mix(color, TRI_EDGE_COLOR, coverage_tri_edge * degeneracy_factor);
    color = mix(color, POINT_COLOR, coverage_vertex);
  }

  frag_color = vec4(color, 1.0);
}

const float r = 20.;
const float pi = 3.14159265;

const float g = 0.76;
float henyey_greenstein_phase_func(float mu)
{
	return
						(1. - g*g)
	/ //---------------------------------------------
		((4. * pi) * pow(1. + g*g - 2.*g*mu, 1.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.);
    float sum = 0.;
    //col.r = CalculateGGX(.25,uv.r);

    for(float x = -r; x < r; x++)
    {
        for(float y = -r; y < r; y++)
        {
            /*if(sqrt(x*x+y*y)>r)
                continue;*/
            vec2 off = vec2(x,y)/r;
            vec2 offFrost = tan(off*pi/2.)*(2./pi);
            //offFrost = vec2(henyey_greenstein_phase_func(off.x*3.), henyey_greenstein_phase_func(off.y*3.))*(2./pi);

            float l = 1.-sqrt(x*x+y*y)/r;
            float lHermite = -2.*l*l*l+3.*l*l;
            float lFrost = tan(l);
            //lFrost = henyey_greenstein_phase_func(l);
            //lFrost = lHermite;

            //Pick between frosty and less frosty
            off = mix(offFrost, off, sin(iTime)*.5+.5>.5?0.:1.);
            float weight = mix(lFrost,lHermite, sin(iTime)*.5+.5>.5?1.:1.);

            off *= 30.;
            sum += weight;
            col += textureLod(iChannel0, uv + off/iResolution.xy,2.).rgb*weight;
        }
    }
    col /= sum;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

float rand(float x)
{
    return fract(sin(x) * 43758.5453);
}

float triangle(float x)
{
	return abs(1.0 - mod(abs(x), 2.0)) * 2.0 - 1.0;
}

float randomNoise(float x, float y)
{
    return fract(sin(dot(vec2(x, y), vec2(12.9898, 78.233))) * 43758.5453);
}

float _Indensity = 0.7; 


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = floor(iTime * 16.0) / 16.0;
	vec2 uv = fragCoord.xy / iResolution.xy;


    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    p += vec2(triangle(uv.y * rand(time * 0.1) * 1.0) * rand(time * 2.1) * 0.004,
			triangle(uv.x * rand(time * 0.1) * 1.0) * rand(time * 3.1) * 0.004);

    float x = (p.x + 4.0 ) * (p.y + 4.0 ) * (iTime);
	float grain = fract(sin(dot(p.xy, vec2(17.0,180.)))* 2400. + iTime);

	float d = sdCircle( p, -0.1 );

    float c = iTime * 0.8;
    vec3 col = vec3(d) - vec3(0.5);
	col *= 11. * cos(35. * d - d * pow(d, .1)) + 11.* d * cos(abs(c*1.3) + 0.8) * 11.; 

    // Grain
	grain = 1.0 - grain;
    vec3 color = mix(col, vec3(grain), 0.3);


    // Depth alpha
    float alpha = col.x * grain*0.007; // cine shader depth
	fragColor = vec4(color,alpha);
}

