//---Scale Sequencer 2
// by Catzpaw 2020

float circle(vec2 p,vec2 s,float r1,float r2){
    float d=1./iResolution.y;
    float v=(s.x+s.y)*.5;
    float l=length(p);r1+=v*.01;r2+=r1;
    float r=min(smoothstep(r1,r1+d,l),smoothstep(r2+d,r2,l));
    s=abs(s);
    v=p.x<0.?s.x*3.+s.y:s.x+s.y*3.;
    r=min(r,smoothstep(v+d,v,atan(abs(p.x),-p.y)));
    return v>0.?r*.6:0.;
}
vec4 tx1(int x){
    return texture(iChannel1,vec2((float(x)+.5)/iResolution.x,.5/iResolution.y))*2.-1.;
}
mat2 rot(float a){
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=fragCoord/iResolution.xy;
    vec3 e=vec3(.07,.07,0);
    vec4 c=texture(iChannel0,uv+e.xz)*.2;
    c=max(c,texture(iChannel0,uv-e.xz)*.2);
    c=max(c,texture(iChannel0,uv+e.zy)*.2);
    c=max(c,texture(iChannel0,uv-e.zy)*.2);
    c*=1.-length(uv-.5)*1.5;
    c=max(c,texture(iChannel0,uv));
    c.w=1.;

    uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    float vol=tx1(0).x*.5+.5;
    vec4 tx=vec4(0);
    if(length(uv)<.5){
        tx=tx1(6);c.xy+=circle(uv,tx.xy   *vol,0.15,.09);c.z +=circle(uv,tx.zw   *vol,0.15,.09);
        tx=tx1(5);c.x +=circle(uv,tx.xy*6.*vol,0.25,.02);c.yz+=circle(uv,tx.zw*6.*vol,0.25,.02);
        tx=tx1(4);c.xy+=circle(uv,tx.xy*5.*vol,0.28,.02);c.z +=circle(uv,tx.zw*5.*vol,0.28,.02);
        tx=tx1(3);c.x +=circle(uv,tx.xy*2.*vol,0.31,.04);c.yz+=circle(uv,tx.zw*2.*vol,0.31,.04);
        tx=tx1(2);c.xy+=circle(uv,tx.xy*2.*vol,0.36,.06);c.z +=circle(uv,tx.zw*2.*vol,0.36,.06);
        tx=tx1(1);c.x +=circle(uv,tx.xy*2.*vol,0.43,.04);c.yz+=circle(uv,tx.zw*2.*vol,0.43,.04);
        c+=circle(uv*rot(-iTime*6.283*bpm/120.),vec2(vol*.7),0.12,.02);
        c+=circle(uv*rot(-iTime*6.283*bpm/120.),vec2(vol*(iTime/180.)),0.48,.01);
    }
	fragColor=c;    
}

