//---Scale Sequencer 2
// by Catzpaw 2020

//SETUP
const float base =432.;	//A tuning (Hz)
const float bpm  =135.;	//beats per minute
const float steps=240.;	//block length
const float start=1.;   //start count (beats)
const float fade =10.;  //fade out time (sec)
const float sec  =180.; //performance time (includes fade out time and start count)
const float gateseed=188.;
const float noteseed=177.;
const int chord3=2; //3rd tone(default:2)
const int chord5=4; //5th tone(default:4)

//SCALE
int key=4; //0:C 1:C+ 2:D 3:D+ 4:E 5:F 6:F+ ... 11:B
int scale=10;
const uint[12] scl=uint[](
    0xcb975420u, //0:major
    0xca875320u, //1:natural minor
    0xcb875320u, //2:harmonic minor
    0xcb975320u, //3:melodic minor
    0xa9765320u, //4:blues octatonic
    0xa9764310u, //5:blues diminished octatonic
    0xc9974420u, //6:major pentatonic (to heptatonic)
    0xca775530u, //7:minor pentatonic (to heptatonic)
    0xcb775540u, //8:ryukyu pentatonic (to heptatonic)
    0xcb755420u, //9:ryukyu hexatonic (to heptatonic)
    0xba875410u, //10:maqam hijaz octatonic
    0xba876320u);//11:gypsy minor octatonic
int scalenote(int y){
    return y<0?-999:key+((int(scl[scale])>>(y&7)*4)&15);
}

//PATTERN
// melodic:range(4),rest(4),seed1(12),seed2(12)
// percussive:note(2x16)
const uint[32] pat=uint[](
    0x37000000u,0x37011171u,0x47012172u,0x37011773u,  //00-   melody
    0x37054394u,0x37054c95u,0x44226166u,0x34617167u,
    0x34028168u,0x34019119u,0x4401a11au,0x3440417cu,  //  -0B
    0x31001434u,0x31001491u,0x31001022u,0x31001022u,  //0C-0F backing
    0x10000000u,0x10000000u,0x10000000u,0x10000000u,  //10-13 chord
    0x44504789u,0x4461a890u,0x44143327u,0x44957631u,  //14-17 bass
    0x313aa13au,0x513aa186u,0x323a323au,0x5556a186u,  //18-1B sd+hh
    0x91e121e1u,0x55e121e5u,0x4db18db1u,0x55919d55u); //1C-1F bd+conga

//SEQUENCE
// speed(4),degree1(4),pattern1(8),division(4),degree2(4),pattern2(8)
// speed    0:0.5x 1:1x 2:2x 3:4x
// degree   0:mute 1:I  2:II 3:III 4:IV 5:V 6:VI 7:VII
// division 0-f ... timing to switch pattern1 to pattern2 (0:only pattern2)
const uint[16] seq1=uint[]( //snare+hihat
    0x11000118u,0x11000118u,0x11000118u,0x11188119u,
