    0x11000606u,0x11000407u,0x1100050Au,0x1100070Bu);

//BIT OPERATION
int imod(int x,int y){return int(x-y*(x/y));}
int imod(uint x,uint y){return int(x-y*(x/y));}
int get12(uint b,int l){return int(b>>l*4)&4095;}
int get8(uint b,int l){return int(b>>l*4)&255;}
int get4(uint b,int l){return int(b>>l*4)&15;}
int get2(uint b,int l){return int(b>>l*2)&3;}

//NES SOUND GENERATORS(without DPCM)
float osc_duty(float x,float d){return fract(x)<d?1.:-1.;}
float osc_tri4(float x){return abs(floor(fract(x)*15.9999)/7.5-1.);}
const float[16] nt=float[](2.,4.,8.,16.,32.,48.,64.,80.,101.,127.,190.,254.,381.,508.,1017.,2034.);
float osc_nesnoise(float x,int i){return fract(sin(floor(x*1789772.5/nt[i])*179.2)*204.8);}

//WAVETABLE OSC(namco C30/N163)
//sound0:flute  ADFF DA75 4456 9ABB A852 0025
//sound1:brass  BFED ED93 0378 78CF C621 2104
//sound2:organ  87D7 F7F7 D7A7 8582 8080 8287
//sound3:piano  BEFF EBA9 9998 7666 6541 0014
//sound4:harpsi BDEF 000F EDB9 6421 0fff 0124
//sound5:bass   0033 2266 0033 CCFF 99DD CCFF
//sound6:octsqr 0000 00AA AAAA 5555 55FF FFFF
//sound7:octsaw 0123 4567 89AB 4567 89AB CDEF
const uint[24] wt=uint[](
    0x000bb8bau,0x100de7fdu,0x203efdefu,0x303ff7dfu,0x4020efedu,0x5020b7dau,0x6a60af97u,0x7a6f9735u,
    0x8a0e9d04u,0x9a0d9734u,0xaa3b9a75u,0xba398786u,0x45c67879u,0x55c4658au,0x65f268cbu,0x75f162fbu,
    0x859068cau,0x959f5068u,0xafdf4825u,0xbfdf1012u,0xcfc00820u,0xdfc10210u,0xeff21802u,0xfff44745u);
float osc_c30(float x,int i){return(float((wt[int(mod(x*24.,24.))]>>(i*4))&15u)/15.-.5);}

//AMP
//  vec3 p( drive, pressure, volume)
vec2 amp(vec2 i,vec3 p){return clamp(sign(i)*pow(abs(i*p.x),vec2(1./p.y)),-1.,1.)*p.z;}

//FREQUENCY FROM MIDI NOTE NUMBER
float freq(float n){return pow(2.,(n-69.)/12.)*base;}

//ENVELOPE
float env_d(float x,float d){return max(0.,1.-x/max(d,1e-4));}

//CHIPTUNE INSTRUMENTS (freq,time)
const float detune =.0025;
vec2 dutybass(float f,float x){return vec2(osc_duty(f*(.5+detune),.5),osc_duty(f*(.5-detune),.5))*env_d(x,.5);}
vec2 tri4bass(float f,float x){return vec2(osc_tri4(f*(.5+detune)),osc_tri4(f*(.5-detune)));}
vec2 tri4lead(float f,float x){return vec2(osc_tri4(f*(1.-detune)),osc_tri4(f*(1.+detune)));}
vec2 c30bass(float f,float x){return vec2(osc_c30(f*(1.+detune),5),osc_c30(f*(1.-detune),5))*env_d(x,.5);}
vec2 c30chord(float f,float x){return vec2(osc_c30(f*(1.-detune),7),osc_c30(f*(1.+detune),7));}
vec2 c30bell(float f,float x){return vec2(osc_c30(f*(1.-detune),1),osc_c30(f*(1.+detune),1))*env_d(x,.6);}
vec2 c30lead(float f,float x){return vec2(osc_c30(f*(1.-detune),4),osc_c30(f*(1.+detune),4))*env_d(x,.5);}

//CHIPTUNE PERCUSSION (time)
float nessd(float x){return osc_nesnoise(x,11)*env_d(x,.2);}
float neshh(float x,float d){return osc_nesnoise(x,0)*env_d(x,d);}
float nesbd(float x){return osc_duty(3./(x*.5+.1),.5)*env_d(x,.2);}
float neslc(float x){return osc_duty(10./(x*.3+.1),.25)*env_d(x,.05);}
float neshc(float x){return osc_duty(12./(x*.3+.1),.25)*env_d(x,.05);}

//GET PATTERN FROM SEQUENCE
float mp=0.; //note length multiplier(from speed)
int   dg=0;  //degree now playing
uint  pt=0u;  //pattern now playing
int   st=0;  //step now playing
bool getpattern(float t,uint sq){
    // speed(4),degree1(4),pattern1(8),division(4),degree2(4),pattern2(8)
    mp=pow(2.,float(get4(sq,7)-1))*16.;
    st=int(mod(t*bpm*mp/steps,16.));
    bool di=int(mod(t*bpm*16./steps,16.))<get4(sq,3);
    dg=di?get4(sq,6):get4(sq,2);
    pt=di?pat[get8(sq,4)]:pat[get8(sq,0)];
    return dg==0?false:true;
}

//GET NOTE FROM PATTERN
int notegen(int s,int s1,int s2){
    return fract(sin(float(s+s1)+float(s1*20)*gateseed)*50.)<(float(get4(pt,6))*.0625)?-1:
    imod(int(abs(sin(float(s+s1+s*s2)*noteseed)*float(s2))),get4(pt,7));
}
vec4 getnote(float t,float l){
    // melodic:range(4),rest(4),seed1(12),seed2(12)
    int s1=get12(pt,3),s2=get12(pt,0),n=notegen(st,s1,s2);
    float x=mod(t,l/mp);
    if((st>=1)&&(n<0)){n=notegen(st-1,s1,s2);x+=l/mp;}
    n+=n<0?0:dg-1;
    return (dg<1)||(n<0)?vec4(x,-1,-1,-1):vec4(x,scalenote(n),scalenote(n+chord3),scalenote(n+chord5));
}
vec4 getgate(float t,float l){
    // percussive:note(2x16)
    int n=get2(pt,st);
    float x=mod(t,l/mp);
    if((st>=1)&&(n==0)){n=get2(pt,st-1);x+=l/mp;}
    return dg<1?vec4(x,-1,-1,-1):vec4(x,n,n,n);
}

//RENDERER
vec2 track1(float t,float l,int s){//TRACK 1 NES noise snaredrum,closehh,openhh
    vec2 o=vec2(0);
    getpattern(t,seq1[imod(s,16)]);
    vec4 n=getgate(t,l);
    o+=(n.y==1.)?nessd(n.x)*.8:0.;
    o+=(n.y==2.)?neshh(n.x,.08)*.4:0.;
    o+=(n.y==3.)?neshh(n.x,.2)*.5:0.;
    return o;
}
vec2 track2(float t,float l,int s){//TRACK 2 NES sqr bassdrum,lowconga,highconga
    vec2 o=vec2(0);
    getpattern(t,seq2[imod(s,16)]);
    vec4 n=getgate(t,l);
    o+=(n.y==1.)?nesbd(n.x)*.6:0.;
    o+=(n.y==2.)?neslc(n.x)*.3:0.;
    o+=(n.y==3.)?neshc(n.x)*.3:0.;
    return o;
}
vec2 track3(float t,float l,int s){//TRACK 3 NES sqr bass
    vec2 o=vec2(0);
    getpattern(t,seq3[imod(s,16)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?dutybass(freq(n.y+36.)*n.x,n.x)*.35:vec2(0);
    return o;
}
vec2 track4(float t,float l,int s){//TRACK 4 N163 chord
    vec2 o=vec2(0);
    getpattern(t,seq4[imod(s,16)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?c30chord(freq(n.y+60.03)*n.x,n.x)*.3:vec2(0);
    o+=(n.y>=0.)?c30chord(freq(n.z+60.02)*n.x,n.x)*.3:vec2(0);
    o+=(n.y>=0.)?c30chord(freq(n.w+60.01)*n.x,n.x)*.3:vec2(0);
    return o;
}
vec2 track5(float t,float l,int s){//TRACK 5 N163 backing
    vec2 o=vec2(0);
    getpattern(t,seq5[imod(s,16)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?c30bell(freq(n.y+72.05)*n.x,n.x)*.3:vec2(0);
    return o;
}
vec2 track6(float t,float l,int s){//TRACK 6 N163 & NES tri melody
    vec2 o=vec2(0);
    getpattern(t,seq6[imod(s,16)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?c30lead(freq(n.y+36.05)*n.x,n.x)*.6+tri4lead(freq(n.y+60.)*n.x,n.x)*.6:vec2(0);
    return o;
}
void render(float t,float l,inout vec2 o){
    float c=0.;       //render target
    int   s=int(t/l); //sequence number now playing
    vec4  n=vec4(0);  //chord info x:gate yzw:notes
    o+=track1(t,l,s); //TRACK 1 NES noise snaredrum,closehh,openhh
    o+=track2(t,l,s); //TRACK 2 NES sqr bassdrum,lowconga,highconga
    o+=track3(t,l,s); //TRACK 3 NES sqr bass
    o+=track4(t,l,s); //TRACK 4 N163 chord
    o+=track5(t,l,s); //TRACK 5 N163 backing
    o+=track6(t,l,s); //TRACK 6 N163 & NES tri melody
}

