#define MASTER_PRESSURE 1.1
float mvol=0.; //for indicator
vec2 mainSound( in int samp,float time){
    float l=steps/bpm,vol=1.;
    vec2 o=vec2(0);
    if(time>sec-fade){vol=(sec-min(time,sec))/fade;}
