void mainImage( out vec4 c, in vec2 f )
{
    vec2 uv = f/iResolution.xy;

    float s = S0(uv).r;

    //inspired by https://www.shadertoy.com/view/ld3Sz7
    //reset on frame 0/when the user presses R
    if(iFrame < 1 || texelFetch( iChannel1, ivec2(82,0),0).x>0.)
    {
        c = sign(vec4(rand(uv))-.5);
    }
    else if(texelFetch( iChannel1, ivec2(67,0),0).x>0.)
    {
        c=vec4(0);
    }
    else
    {
        if(iMouse.z>0.&&distance(iMouse.xy,f)<15.&&rand(f)>.75 ) c=vec4(1.);
        else
        {
            //sample how many neighbors we have
            float n =  N(-1, -1) + N(-1, 0) + N(-1, 1) +
                       N(0,-1)   +            N(0, 1) +
                       N(1,-1)   + N(1, 0)  + N(1, 1);

            //if we are dead and have 3 neighbors we resurrect
            if(s<=0.&&n==3.0)s=1.0;

            //kill if we don't have 3 or 2 neighbors
            if(n!=2.0&&n!=3.0)s*=0.;

            //fade as it ages
            if(s>0.2)s-=0.01;

            //set the current pixel
            c = vec4(vec3(s), 1.0);
        }
    }
}

