//change i,j into thhe corresponding movement in our coordinate space
#define R(i,j) vec2(i,j)*vec2(1.0/iResolution.xy)
//sample in the direction i,j from our current uv position
#define T(i,j) S0((uv + R(i,j))).r
//retrieve color from neighboring pixel
#define C(i,j) S0((uv + R(i,j))).rgb
//checks if the neighbor is alive
#define N(i,j) float( T(i,j) > 0.)
//sample a texture
#define S0(i) texture(iChannel0, fract(i))
#define S1(i) texture(iChannel1, fract(i))

float rand (vec2 p)
{
    return fract(sin(dot(p.xy,vec2(12389.1283,8941.1283)))*(12893.128933+iDate.w));
}

vec4 randColor(vec2 p)
{
    float c = rand(p);
    float r = float(.5 < rand(p+vec2(-.5,-.5)));
    float g = float(.5 < rand(p+vec2(.5,.5)));
    float b = float(.5 > rand(p+vec2(-1.,1.)));

    return vec4(c*r, c*g, c*b, 1.0);
}

