// A smart way to control the animation. function is from "http://qiita.com/gaziya5/items/29a51b066cb7d24983d6"
float animscene(in float t, float w, float s) {
    return clamp(t - w, 0.0, s) / s;  
}

vec4 Layer1(vec3 p,  float z, float speed, float dir, mat3 rot) {
    p.z += iTime*3.0;
    p.z += z;
    p.z = mod(p.z,9.0)-4.5;

    p *= rot*matRotateX(radians(30.0*dir))*matRotateY(radians(40.0*dir));
    p.y+=1.25;

    float y = 0.25;

    // box
    float b = sdBox(p-vec3(0.0,1.0+y,0.0),vec3(1.5,2.5,0.2));
    float b2 = sdBox(p-vec3(0.0,1.0+y,0.0),vec3(1.2,2.2,0.3));

    float animTime = mod(iTime,speed*4.0);
    float ey = 0.0;
    ey += linear(animscene(animTime, 0.0, speed))*5.0;
    ey += linear(animscene(animTime, speed*2.0, speed))*-5.0;

    float ex = 0.0;
    ex += linear(animscene(animTime, speed, speed))*2.6;
    ex += linear(animscene(animTime, speed*3.0, speed))*-2.6;

    vec3 emitPos = vec3((1.3*dir)-(ex*dir),1.25+(2.5-ey),0.0);
    vec3 ecol = emitColor(p,emitPos, vec3(0.0,0.7,0.8),0.02,0.9,5.0);

    float d = max(-b2,b);
    if(dir == 1.0){
    	p *= matRotateZ((z == 6.0) ?radians(30.0):radians(-30.0));
		d = (z == 6.0) ?max(-p.y+0.5,max(-b2,b)):max(p.y-1.5,max(-b2,b));	
    }

    vec4 res = vec4(vec3(0.8)+ecol,d);
    return res;
}

vec4 GetDist(vec3 p) {
    vec3 prevP = p;
    float _floor = p.y;

    vec4 layer1 = Layer1(p,0.0,0.5,1.0,matRotateZ(radians(iTime*50.0)));
    vec4 layer2 = Layer1(p,3.0,1.0,-1.0,matRotateZ(radians(iTime*-60.0)));
    vec4 layer3 = Layer1(p,6.0,1.5,1.0,matRotateZ(radians(iTime*70.0)));
	vec4 layers = combine(layer1,combine(layer2,layer3));

    return layers;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

float typoNine(vec2 p) {
    float c0 = sdUnevenCapsule(p*Rot(radians(90.0)),0.15,0.15,0.14);
    float c1 = sdUnevenCapsule(p*Rot(radians(90.0)),0.07,0.07,0.14);
    float cut0 = dBox2d((p+vec2(-0.26,-0.04))*Rot(radians(20.0)),vec2(0.08,0.08));
    float cut1 = dBox2d(p+vec2(-0.32,0.1),vec2(0.2,0.1));
    float c2 = length(p+vec2(-0.12,0.11))-0.04;
    float topC = min(max(-cut1,max(-cut0,max(-c1,c0))),c2);

    vec2 btmP = vec2(0.1,0.11);
    c0 = sdUnevenCapsule((p+btmP)*Rot(radians(90.0)),0.15,0.15,0.22);
    c1 = sdUnevenCapsule((p+btmP)*Rot(radians(90.0)),0.07,0.07,0.22);
    c2 = length(p+btmP+vec2(-0.1,-0.11))-0.04;
    cut0 = dBox2d((p+vec2(-0.05,0.2)),vec2(0.08,0.08));
    float btmC = max(-cut0,min(max(-p.x-btmP.x+0.1,max(-c1,c0)),c2));

    return min(topC,btmC);
}

vec3 jpTraditionalTex(vec2 p, vec3 col, vec3 lcol) {
    p.y -= iTime*0.1;
    p*=1.5;
    p = mod(p,0.4)-0.2;


    float len = 0.2;
    float len2 = 0.15;
    float l = sdLine(p,vec2(-len,-len),vec2(len,len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len,len),vec2(len,-len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(0.0,len),vec2(0.0,-len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len,0.0),vec2(len,0.0));
	col = mix(col,lcol,S(l,0.0));

	l = sdLine(p,vec2(-len*0.3,-len2),vec2(len*0.3,len2));
	col = mix(col, lcol,S(l,0.0));
	l = sdLine(p,vec2(-len*0.7,-len2*0.4),vec2(len*0.7,len2*0.4));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len*0.3,len2),vec2(len*0.3,-len2));
	col = mix(col, lcol,S(l,0.0));    
    l = sdLine(p,vec2(-len*0.7,len2*0.4),vec2(len*0.7,-len2*0.4));
	col = mix(col,lcol,S(l,0.0));

	l = sdLine(p,vec2(0.0,len),vec2(len*0.3,len2));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(0.0,len),vec2(-len*0.3,len2));
	col = mix(col, lcol,S(l,0.0));
	l = sdLine(p,vec2(0.0,-len),vec2(len*0.3,-len2));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(0.0,-len),vec2(-len*0.3,-len2));
	col = mix(col, lcol,S(l,0.0));

	l = sdLine(p,vec2(len,0.0),vec2(len*0.7,len2*0.4));
	col = mix(col, lcol,S(l,0.0));
	l = sdLine(p,vec2(len,0.0),vec2(len*0.7,-len2*0.4));
	col = mix(col, lcol,S(l,0.0));
	l = sdLine(p,vec2(-len,0.0),vec2(-len*0.7,len2*0.4));
	col = mix(col, lcol,S(l,0.0));
	l = sdLine(p,vec2(-len,0.0),vec2(-len*0.7,-len2*0.4));
	col = mix(col, lcol,S(l,0.0));

    l = sdLine(p,vec2(len*0.3,len2),vec2(len,len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(len*0.7,len2*0.4),vec2(len,len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len*0.3,-len2),vec2(-len,-len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len*0.7,-len2*0.4),vec2(-len,-len));
	col = mix(col, lcol,S(l,0.0));    

	l = sdLine(p,vec2(-len*0.3,len2),vec2(-len,len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(-len*0.7,len2*0.4),vec2(-len,len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(len*0.3,-len2),vec2(len,-len));
	col = mix(col, lcol,S(l,0.0));
    l = sdLine(p,vec2(len*0.7,-len2*0.4),vec2(len,-len));
	col = mix(col, lcol,S(l,0.0));      

    return col;
}

vec3 bg(vec2 p){
    float d = smoothstep(0.0,0.2,p.y-sin(iTime*0.5)*0.5);
  	vec3 col = vec3(d);
	col = mix(vec3(0.0,0.2,0.3), vec3(0.0,0.05,0.1),d);

	col = jpTraditionalTex(p,col,vec3(0.5));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    vec2 uvref = uv;
    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(iTime*20.0));
    ro.yz *= Rot(radians(-20.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);
        vec3 refl = bg(r.xy);
        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz+refl;
        col *= dif.y;
    } else {
        // background
        col = bg(uv);
    }

    uv*=0.45;

    float nine = typoNine(uv+vec2(0.05,-0.055));
    col = mix( col, jpTraditionalTex(uvref,col,vec3(0.0,1.3,0.5))-vec3(0.8), S(nine,0.005) );

    fragColor = vec4(col,1.0);
}

