vec2 ClosestPointOnLine(vec2 a, vec2 b, vec2 p)
{
    vec2 v = b - a;
    return a + v * clamp(dot(v, (p - a) / dot(v,v)), 0.0, 1.0);
}

vec2 NormalizeSpace(vec2 s)
{
    return (s.xy - 0.5 * iResolution.xy) / iResolution.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float smoothness = 2.0/iResolution.y;
