#define T2(U) texelFetch( iChannel2, ivec2(U)   , 0 )

#define l2(x) dot(x,x)

#define TAU 6.2831853

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

// --- keyboard
