    vec3 du = 30.*f*f*(f*(f-2.)+1.);

    vec2 ga = sin((hash3i1f(i+uvec3(0,0,0))+vec2(0,.25))*tau);
    vec2 gb = sin((hash3i1f(i+uvec3(1,0,0))+vec2(0,.25))*tau);
    vec2 gc = sin((hash3i1f(i+uvec3(0,1,0))+vec2(0,.25))*tau);
    vec2 gd = sin((hash3i1f(i+uvec3(1,1,0))+vec2(0,.25))*tau);
    vec2 ge = sin((hash3i1f(i+uvec3(0,0,1))+vec2(0,.25))*tau);
    vec2 gf = sin((hash3i1f(i+uvec3(1,0,1))+vec2(0,.25))*tau);
    vec2 gg = sin((hash3i1f(i+uvec3(0,1,1))+vec2(0,.25))*tau);
    vec2 gh = sin((hash3i1f(i+uvec3(1,1,1))+vec2(0,.25))*tau);

    float va = dot(ga, f.xy - vec2(0,0));
