// Plento
vec2 R;

#define ss(a, b, t) smoothstep(a, b, t)
#define movement(a) sin(a*1.3 + iTime)*.6  
mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}
vec3 tex(vec2 p){return texture(iChannel0, p).xyz;   }
vec2 m(){
    vec2 m = iMouse.xy / iResolution.xy-.5; 
    m.x *= iResolution.x/iResolution.y;
	return m;
}
float map(vec3 rp){
    rp.y += movement(rp.z);
    rp.xz *= rot(rp.y*.3);
    return .8-length(rp.xy);
}

vec3 normal( in vec3 pos ){vec2 e = vec2(0.002, -0.002);return normalize(e.xyy * map(pos + e.xyy) + e.yyx * map(pos + e.yyx) + e.yxy * map(pos + e.yxy) + e.xxx * map(pos + e.xxx));}
float march(vec3 rd, vec3 ro){float t = 0., d = 0.;for(int i = 0; i < 60; i++){d = map(ro + rd*t);if(abs(d) < .002 || t > 64.){break;}t += d * .7;}return t;}

float height(vec2 p){return tex(p).r;}

vec3 TextureBump(vec2 uv, float strength){
    float h = 0.002;

    vec3 p = tex(uv); 
    float dx = height(uv) - height(uv + vec2(h, .0));
    float dy = height(uv) - height(uv + vec2(0., h));

    return normalize( vec3(0., 0., -1.) + vec3(dx, dy, 0.)*strength);
}

vec3 color(vec3 p, vec3 rd, vec3 n, vec2 u, float t){
    vec3 ld = normalize(-p);

    p.y += movement(p.z);
    p.z += iTime * 1.4;
    p.x = abs(p.x);

    vec3 bn = TextureBump(p.xz*.3, 120.0);
    vec3 col = mix(vec3(1., 0., 1.)*.25, vec3(.6), tex(p.xz*.3).r);

    n = normalize(n + bn);

    float spec = pow(max(dot(normalize(reflect(ld, n)), rd), 0.), 12.);
    float dif = max(dot(n, ld), .02);

    col *= 2.8*dif*vec3(1);
    col += vec3(.8, .4, .2) * spec * 1.25;

    col = mix(vec3(0.), col, exp(-t*t*t*.062));

	return 1.-exp(-col * .7);   
}


void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    vec2 m = iMouse.xy / R.xy-.5; 
    m.x *= R.x/R.y;

    vec3 rd = normalize(vec3(uv, 0.6));
    rd = normalize(vec3(uv, 1.0 - dot(uv, uv) * -0.2));
    vec3 ro = vec3(0., 0., 0.);

    rd.xy*=rot(iTime*.2);

    float t = march(rd, ro);

    vec3 n = normal(ro + rd*t);
    vec3 col = color(ro + rd*t, rd, n, u, t);

    col *= ss(1.05, 0., length(uv));

    f = vec4(sqrt(clamp(col, .0, 1.)), 1.);

}

