vec3 borderF(vec2 a)
{
    return minv(minv(minv(vec3(1.,0.,a.x),vec3(0.,1.,a.y)),vec3(-1.,0.,size.x - a.x)),vec3(0.,-1.,size.y - a.y));
}

bool inside(vec2 a)
{
    if(border(a) > 1.) return true;
    return false;
}

//loop the vector
vec2 loop_d(vec2 pos)
{
	return mod(pos + size*0.5, size) - size*0.5;
}

//loop the space
vec2 loop(vec2 pos)
{
	return mod(pos, size);
}


void Check(inout vec4 U, vec2 pos, vec2 dx)
{
    vec4 Unb = texel(ch0, loop(pos+dx));
    //check if the stored neighbouring particle is closer to this position 
    if(length(loop_d(Unb.xy - pos)) < length(loop_d(U.xy - pos)))
    {
        U = Unb; //copy the particle info
    }
}

void CheckRadius(inout vec4 U, vec2 pos, float r)
{
    Check(U, pos, vec2(-r,0));
    Check(U, pos, vec2(r,0));
    Check(U, pos, vec2(0,-r));
    Check(U, pos, vec2(0,r));
}

#define rad 2
vec2 computeforce(vec4 pr)
{
    vec2 F = vec2(0);
    for(int i = -rad; i <= rad; i++)
        for(int j = -rad; j <= rad; j++)
        {
            if(i != 0 && j != 0)
            {
                //neighbor particle
                vec2 cell = pr.xy+vec2(i,j);
                vec4 nb = texel(ch0, cell);
                if(maxx(abs(nb.xy - cell))<0.5) //if neighbor is inside its cell then count force (to exclude repeated evals)
                {
                    vec2 dv = nb.zw - pr.zw; //velocity difference
                    vec2 dr = nb.xy - pr.xy; //position vector
                    vec2 tanv = dot(dv,dr)*dr/(dot(dr,dr) + 1e-3); //tangental velocity
                    F += 1.*tanv*exp(-0.1*dot(dr,dr)) + 1.3*dr*exp(-2.*dot(dr,dr));
                }
            }
        }
    vec3 borderval = borderF(pr.xy);
    F += 0.2*borderval.xy*exp(-borderval.z);
    return F;
}

void mainImage( out vec4 U, in vec2 pos )
{
    vec2 muv = iMouse.xy/size;

    //this pixel value
    U = texel(ch0, pos);

    //check neighbours 
    CheckRadius(U, pos, 1.);
    CheckRadius(U, pos, 2.);
    CheckRadius(U, pos, 3.);
    CheckRadius(U, pos, 4.);
    CheckRadius(U, pos, 5.);

    U.xy = loop(U.xy);

    // particle cloning 
    if(length(U.xy - pos) > 3.)
    	U = vec4(pos, 0.95*U.zw);

	//update the particle    
    U.zw = U.zw + dt*computeforce(U);
    U.xy += dt*U.zw;

    U.xy = loop(U.xy);


    if(iFrame < 1 || (iMouse.z > 0. && length(iMouse.xy - pos) < 30.) || (maxx(abs(size.xy*0.5 - pos)) < 50.))
    {
        U.xy = vec2(pdens*round(pos.x/pdens),pdens*round(pos.y/pdens));
        U.zw = 10.*(hash22(U.xy) - 0.5);
    }
}

