#define CHANNEL iChannel0
#define colorDist colorDistFast

float colorDistFast(vec4 c1, vec4 c2){
	float dr = c1.r - c2.r;
    float dg = c1.g - c2.g;
    float db = c1.b - c2.b;
    return dr * dr + dg * dg + db * db;
}

float colorDistAccurate(vec4 c1, vec4 c2){
	vec4 c = c1 - c2;
    float y = c.r * 0.2124681075446384 + c.g * 0.4169973963260294 + c.b * 0.08137907133969426;
  	float i = c.r * 0.3258860837850668 - c.g * 0.14992193838645426 - c.b * 0.17596414539861255;
  	float q = c.r * 0.0935501584120867 - c.g * 0.23119531908149002 + c.b * 0.13764516066940333;
  	return y*y + i*i + q*q;
}


// pick one of the 4 diagonal pixels!
// d.x must not be zero
vec4 pick4(vec2 center, vec2 d){

    float closest_dist = 10000000000000.0f;
    vec4 pick;
    vec4 sampCenter = texture(CHANNEL, center/ iResolution.xy);

	vec4 samp;
    float dist;

	samp = texture(CHANNEL, (center+d) / iResolution.xy);
    dist = colorDist(sampCenter, samp);
    if (closest_dist > dist){
        pick = samp;
        closest_dist = dist;
    }
    samp = texture(CHANNEL, (center-d) / iResolution.xy);
    dist = colorDist(sampCenter, samp);
    if (closest_dist > dist){
        pick = samp;
        closest_dist = dist;
    }

    samp = texture(CHANNEL, (center+vec2(d.y*-1.0f, d.x)) / iResolution.xy);
    dist = colorDist(sampCenter, samp);
    if (closest_dist > dist){
        pick = samp;
        closest_dist = dist;
    }
    samp = texture(CHANNEL, (center-vec2(d.y*-1.0f, d.x)) / iResolution.xy);
    dist = colorDist(sampCenter, samp);
    if (closest_dist > dist){
        pick = samp;
        closest_dist = dist;
    }

    return pick;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	if (fragCoord.x < iMouse.x){// show unmodified
		fragColor = texture(CHANNEL, fragCoord.xy / iResolution.xy);
    } else if (abs(fragCoord.x - iMouse.x) < 1.5f){// render line
    	fragColor = vec4(1.0f, 1.0f, 1.0f, 2.0f) - texture(CHANNEL, fragCoord.xy / iResolution.xy);
    }else {// blur

		int r = 5;
        vec3 color;

		for (int i=1; i <= r; i++)
		{
			for (int j=0; j <= r; j++)
			{
				color += pick4(fragCoord.xy, vec2(i, j)).rgb;

			}
		}

		fragColor = vec4(color/float(r*(r+1)), 1.0);
	}
}

