#define Main void mainImage (out vec4 Q, vec2 U)
#define R iResolution.xy
#define A(U) texture(iChannel0,X((U),iMouse.z>0.?iMouse.xy:0.5*R)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Neighborhood vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0)), m = 0.25*(n+e+s+w);

#define curl 0.25*(e.y-w.y+s.x-n.x)
#define conv 0.25*(w.x-e.x+s.y-n.y)
#define grad 0.25*vec2(w.z-e.z,s.z-n.z)
#define darg 0.25*vec2(s.w-n.w,e.w-w.w)

