    vec2 s1 = iSnareGhidra(mod(t + beat, beat));
    Q(measure*4.){
    } else QL (measure*4.){
    	s += s1;
    }

    return s;
}
// ------------ CHANNELS ------------ //

vec2 cSpringReverb(float t){
	vec2 s = vec2(0);
    float iters = 10.;
    float st = 7./iSampleRate;
    for(float i = 0.; i < iters; i++){
        float offs = r11(i)*0.02; // electronic
    	s += gPads(t - st*i - offs)*(1. - i/iters);
    	s += gHats(t - st*i - offs*0.2)*(1. - i/iters)*2.;
    }

    s /= iters;
    s *= 2.;
    //s = sat(s, 2.);
    //s = clamp(s, -1., 1.);
    //s /= 2.;
    return s;
}

vec2 cEchoInput(float t){
	return gLead(t);
}
vec2 cEcho(float t) {
	vec2 s = vec2(0);
    float fb = 0.65, tm = qbeat*1.5, cf = 0.9, ct = tm;
    // tap 1 
    s += cEchoInput(t) * cf; cf *= fb; 
    // tap 2
    s += cEchoInput(t - ct) * cf; cf *= fb; ct += tm;
    // tap 3
    s += cEchoInput(t - ct) * cf; cf *= fb; ct += tm;
    // tap 4
    s += cEchoInput(t - ct) * cf; cf *= fb; ct += tm;
    // tap 5
    //s += cEchoInput(t - ct) * cf; cf *= fb; ct += tm;

    return s;
}

// ------------ MAIN ------------ //

vec2 mainSound( in int samp, float t )
{
    #ifdef tOffset
    t += tOffset;
    #endif
    vec2 s = vec2(0);
    tGlobal = t;

    // drums
    vec2 kick = gKick(t);
    vec2 bass = gBass(t);
    vec2 snare = gSnare(t);
    vec2 iirKick; eIIR(t, gKick, 6., 10., iirKick); 
    vec2 cym1bal = gCymbal(t);
    vec2 cymbalB = gCymbalB(t);
    vec2 kickR = gKickR(t + measure);
    vec2 hats = gHats(t);


    // synths
    vec2 pads = gPads(t);
    vec2 introDrone = gIntroDrone(t,0.)/3.;
    vec2 lead = gLead(t);   
    vec2 reverb = cSpringReverb(t);
    vec2 echo = cEcho(t);


    // mixer
	//pads *= sideChain(t);	
    //reverb *= sideChain(t);	
    s += kickR*0.7;
    s += bass*0.24;
    s += pads*0.4;   
    s += introDrone*2.;

    s += reverb*0.5;
    //s += hats*3.;
    s += echo*0.2;
    s += snare*0.18;

    s *= 0.7;
    //s = clamp(s, -1., 1.);
    s = sat(s, 1.7);
    return s;
    //return vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );
}

