//#define tOffset 0. + measure*4. + beat*9.

#define pi acos(-1.)
#define tau (2.*pi)
#define TT tau*t

#define tempo 174.
#define hbeat ((60./tempo)/1.)
#define qnote (hbeat/2.)
#define beat  (hbeat*2.)
#define qbeat  (beat/4.)
#define measure  (beat*4.*4.)
#define hmsr  (measure*0.5)
#define bar (0.5*hmsr)
#define hbar (0.5*bar)

#define P(x) t/measure < x
#define PL(x) t/measure > x

#define E else
#define sStep (1./iSampleRate)

#define f(x) float(x)
#define sat(x, amt) clamp(x*amt, -1., 1.)
#define Q(period)  \
	if (t < period)
#define QL(period)  \
	if (t > period)

float note(float note, float octave){ 
	return 27.5*exp2(((octave*12. ) + note )/12.);//pow(2., );
}


float r11(float i){
	return fract(sin(i*742.62412)*1535.52156);
}

#define rT(i) texture(iChannel0, vec2(i)).x
float noise( vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.1);
}

float noise(float t){
    float n = 0.;
    n = fract(r11(t)*r11(t*2.41));
    vec2 p = vec2(fract(r11(t*14.11)),fract(r11(t*124.1)*r11(t*3.15)));
    vec2 q = vec2(fract(r11(t*14.124)*r11(t*41.15)), n*0.15);
    n = fract(dot(p,q));
	return n;
}   
float notVeryRandom(float st){
    return fract(sin(st*451.5524));
}

// ------------ EFFECTS ------------ //

#define lofi(i,m) (floor((i)/(m))*(m))
#define lofir(i,m) (floor((i)/(m)+0.5)*(m))

#define eIIR(t, instr, stSz, iters, varName) \
	{										 \
    	varName = vec2(0);				     \
        float st = stSz/iSampleRate;		     \
        for(float i = 0.; i < iters; i++){	 \
            varName += instr(t - st*i);      \
        }								     \
        varName /= iters;        			 \
    }

#define eChorus(t, instr,freq, iters,stepSz, varName, outAmp) \
	{										 		  \
    	varName = vec2(0);				     		  \
        float st = stepSz/iSampleRate;		 		  \
        float n = noise(t); 				          \
        for(float i = 0.; i < iters; i++){	   		  \
            varName += instr(t - st*i*sign(mod(i, 3.) - 1.), freq)*outAmp;      \
        }								     		  \
        varName /= iters;        			          \
    }

// ------------ DRUMS ------------ //

vec2 iKick(float t){
    vec2 s = vec2(0);
    float envA = exp(-t*20.);
    float envB = exp(-t*50.);
    s += sin(tau*t*(40. + sin(t*90.)*1. +envB*1000.))*envA;
    return s;
}
vec2 iKickB(float t){
    vec2 s = vec2(0);
    float envA = exp(-t*20.);
    float envB = exp(-t*50.);
    s += sin(tau*t*(10. + sin(t*90.)*1. +envB*400.))*envA;
    s = sat(s, 2.);
    return s;
}
vec2 iKickA(float t){
    vec2 s = vec2(0);
    float env = exp(-t*10.);
    s.x += sin( (140. * env+ 10. ) *tau*t) * env ;
    s.y = s.x;
	return s;
}
vec2 iSnareA(float t){
	vec2 s = vec2(0);

    float envA = exp(-t*30.);
    float envB = exp(-t*37.);
    float envC = exp(-t*90.);
    //float envC = exp(smoothstep(1.,0.,(t - 1.)) );
    s += sin( t*tau*(200. + envA * 190.) + noise(t)*(1. - envC))*envB;


    s = sat(s, 1.3);
    return s;
}
vec2 iSnareGhidra(float t){
    vec2 s = vec2(0);
    float hit = mod(t + beat/2.,beat);

    s += sin(tau*note(12.+ 1.6*exp(-hit*26.) ,2.))*exp(-hit*18.)*0.2;    	
    s += sin(tau*note(12.+ 1.6*exp(-hit*26.) ,1.))*exp(-hit*13.)*0.8;    	

    float satAmt = 0.4;
    s = s*(1.-satAmt) +  (s / (0.99 -abs(s)))*satAmt;
    //s *= 0.7;
    s = (s + noise(t + noise(t))*s*8.)/2.; 
	return s*1.;
}

vec2 iHatsA (float t) {
	vec2 s = vec2(0);;
    float env = exp(-t*24.);
    float r = r11(t);
    s.x = sin(470.*t*tau + sin(2040.*t*tau + r*4500.)*4. + r*7.*env ) * env;
    s.y = s.x;
    return s;
}
vec2 iHatsB (float t) {
	vec2 s = vec2(0);
    float env = exp(-t*50.);
    float r = r11(t);
    s.x = sin(570.*t*tau + sin(2040.*t*tau + r*1500.)*4. + r*4. ) * env;
    s.y = s.x;
    s *= 0.5;
    return s;
}
vec2 iHatsC(float time){
	float s;

    //time = mod(time, beat*2.);
    //time = mod(time, beat);
    int t = int(time*iSampleRate/7.);
    t = int(time*iSampleRate*2.);
    t = t % ((t >>3) % t*4);
    s =float(t & 0xff - 128)/ 128.;
    s *= pow(exp(-time), 1.);
    s = (s + (s*noise(time * mod(time, 0.001)*300.))*2.*exp(-time));
    s *= 3.;
    return vec2(float(s));
}

vec2 iCymbal(float t, float freq){
	vec2 s = vec2(0);

    float n = noise(t)*clamp(exp( (t- 1.)*1.), 0., 1.);
    //s += n;
    float fund = 1000.;
    //s += sin(fund*t*tau + sin(t*tau*fund*12.)*5.* exp(-t*5.) + n*600.) * exp(-t*10.);
    s += sin(fund*t*tau + sin(t*tau*fund*49.001)*5.* exp(-t*5.) ) * exp(-t*16.);


    s = abs(s);

    s = clamp(s, -1., 1.);

	return s;
}
vec2 hats (float t) {
	vec2 s = vec2(0);

    t = mod(t, hbeat);
    float env = exp(-t*20.);
    float r = r11(t);
    s.x = sin(470.*t*tau + sin(2040.*t*tau + r*4500.)*4. + r*7.*env ) * env;
    s.y = s.x;
    return s;
}

vec2 hatsTwo (float t) {
	vec2 s = vec2(0);

    t = mod(t, hbeat/2.);
    float env = exp(-t*50.);

    float r = r11(t);
    s.x = sin(570.*t*tau + sin(2040.*t*tau + r*1500.)*4. + r*4. ) * env;
    s.y = s.x;

    return s;

}


// ------------ SYNTHS ------------ //


vec2 iSineWide(float t, float freq){
	vec2 s = vec2(0);
    t += noise(t)*0.00004;
    s.x = sin(freq*tau*t);
    s.y = sin(freq*tau*t + 5.);
    return s;
}
vec2 iPiano(float t, float freq){
	vec2 s = vec2(0);
    t += noise(t)*0.00004;
    float envA = exp(-t*3.);
    s.x = sin(freq*tau*t + sin(freq*tau*t*1.)*envA*1.);
    s.y = sin(freq*tau*t + sin(freq*tau*t*1.)*envA*1.+ 5.);
    return s;
}
vec2 iOrganLead(float t, float freq){
    vec2 s = vec2(0);
    float env = exp(-t*9.);
    freq *= 4.;
    float iterations = 3.;
    float scale = 1.;
    for (float i = 0.; i < iterations; i++) {
        float r = r11(i)*10.;
    	s.x += sin((freq + r)*tau*t )*env*scale;
    	s.x += sin(freq*tau*(t + 0.05) )*env*scale;
    	s.x += sin(0.5*freq*tau*(t + 0.06) )*env*scale;
        scale *= 0.8;
        s.x * 0.33;
        freq *= 2.01;
    }
    s.x /= iterations;
    s.y = s.x;
    return s;
}
vec2 iPlucky(float t, float freq){
	vec2 s = vec2(0);
    freq *= 4.;
	float r = r11(t)*0.01;
    float envA = exp(-t*6.);
    float envB = exp(-t*20.);
	s.x += sin((freq + r)*tau*t + sin((8.*freq + r)*tau*t)*envB*0.5 )*envA;
    s.y = s.x;
    return s;
}

vec2 iSawAChorused(float t, float freq){
	vec2 s = vec2(0);
    eChorus(t, iPiano,freq*2.,10., 1., s, 1.);
    //s *= exp(-t);
    //s.x /= 6.;
    return s;
}
vec2 iSawA(float t, float freq){
	vec2 s = vec2(0);
    s.x = fract(freq*tau*t + sin(t)*9. + r11(t)*0.1*exp(-t*200.))*0.2;
    s.y = s.x;
    return s;
}
vec2 iSuperSaw(float t,float freq){
	vec2 s = vec2(0);
    //float n = noise(t);
    t += r11(t)*0.00009;
    float detuneAmt = 1.;
    //t += noise(t)*0.0004;
    s += iSawA(t, freq);
    s += iSawA(t, freq*(1. - 0.02*detuneAmt));
    s += iSawA(t, freq*(1. + 0.015*detuneAmt));
    s /= 3.;
    return s;
}
vec2 iAmbG(float t, float freq){
	vec2 s = vec2(0);
    freq *= 3.;
    t = mod(t, hmsr);
    float iters = 3. + clamp( exp(-t/2.)*5., 0., 30.);
    for (float i = 1.;i < iters; i++){
    	s += sin(t*tau*freq*i)/i;
    }

    float n = noise(t);
    vec2 sOrig = s;
    float tMod = sin(t*8.)*(1. + exp(t/2.- 1.)*0.3);
    float tMod2 = cos(t*2.)*0.5;
    s = sOrig*sin(freq*4.*t*tau + n*0.4  + tMod          );
    s += sOrig*sin(freq*2.*t*tau + n*0.4 + tMod2        );
    s += sOrig*(fract(freq*8.*t*tau + n*0.4 -tMod + tMod2  ));

    s /= 8.;

    return s;
}


// ------------ GENERATORS ------------ //

vec2 gKickR(float t) {
    vec2 s = vec2(0);

    vec2 k = iKick(t);

    Q(measure*4.){
        return vec2(0);
    } else QL (measure*4.){
        s += k;
    }
    float period = hbeat;
    t = mod(t + beat*4.,beat*8.);
	float r = noise(floor(t/period) + 1.);
	float rB = noise(floor(t/period) + 2.);

    if (rB < 0.1){
    	r = floor(r* 3.99);
    	float offs = (period/4.)*r;
        float mT = mod(t, period) - offs;
        if (mT > 0.){
            eIIR(mT, iKickB, 6., 10., s);
        }
    }
    s = sat(s, 1.2);
    //s.y = s.x;
	return s;
}

