	s4 = min(s4, length(max(abs(p+vec3(0.0, 2.5, 0.0)) - vec3(8.5, 0.05, 8.5), 0.0)));
	s4 = min(s4, length(max(abs(p+vec3(2.0, -2.5, 0.0)) - vec3(4.5, 0.05, 2.5), 0.0)));
	s4 = min(s4, length(max(abs(p+vec3(-0.7, 0.0, -2.5)) - vec3(8.5, 2.5, 0.05), 0.0)));
	s4 = min(s4, length(max(abs(p+vec3(3.75, 0.0, 0.0)) - vec3(0.05, 40.5, 40.5), 0.0)));;
	s4 = min(s4, length(max(abs(p+vec3(-3.9, 0.0, 0.0)) - vec3(0.05, 40.5, 40.5), 0.0)));
	return min( min( min( min(cube, s1), s2 ), s3 ), s4);
}

mat material(vec3 p, float rayNumber)
{
	//float cube = length(max(abs(p) - vec3(0.95), 0.0)) - 0.05;
	float s1 = length(p+vec3(-1.0, -0.5, -1.0)) - 1.3;
	float s2 = length(p+vec3(-2.75, 1.35, 0.)) - 0.05;
	float s5 = length(max(abs(p+vec3(-1.0, 1.5, 1.2)) - vec3(1.0, 0.05, 1.65), 0.0));
	mat m;
	m.emit = vec3(0.0);
	m.transmit = vec3(1.0);
	m.diffuse = 0.0;
/*	if (cube < s1 && cube < s2 && cube < s5) {
		m.transmit = vec3(0.9, 0.6, 0.3);
		m.diffuse = 0.6;
	} else */
    if (s2 < 0.01) {
		m.emit = vec3(5.0, 3.5, 3.0)*20.0;
		m.transmit = vec3(0.9, 0.6, 0.3);
		m.diffuse = 0.85;
	} else if (s1 < 0.01 || abs(sin(p.z*p.y)) < 0.5) {
		m.transmit = vec3(0.8, 0.6, 0.4);
        if (s1 < 0.01 && abs(p.z-2.0) < 0.1)
	        m.emit = vec3(1.1, 0.2, 0.1);
		m.diffuse = 0.15;
    } else if ((p.z > 0.0 || p.z < -2.0 && p.y < 0.0) && mod(rayNumber, 8.0) < 1.0) { // && mod(abs(length(p.xy-vec2(2.0, -1.5))-2.5), 1.0) < 0.25) {
        m.diffuse = 0.05;
        m.transmit = vec3(0.8, 0.6, 0.4);
	} else {
		m.diffuse = 1.0;
		m.transmit = 0.5 * vec3(0.9, 0.93, 0.96); //min(vec3(1.0), abs(p));
	}
	return m;
}

vec3 normal(ray r, float d)
{
	float e = 0.001;
	float dx = scene(vec3(e, 0.0, 0.0) + r.p) - d;
	float dy = scene(vec3(0.0, e, 0.0) + r.p) - d;
	float dz = scene(vec3(0.0, 0.0, e) + r.p) - d;
	return normalize(vec3(dx, dy, dz));
}

vec3 shadeBg(vec3 nml, float t)
{
    return vec3(0.0);

    vec3 lightPos_ = vec3(
        -cos(t)*-8.5, 
        sin(t)*3.0 - 4.0, 
        -(sin(t)*4.0)
    );
    vec3 bgLight = normalize(lightPos_);
    vec3 lightPos = bgLight * 9999.0;
    vec3 sun = vec3(5.0, 3.5, 2.0)*4.0;
	vec3 bgCol = vec3(0.2, 0.15, 0.1);
	float bgDiff = dot(nml, vec3(0.0, 1.0, 0.0));
	float sunPow = dot(nml, bgLight);
	bgCol += 0.1*sun*pow( max(sunPow, 0.0), 2.0);
	bgCol += 2.0*bgCol*pow( max(-sunPow, 0.0), 2.0);
	bgCol += bgDiff*vec3(0.25, 0.5, 0.5);
	bgCol += sun*pow( max(sunPow, 0.0), abs(bgLight.y)*256.0);
	bgCol += bgCol*pow( max(sunPow, 0.0), abs(bgLight.y)*128.0);
	return max(vec3(0.0), bgCol);
}

vec2 xy(float k, float n)
{
	return vec2(floor(k/n), k-(floor(k/n)*n));
}

void offset(inout vec3 nml, float k, float count, float diffuse) {
	vec3  uu  = normalize( cross( nml, vec3(0.01,1.0,1.0) ) );
	vec3  vv  = normalize( cross( uu, nml ) );
	vec2  aa = hash2( count );
	float ra = sqrt(aa.y);
	float rx = ra*cos(6.2831*aa.x); 
	float ry = ra*sin(6.2831*aa.x);
	float rz = sqrt( sqrt(k)*(1.0-aa.y) );
	vec3  rr = vec3( rx*uu + ry*vv + rz*nml );	
	nml = normalize(mix(nml, rr, diffuse));
}

// camera rotation
mat3 rotationXY( vec2 angle ) {
	// pitch
	float cp = cos( angle.x );
	float sp = sin( angle.x );
	// yaw
	float cy = cos( angle.y );
	float sy = sin( angle.y );

	return mat3(
		cy     , 0.0, -sy,
		sy * sp,  cp,  cy * sp,
		sy * cp, -sp,  cy * cp
	);
}

ray setupRay(vec2 uv, float k, float t) {

	mat3 rot = rotationXY( vec2( t*0.501, t*0.602 ) );
	ray r;
	r.light = vec3(0.0);
	r.transmit = vec3(1.0);
	r.p = rot * vec3(0.3, -2.0, -3.5);
	r.d = rot * normalize(vec3(uv.x, uv.y+0.75, 1.3));

	return r;
}

vec3 traceLight(ray r, vec3 nml, ray light) {
    vec3 dir = normalize(light.p - r.p);
    vec3 col = vec3(0.0);
    for (int i=0; i<30; i++) {
		float dist = scene(r.p);
		r.p += dist * dir;
		if (length(r.p - light.p) < THRESHOLD*4.0) {
            col = max(0.0, dot(nml, dir)) * max(0.0, dot(-dir, light.d)) * light.light;
            break;
		} else if (dist < THRESHOLD || dist > MAX_DISTANCE) {
            break;
    	}
    }
	return col;
}

vec4 trace(vec2 fragCoord, vec2 uv, vec2 uvD, inout float rayNumber, float convergeCount)
{	
	vec3 accum = vec3(0.0);

    float k = 0.0;

    vec2 rc = fragCoord + (5.0+mod(iTime, 1.73728))*vec2(rayNumber*37.0, rayNumber*63.0);
    vec4 rand = texelFetch(iChannel0, ivec2(mod(rc, vec2(256.0))), 0);

    float time = 64.0 + 11.5; //floor(float(iFrame)/300.0);
    float shutterSpeed = 0.01;

    float t = time + shutterSpeed*rand.x;

    int bounces = 0;
    ray lights[3];

    ray r;
    r.d = normalize(rand.xyz);
    rand = rand.yzwx;
    r.p = vec3(2.75, -1.35, 0.) + r.d * 0.06;
    r.d = normalize(r.p - vec3(2.75, -1.35, 0.));
    r.light = vec3(5.0, 3.5, 3.0)*20.0;
    r.transmit = vec3(1.0);
    lights[0] = r;

    for (int i=0; i<100; i++) {
		float dist = scene(r.p);
		r.p += dist * r.d;
		if (dist < THRESHOLD) {
			r.p -= dist * r.d;
			vec3 nml = normal(r, dist);
            mat m = material(r.p, rayNumber);
            if (bounces == 0) {
                r.transmit *= m.transmit;
                lights[1] = r;
                lights[1].light = r.light * r.transmit;
	            bounces++;
                float diffuse = m.diffuse;
                offset(r.d, rayNumber+k, rand.x+rayNumber+k+10.0*dot(nml, r.d), diffuse*0.5);
                r.d = reflect(r.d, nml);
                lights[1].d = nml;
                r.p += 4.0*THRESHOLD * r.d;
            } else {
                r.transmit *= m.transmit;
                lights[2] = r;
                lights[2].light = r.light * r.transmit;
                float diffuse = m.diffuse;
                offset(r.d, rayNumber+k, rand.x+rayNumber+k+10.0*dot(nml, r.d), diffuse*0.5);
                r.d = reflect(r.d, nml);
                lights[2].d = nml;
                break;
            }
		} else if (dist > MAX_DISTANCE) {
            break;
    	}
    }

    rand = texelFetch(iChannel0, ivec2(mod(rc+vec2(37.0,63.0), vec2(256.0))), 0);
	r = setupRay(uv+(uvD*vec2(rand.x, rand.y)), k+rayNumber, t);
    bounces = 0;
    k = 0.0;

    vec3 startP = r.p;
    float hitD = 1000000.0;

	for (int i=0; i<RAY_STEPS; i++) {
		float dist = scene(r.p);
		r.p += dist * r.d;
		if (dist < THRESHOLD) {
            if (bounces == 0) hitD = length(r.p - startP);
            mat m = material(r.p, rayNumber);
			r.p -= dist * r.d;
			vec3 nml = normal(r, dist);
            float diffuse = m.diffuse;
            r.light += r.transmit * m.emit;
            r.transmit *= m.transmit;
			offset(r.d, rayNumber+k, rand.x+rayNumber+k+10.0*dot(nml, r.d), diffuse*0.5);
            r.d = reflect(r.d, nml);
			r.p += 4.0*THRESHOLD * r.d;

            vec3 collected = vec3(0.0);
			collected += traceLight(r, nml, lights[0]);
			collected += traceLight(r, nml, lights[1]);
			collected += traceLight(r, nml, lights[2]);
			accum += r.light*3.0 + r.transmit * collected;

            k += 3.0;

            //r.light += 0.1;

            rand = rand.yzwx;
            bounces++;
            if (bounces > 3) {
                break;
            }
		} else if (dist > MAX_DISTANCE) {
            //r.light += r.transmit * 2.0*r.d.yxx;
            break;
		}
	}
    accum += r.light;
    k++;
	rayNumber += k;
	return vec4(accum, hitD);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int frame = iFrame % int(3.0 * iResolution.y);
    if (frame == 0) {
        fragColor = vec4(0.0);
        return;
    }
    float scrollY = max(0.0, float(frame) - iResolution.y);
    vec2 sy = scrollY > 0.0 ? vec2(0.0, -1.0) : vec2(0.0);
    vec4 color = texelFetch(iChannel1, ivec2(fragCoord + sy), 0);
    int samples = 20;
    float rayNumber = 0.0;
    if (scrollY > 0.0) {
        if (floor(fragCoord.y) > 0.0) {
            fragColor = color;
            return;
        }
        color *= 0.0;
    } else {
        if (int(iResolution.y - fragCoord.y) != frame) {
            fragColor = color;
            return;
        }
    }

    vec3 c = texelFetch(iChannel2, ivec2(fragCoord), 0).rgb;

    vec4 lightD = vec4(color.rgb, 0.0);

    vec2 aspect = vec2(iResolution.x/iResolution.y, 1.0);
    vec2 uv = (fragCoord.xy + vec2(0.0, -scrollY+300.0)) / iResolution.xy;
    uv = (2.0*uv - 1.0) * aspect;

    vec2 uvD = ((2.0*((fragCoord.xy + vec2(0.0, -scrollY+300.0))+vec2(1.0, 1.0)) / iResolution.xy - 1.0) * aspect) - uv;

    for (int i = 0; i < samples; i++) {
        lightD += trace(fragCoord + vec2(0.0, -scrollY+300.0), uv, uvD, rayNumber, 0.0);
    }
    lightD.rgb /= rayNumber;
    lightD.a /= rayNumber;

    fragColor = lightD;
}

