float Circle(vec2 uv, vec2 p, float r, float blur){
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float mask = Circle(uv, vec2(0), 0.4, 0.05);
    mask -= Circle(uv, vec2(0.3*sin(iTime), 0.3*cos(iTime)), 0.25, 0.05);
    vec3 col = vec3(1);
    col *= mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur){
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}

float Band(float t, float start, float end, float blur){
	float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    float band = step1 * step2;
    return band;
}

float Rect(vec2 uv, float left, float right, float top, float bottom, float blur){
	float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    float rect = band1 * band2;
    return rect;
}

float remap01(float a, float b, float t){
	return (t-a) / (b-a);
}

float remap(float a, float b, float c, float d, float t){
	return remap01(a, b, t) * (d-c) + c;
}

float Smiley(vec2 uv, vec2 p, float size){

    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(0), 0.4, 0.05);
    vec3 col = vec3(1,1,0);


    mask -= Circle(uv, vec2(-0.15,0.1), 0.08, 0.015);
    mask -= Circle(uv, vec2(0.15,0.1), 0.08, 0.015);
    //mask -= Circle(uv, vec2(0,-0.2), 0.1, 0.015);

    float mouth = Circle(uv, vec2(0), 0.3, 0.015) * abs(sin(iTime));
    mouth -= Circle(uv, vec2(0,0.05), 0.3, 0.015) * abs(sin(iTime));

    mask-= mouth;
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float t  = iTime;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

   	float mask = Smiley(uv, vec2(sin(iTime)*0.3,cos(iTime)*0.3), 0.5);
	mask += Smiley(uv, vec2(cos(iTime)*0.1,sin(iTime)*0.5), 0.5);

    float x = uv.x;
    float y = uv.y;

    float m = -(x-0.5)*(x+0.5);
    //m = m*m*4.0;
    m = m*sin(t+x*8.0)*0.5;
    y = y-m;

    float blur = remap(-0.5, 0.5, 0.01, 0.25, x);
    blur = pow(blur*4.0,3.0);

    mask += Rect(vec2(x,y), -0.5, 0.5, 0.1, -0.1, blur);

    vec3 col = vec3(1,1,0)*mask;
    //col = vec3(mouth);
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define time iTime
#define MAX_DST 150
#define EPSI 0.001
int mat = 0;
float random(vec2 p){
    return(fract(sin(p.x*431.+p.y*707.)*7443.));
}
float noise(vec2 uv){
     vec2 id = floor(uv*10.);
    vec2 lc = smoothstep(0.,1.,fract(uv*10.));

    float a = random(id);
    float b = random(id + vec2(1.,0.));
    float c = random(id + vec2(0.,1.));
    float d = random(id + vec2(1.,1.));

    float ud = mix(a,b,lc.x);
    float lr = mix(c,d,lc.x);
    float fin = mix(ud,lr,lc.y);
    return fin;
}

float octaves(vec2 uv,int octs){
    float amp = 0.5;
    float f = 0.;
    for(int i =1; i<octs+1;i++){
        f+=noise(uv)*amp;
        uv*=2.;
        amp*=0.5;
    }
    return f;
}
mat2 rotate(float a){
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}

float sphere(vec3 p){
    float r = 1.;
    r*=1.-(sin(p.y*20.+time*22.)*0.001)*20.;
    r*=1.-(cos(p.x*10.+time*22.)*0.001)*50.;

    return length(p)-r;
}


float sdBox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(vec3 p){
    float sph = sphere(p+vec3(-10.,-6.5,-3.-time*62.));
    float plane = p.y+8.+octaves((p.xz/30.)+(time/10.)+sin(length(p.xz*2.))*.04,10);
    float c = 20.;
    float c2 = 8.;
    p.x+=sin(p.z*0.2);
    p.x=mod(p.x+c*.5,c)-c*.5;
    p.z=mod(p.z+c2*.5,c2)-c2*.5;

    return  min(min(sph,min(sdBox(p,vec3(0.5,4.0,1)),sdBox(p+vec3(0,-4.5,0),vec3(1.5,0.5,4)))),plane);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    vec3 ro = vec3(10,7,-5);
    vec3 rd = normalize(vec3(uv,1.));
    ro.yz*=rotate(.3);
    rd.yz*=rotate(.3);

    ro.z+=time*62.;
    ro.x+=sin(time)*4.;
    ro.y+=cos(time)*2.;

    float tot = 0.;
    float dst = 0.;
    float shad = 0.;
    vec3 p = vec3(0.) ;
    mat = 0;
    for(int i =0;i<MAX_DST;i++){
        p = ro+rd*tot;
        dst = map(p);
        tot+=dst;    
        if(dst<EPSI){
            shad = float(i)/float(MAX_DST);
            break;
        }
    }
    if(dst>EPSI){
        mat = 1;
    }


    col= vec3(shad);
    uv.y*=5.;
    if(mat == 1)col =vec3(octaves(uv/10.,8)/1.6)+vec3(.5);
    col = mix(col,vec3(0.),1.-exp(-0.4*shad));//fog
    fragColor = vec4(col,1.0);
}

float tau=6.28318;
float star(vec3 p,float t,float r,out float i){
 p.xz*=mat2(cos(t*r),-sin(t*r),sin(t*r),cos(t*r));
 float re=tau/5.,a=mod(atan(p.y,p.x)+re*t*.25,re)-re*.5,
 l=length(p.xy);
 vec3 q=vec3(cos(a)*l,sin(a)*l,p.z*p.z*10.),d=vec3(1.,0.,0.);
 i=length(q.xy-d.xy*clamp(dot(q.xy,d.xy),.0,1.))-(.5-q.x*.25);
 return length(q-d*clamp(dot(q,d),.0,1.))-(.5-q.x*.25);
}
float m(vec3 p,out float i,out float j){
 float t=iTime*2.,a=atan(p.y,p.x);
 return min(star(p*vec3(2.,2.,1.),t*.25,1.,i),
  star(vec3(mod(p.z+1.5,3.)-1.5,
   mod(a*12.+tau*.5+(t+tau*.9)*
    (mod(floor((p.z+1.5)/3.),2.)*3.-1.5),tau)-tau*.5,
     length(p.xy)-5.),(t+tau)*.25,1.,j));
}
void mainImage(out vec4 col,in vec2 coord){
  vec2 uv=(coord-iResolution.xy*.5)/iResolution.y,e=vec2(.01,.0);
  vec3 ro=vec3(0.,0.,-3.),rd=normalize(vec3(-uv,1.)),cp=ro+rd;
  float st=0.,dist=0.,cd=0.,i,j,g;
  for(;st<1.;st+=1./300.){
   cd=m(cp,i,j);
   if(abs(cd)<.01||cd>200.)break;
   dist+=cd*.05;
   cp=ro+rd*dist;
  }
  st=pow(st+(fract(sin(uv.x+ uv.y*7512.)*4573.1)-.5)*.1,5.);
  col=mix(vec4(1.,.8,.0,0.),vec4(0.),length(uv)-st)+st;
  if(cd<.01){
  vec3 n=normalize(vec3(cd-m(cp+e.xyy,g,g),cd-m(cp+e.yyx,g,g),
  cd-m(cp+e.yyx,g,g)));
  float li=clamp(dot(n,vec3(.577)),0.,1.);
  col.rgb=vec3(.95,.4,.12)*.25;
  col.rgb+=vec3(1.,1.,1.)*li*.75;i=-(i>.1?j:i);
  col.rgb=mix(col.rgb,vec3(1.,1.,0.)*1.2,(i+pow(i,4.)+step(.125,i))/3.);
 }
}

float tau=6.28318;
float star(vec3 p,float t,out float i){
 float re=tau/5.,a=mod(atan(p.y,p.x)+re*t*.25,re)-re*.5,
 l=length(p.xy);
 vec3 q=vec3(cos(a)*l,sin(a)*l,p.z*p.z*10.),d=vec3(1.,0.,0.);
 i=length(q.xy-d.xy*clamp(dot(q.xy,d.xy),.0,1.))-(.5-q.x*.25);
 return length(q-d*clamp(dot(q,d),.0,1.))-(.5-q.x*.25);
}
float m(vec3 p,out float i){
 float t=iTime*2.,a=atan(p.y,p.x);
 return min(star(p*vec3(2.,2.,1.),t*.25,i),
  star(vec3(mod(p.z+1.5,3.)-1.5,
   mod(a*12.+tau*.5+t*(mod(floor((p.z+1.5)/3.),2.)*3.-1.5),tau)-tau*.5,
    length(p.xy)-5.),t,a));
}
void mainImage(out vec4 col,in vec2 coord){
  vec2 uv=(coord-iResolution.xy*.5)/iResolution.y,e=vec2(.01,.0);
  vec3 ro=vec3(0.,0.,-3.),rd=normalize(vec3(-uv,1.)),cp=ro+rd;
  float st=0.,dist=0.,cd=0.,i,g;
  for(;st<1.;st+=1./200.){
   cd=m(cp,i);
   if(abs(cd)<.01||cd>300.)break;
   dist+=cd*.05;
   cp=ro+rd*dist;
  }
  col=mix(vec4(1.,.8,.0,0.),vec4(0.),length(uv)-st*st)+st*st;
  if(cd<.01){
  vec3 n=normalize(vec3(cd-m(cp+e.xyy,g),cd-m(cp+e.yyx,g),
  cd-m(cp+e.yyx,g)));
  float li=clamp(dot(n,vec3(.577)),0.,1.);
  col.rgb=vec3(.95,.4,.12)*.25;
  col.rgb+=vec3(1.,1.,1.)*li*.75;i=-i;
  col.rgb=mix(col.rgb,vec3(1.,1.,0.)*1.2,(i+pow(i,4.)+step(.125,i))/3.);
 }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uva = uv;
	/*uv.y*=(sin(iTime/10.0)+1.0)/2.0;
    uv.x*=(cos(iTime/10.0)+1.0)/3.0;
    */

    float freq = 5.0 + sin(iTime)*3.0;

    float split =15.0;
    float dsp = (1.0/split)*2.0;
    float esp = (1.0/split);
    if(mod(uv.x,dsp)>esp){
        uv.x+=esp;
    }
    else{
        uv.x-=esp;
    }

    if(mod(uv.y,dsp)>esp){
        uv.y+=esp;
    }
    else{
        uv.y-=esp;
    }


    uv.x+=sin((uv.y*freq)+iTime)/5.0;


    uv.x=mod(uv.x,1.0);
    uv.y=mod(uv.y+(iTime/split), 1.0);

    // Time varying pixel color
    vec4 col = texture(iChannel0,uv);

    col.r+=floor(uva.x*split)/split;
    col.b+=mod(1.0 - uva.y, 1.0);

    // Output to screen
    fragColor = col;
}

#define res iResolution
#define TAU 6.283185

void mainImage(out vec4 col, in vec2 _pos) {
    //get [-1,1] position
    vec2 pos = (_pos*2. - res.xy) / res.y;

    //the number of sides
    float numSides = iMouse.z > .5 ? iMouse.x/iResolution.x * 6. + 3. : sin(iTime * .3) + 4.;

    //ray direction, pos, and z
    vec2 rd = pos * 4.;
    vec2 ro = vec2(0);
    float z = 0.;

    //attenuation factor (see line 52)
    float att = 1.;

    for(int bounce = 0; bounce < 16; bounce++) {
        //the normal and the distance to the nearest line that intersects with the ray
        float fd = 100000.;
        vec2 fnorm;

        //check all lines (mirrors) in an ngon for intersections
        for(int line = 0; line < int(ceil(numSides)); line++) {
            float a = float(line) / numSides;

            //get the origin, direction, and normal of a line
            vec2 lo = vec2(sin(a * TAU), cos(a * TAU));
            vec2 ld = vec2(cos(a * TAU), -sin(a * TAU));
            vec2 norm = ld.yx * vec2(1,-1);

            //back-face culling (that also prevents a ray from hitting the same line twice)
            if(dot(norm, rd) > 0.) continue;

            //get the distance to the intersection
            float dist = (inverse(mat2(rd, ld)) * (lo - ro)).x;

            if(dist > -.0001 && dist < fd) {fd = dist; fnorm = norm;}
        }

        //if the ray exits the kaleidoscope before it hits the mirror,
        //move ray origin as far as it can go before it exits.
        if(z + fd > 1.) {
            ro += rd * (1. - z);
            break;
        }

        //increase z by the distance to the mirror
        z += fd;
        //move ray origin to the intersection
        ro += rd * fd;
        //reflect ray direction
        rd = reflect(rd,fnorm);
        //attenuate the reflected light a bit
        att *= .9;
    }

    //texture lookup
    col = texture(iChannel1, rd) * att;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 tex = texture(iChannel0,uv);

    // divide by sample-count
	vec3 color = tex.rgb/tex.a;

    #if !GRADING
    fragColor.rgb=color;return;
    #endif

	// vignette to darken the corners
	uv-=.5;
	color *= mix(vec3(.6,.9,1),vec3(1),1.-dot(uv,uv)*.75);

	// exposure
	color *= 2.5;

	// tonemap
	color /= color+1.;

	// gamma correction
	color = pow(color, vec3(.45));

	// grading
	{
		// make it pop
		color = smoothstep(.3,1.,color);

		// cold tint
		color = pow(color,vec3(1,1.05,1.1).bgr);
	}

	fragColor = vec4(color,1);
}

