// Uncomment to deactivate AABB
// #define NAIVE

const float FARAWAY=1e30;

struct Camera {
    vec3 Obs;
    vec3 View;
    vec3 Up;
    vec3 Horiz;
    float H;
    float W;
    float z;
};

struct Ray {
    vec3 Origin;
    vec3 Dir;
};

Camera camera(in vec3 Obs, in vec3 LookAt, in float aperture) {
   Camera C;
   C.Obs = Obs;
   C.View = normalize(LookAt - Obs);
   C.Horiz = normalize(cross(vec3(0.0, 0.0, 1.0), C.View));
   C.Up = cross(C.View, C.Horiz);
   C.W = float(iResolution.x);
   C.H = float(iResolution.y);
   C.z = (C.H/2.0) / tan((aperture * 3.1415 / 180.0) / 2.0);
   return C;
}

Ray launch(in Camera C, in vec2 XY) {
   return Ray(
      C.Obs,
      C.Obs+C.z*C.View+(XY.x-C.W/2.0)*C.Horiz+(XY.y-C.H/2.0)*C.Up 
   );
}

struct Material {
   vec3 Ka;
   vec3 Kd;
   vec3 Ks;
   float s;
   vec3 Ke;
};

struct Intersection {
   float t;
   Material material;
   vec3 P;
   vec3 N;
};

Intersection intersection() {
   Intersection I;
   I.t = FARAWAY;
   return I;
}

struct Sphere {
   vec3 Center;
   float R;
};

bool intersect_sphere(in Ray R, in Sphere S, out float t) {
   vec3 CO = R.Origin - S.Center;
   float a = dot(R.Dir, R.Dir);
   float b = 2.0*dot(R.Dir, CO);
   float c = dot(CO, CO) - S.R*S.R;
   float delta = b*b - 4.0*a*c;
   if(delta < 0.0) {
      return false;
   }
   t = (-b-sqrt(delta)) / (2.0*a);
   return (t > 0.0);
}

void sphere(in Ray R, in Sphere S, in Material M, inout Intersection I) {
   float t;
   if(intersect_sphere(R, S, t) && t < I.t) {
      I.t = t;
      I.material = M;
      I.P = R.Origin + t*R.Dir;
      I.N = normalize(I.P - S.Center);
   }
}


bool intersect_triangle(in Ray R, in vec3 A, in vec3 B, in vec3 C, out float t) {
   vec3 E1 = B-A;
   vec3 E2 = C-A;
   vec3 P = cross(R.Dir,E2);
   float det = dot(P,E1);
   if(det < 1e-6) {
      return false;
   }
   float invdet = 1.0 / det;
   vec3 T = R.Origin - A;
   float u = invdet * dot(P,T);
   if(u < 0.0 || u > 1.0) {
      return false;
   }
   vec3 Q = cross(T,E1);
   float v = invdet * dot(Q,R.Dir);
   if(v < 0.0 || (u+v) > 1.0) {
      return false;
   }
   t = invdet * dot(Q,E2);
   return true;
}

void triangle(in Ray R, vec3 A, vec3 B, vec3 C, in Material M, inout Intersection I) {
   float t;
   if(intersect_triangle(R, A,B,C, t) && t < I.t) {
      I.t = t;
      I.material = M;
      I.P = R.Origin + t*R.Dir;
      I.N = normalize(cross(B-A,C-A));
   }
}

bool segment_box_intersection(
  in vec3 q1,
  in vec3 dirinv,
  in vec3 boxmin,
  in vec3 boxmax
) {  
   vec3 T1 = dirinv*(boxmin - q1);
   vec3 T2 = dirinv*(boxmax - q1);
   vec3 Tmin = min(T1,T2); 
   vec3 Tmax = max(T1,T2);
   float tmin = max(max(Tmin.x, Tmin.y),Tmin.z);
   float tmax = min(min(Tmax.x, Tmax.y),Tmax.z);   
   return (tmax >= 0.0) && (tmin <= tmax) && (tmin <= 1.0);
}

int stack_top = -1;
int stack_n[20];
int stack_b[20];
int stack_e[20];

bool stack_empty() {
  return (stack_top < 0);
}

void stack_push(in int n, in int b, in int e) {
  stack_top++;
  stack_n[stack_top] = n;
  stack_b[stack_top] = b;
  stack_e[stack_top] = e;  
}

void stack_pop(out int n, out int b, out int e) {
  n = stack_n[stack_top];
  b = stack_b[stack_top];
  e = stack_e[stack_top];
  --stack_top;
}

void mesh_AABB(in Ray R, in Material M, inout Intersection I) {
#ifdef NAIVE
    for(int t=0; t<triangles.length(); ++t) {
        ivec3 T = triangles[t];
        triangle(R, vertices[T.x], vertices[T.y], vertices[T.z], M, I);
    }
#else    
    vec3 invDir = vec3(1.0/R.Dir.x, 1.0/R.Dir.y, 1.0/R.Dir.z);

    if(segment_box_intersection(R.Origin, invDir, bbox_min[1], bbox_max[1])) {    
       stack_push(1, 0, triangles.length());
    }

    while(!stack_empty()) {
       int n,b,e;
       stack_pop(n,b,e);
       if(e-b <= leaf_size) {
	     for(int t=b; t<e; ++t) {
             ivec3 T = triangles[t];
             triangle(R, vertices[T.x], vertices[T.y], vertices[T.z], M, I);
          }
        } else {
       	  int m = b + (e-b)/2;
	      if(segment_box_intersection(R.Origin, invDir, bbox_min[2*n], bbox_max[2*n])) {
	         stack_push(2*n, b, m);
	      }
	      if(segment_box_intersection(R.Origin, invDir, bbox_min[2*n+1], bbox_max[2*n+1])) {
	         stack_push(2*n+1, m, e);
	      }
       }
    }
#endif    
}


void sky(out vec4 fragColor, in Ray R) {
   vec3 V = normalize(R.Dir);
   float s = 0.5 * (V.z + 1.0);
   fragColor = mix( 
      vec4(0.0, 0.0, 0.0, 1.0),
      vec4(1.0, 1.0, 2.0, 1.0),
      s
   );
}


vec3 lighting( in Intersection I, in vec3 LightPos) {
   vec3 L = LightPos - I.P;
   float diffuse = max(dot(I.N, L)/length(L),0.0);
   return diffuse * I.material.Kd;
}

