#define MAX_STEPS 800
#define MAX_DIST 100.
#define LIGHT_VOLUME_STEP 0.05
#define SURF_DIST 0.01
#define PI 3.1415
#define LIGHT_DENSITY 0.3
#define LIGHT_POS vec3(0., 15., 1.3)
#define LIGHT_DIR vec3(0., -1., 0.)

float GetLightDist(vec3 p)
{
    vec3 base = LIGHT_POS;
    base.y = 0.;

	return GetCone(p, base, LIGHT_POS, 2.9, 0.0);
}

float RayMarchLight(vec3 ro, vec3 rd)
{
    float d0 = 0.;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * d0;
        float ds = abs(GetLightDist(p));
        d0 += ds;

        if (d0 > MAX_DIST || ds < SURF_DIST)
        {
            break;
        }
    }

    return step(d0, MAX_DIST / 2.) * d0;
}

float GetRadioEdge(vec3 p)
{   
    vec3 n = -vec3(0,0,1);
    float leftPlane = dot(p, n * GetRot3dY(2. * PI / 6.));
    float rightPlane = dot(p, n * GetRot3dY(-2. * PI / 6.));

    p -= vec3(0,0.0,1);

    float result = GetBox(p, vec3(1, .1, 1));
    result = max(result, leftPlane);
	result = max(result, rightPlane);

    return result;
}

float GetRadioShape(vec3 p)
{
    float base = GetBox(p, vec3(10,1,20), vec3(0, -1, 0), vec3(0, 0, 0));

    float s1 = GetRadioEdge(p);
    float s2 = GetRadioEdge(p * GetRot3dY(2. * PI / 3.));
    float s3 = GetRadioEdge(p * GetRot3dY(-2. * PI / 3.));

    float outer = GetCylinder(p, vec3(0,-1,0), vec3(0,1,0), 2.);
    float inner = GetCylinder(p, vec3(0,-1,0), vec3(0,1,0), .4);
    float center = GetCylinder(p, vec3(0,-.05,0), vec3(0,.05,0), .2);

    float result = s1;
    result = min(result, s2);
    result = min(result, s3);
    result = max(result, outer);
    result = max(result, -inner);
    result = min(result, center);

    return result;
}

float GetSpheres(vec3 p)
{
    vec3 s1Pos = vec3(0, 2.6, 1.3) + vec3(cos(-iTime * 0.3), 0, sin(-iTime * 0.3)) * 1.3;
    vec3 s2Pos = vec3(0, 1.7, 1.3) + vec3(cos(iTime * 0.26), 0, sin(iTime * 0.26)) * 1.7;

    float s1 = GetSphere(p, s1Pos, .3);
    float s2 = GetSphere(p, s2Pos, .3);

    return min(s1, s2);
}

float GetRoom(vec3 p)
{
    vec3 pFloor = p - vec3(0,0,0);
    vec3 pCeil = p - vec3(0,4,0);

    float fl = GetBox(pFloor, vec3(5, .1, 5));
    float ce = GetBox(pCeil, vec3(5, .1, 5));
    float hole = GetCylinder(p - vec3(0,4,1.3), vec3(0,-1.,0), vec3(0,1.,0), 2.1);

    float result = ce;
    result = max(result, -hole);
    result = min(result, fl);

    return result;
}

float GetDistScene(vec3 p)
{
    vec3 pShape = p - vec3(0,4.05,1.27);
    pShape *= GetRot3dY(iTime);

    float shape = GetRadioShape(pShape);   
    float room = GetRoom(p);

    return min(room, shape);
}

float RayMarchScene(vec3 ro, vec3 rd)
{
    float d0 = 0.;
        for (int i = 0; i < MAX_STEPS; i++)
        {
            vec3 p = ro + rd * d0;
            float ds = abs(GetDistScene(p));
            d0 += ds;

            if (abs(d0) > MAX_DIST || ds < SURF_DIST)
            {
                break;
            }
        }

    return min(d0, MAX_DIST);
}

vec3 GetNormal(vec3 p)
{   
    float d = GetDistScene(p);
    vec2 e = vec2(0.01, .0);

    vec3 n = d - vec3(
    	GetDistScene(p - e.xyy),
    	GetDistScene(p - e.yxy),
    	GetDistScene(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p, out vec3 n)
{  
    vec3 lightPos = LIGHT_POS;
    vec3 lray = normalize(lightPos - p);
    n = GetNormal(p);
    float distToLight = length(lightPos - p);

    float co = radians(10.0);
    float coDel = radians(14.0);
    float fall = clamp((dot(lray, -LIGHT_DIR) - cos(coDel)) / (cos(co) - cos(coDel)), 0.0, 1.0);
    float diff = max(0.0, dot(lray, n));

    float shadow = 1.;

    float realDistToLight = RayMarchScene(p + n * .02, lray);
    if (realDistToLight < distToLight)
    {
        shadow = 0.7;
    }

    return diff * fall * shadow;
}

float GetLightDistance(vec3 ro, vec3 rd, out vec3 pIn, out vec3 pOut)
{
    vec3 roBack = ro + rd * 20.;

    float dc = RayMarchLight(ro, rd);
    float df = RayMarchLight(roBack, -rd);

    if (dc < 0.01 && df < 0.01)
    {
        return 0.;
    }

    pIn = ro + rd * dc;
    pOut = roBack - rd * df;

    return length(pIn - pOut);
}

float GetLightDensity(vec3 ro, vec3 rd, vec3 hitPoint)
{
    vec3 pIn;
    vec3 pOut;

    float traverseDist = GetLightDistance(ro, rd, pIn, pOut);
    if (traverseDist < 0.01 || length(ro - pIn) > length(ro - hitPoint))
    {
        return 0.;
    }

    float distToHitPoint = length(pIn - hitPoint);
    traverseDist = min(traverseDist, distToHitPoint);

    vec3 singleStep = rd * LIGHT_VOLUME_STEP;
    vec3 lSetVec = vec3(0);
    float lightDensity = 0.;

    while (length(lSetVec) < traverseDist)
    {
        vec3 vlP = pIn + lSetVec;
        vec3 lightDir = normalize(LIGHT_POS - vlP);

        float distToLight = length(vlP - LIGHT_POS);
        float realDistLoLight = RayMarchScene(vlP, lightDir);
        realDistLoLight = max(realDistLoLight, distToLight);

        float distMult = smoothstep(1., 0.8, distToLight / LIGHT_POS.y);
        lightDensity += LIGHT_VOLUME_STEP * LIGHT_DENSITY * distMult * (1. - step(realDistLoLight, distToLight));

        lSetVec += singleStep;
    }

    return lightDensity * 0.4;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0, 1.5, -4.9);   
    vec3 rd = normalize (vec3(uv.x, uv.y, 1));    
    rd *= GetRot3dX(-.1);

    float d = RayMarchScene(ro, rd);

    vec3 n;
    vec3 p = ro + rd * d;
    float dif = GetLight(p, n);
    vec3 diffColor = vec3(1, 1, 0) * dif * 0.4;

    float lightDensity = GetLightDensity(ro, rd, p);
    vec3 lightVolumeColor = lightDensity * vec3(1, 1, 0);

    fragColor = vec4(diffColor + lightVolumeColor, 1.0);
}

