//math help

mat2 GetRot2d(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat2(c, -s, s, c);
}

mat3 GetRot3dX(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat3(vec3(1, 0, 0), vec3(0, c, -s), vec3(0, s, c));
}

mat3 GetRot3dY(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat3(vec3(c, 0, s), vec3(0, 1, 0), vec3(-s, 0, c));
}

mat3 GetRot3dZ(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat3(vec3(c, -s, 0), vec3(s, c, 0), vec3(0, 0, 1));
}

//Primitives

float GetSphere(vec3 p, vec3 spherePos, float r)
{
    return length(p - spherePos) - r;
}

float GetCylinder(vec3 p, vec3 top, vec3 bottom, float radius)
{
    vec3 ab = bottom - top;
    vec3 ap = p - top;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = top + t * ab;

    float x = length(p - c) - radius;
    float y = (abs(t - .5) - .5) * length(ab);
    float exterior = length(max(vec2(x, y), 0.));
    float interior = min(max(x, y), 0.);

    return exterior + interior;
}

float GetBox(vec3 p, vec3 s)
{
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float GetBox(vec3 p, vec3 size, vec3 pos, vec3 rot)
{   
    p -= pos;    
    p *= GetRot3dZ(rot.z) * GetRot3dY(rot.y) * GetRot3dX(rot.x);

    return GetBox(p, size);
}

float GetCone(vec3 p, vec3 a, vec3 b, float ra, float rb)
{
    float rba  = rb-ra;
    float baba = dot(b-a,b-a);
    float papa = dot(p-a,p-a);
    float paba = dot(p-a,b-a)/baba;

    float x = sqrt( papa - paba*paba*baba );

    float cax = max(0.0,x-((paba<0.5)?ra:rb));
    float cay = abs(paba-0.5)-0.5;

    float k = rba*rba + baba;
    float f = clamp( (rba*(x-ra)+paba*baba)/k, 0.0, 1.0 );

    float cbx = x-ra - f*rba;
    float cby = paba - f;

    float s = (cbx < 0.0 && cay < 0.0) ? -1.0 : 1.0;

    return s*sqrt( min(cax*cax + cay*cay*baba,
                       cbx*cbx + cby*cby*baba) );
}

