void
mainImage(out vec4 fragColor, in vec2 fragPos)
{
    vec3 col;
    vec2 screen = fragPos / iResolution.xy;

    //Simple montecarlo integration
    if(iFrame > 0)
    {
        col = texture(iChannel0, screen).xyz;
        col /= float(iFrame);
    } 

