        a = A(V);
        r = min(1e3, ln(U,V,-z*X*a.xy,0.));
        V -= z*X*a.xy;
        a = A(V);
        r = min(r,ln(U,V,-z*X*a.xy,1.));
        Q += exp(-2.*r);
    }
}

