// Misc
#define SCALE_BEND 8.0
#define SCALE_TIME 0.7
#define COLOR vec3(0.0, 1.0, 1.0) * 1.0

#define SCALE_UV  (1.0 / 1.2)
#define OFFSET_UV vec2(((iResolution.x / iResolution.y / SCALE_UV) - 1.0) * 0.5, 0.1)

// AA
#define LINE_HALF_WIDTH (2.5 / iResolution.y / SCALE_UV)
#define DISTANCE_RESOLUTION (0.2 / iResolution.y / SCALE_UV)
#define NUM_ANGLES 8.0

// Background
#define COLOR_CHECKER1 vec3(1.0) * 0.35
#define COLOR_CHECKER2 vec3(1.0) * 0.70
#define SCALE_CHECKER 4.0


float smoothSmoothStep(float t, float p) {
  // Odd around (0.5, 0.5) with f(u \in [0, 0.5]) = (2 t)^p / 2.
  float u = clamp(t, 0.0, 1.0);
  float b = float(u >= 0.5);
  float s = 2.0 * b - 1.0;
  return b - s * pow((2.0 * (b - s * u)), p) * 0.5;
}


float getDistance(vec2 uv, float p) {
  float result = LINE_HALF_WIDTH;
  bool init_state = uv.y < smoothSmoothStep(uv.x, p);

  // Search NUM_ANGLES directions
  for (float t = 0.0; t < NUM_ANGLES; t++) {
    vec2 dire = vec2(cos(2.0 * 3.1415 * t / NUM_ANGLES), sin(2.0 * 3.1415 * t / NUM_ANGLES));

    // Search up-to DISTANCE_RESOLUTION
    bool state = init_state;
    for (float i = 1.0; i < LINE_HALF_WIDTH / DISTANCE_RESOLUTION; i++) {
      float curr_step = i * DISTANCE_RESOLUTION;
      vec2 xy = uv + curr_step * dire;
      bool curr_state = xy.y < smoothSmoothStep(xy.x, p);
      if (state != curr_state && curr_step < result) {
        result = curr_step - DISTANCE_RESOLUTION;
        break;
      }

      state = curr_state;
    }
  }

  return result;
}


vec3 checker(vec2 uv) {
  float xi = floor(SCALE_CHECKER * uv.x);
  float yi = floor(SCALE_CHECKER * uv.y);
  bool even_spot = mod(xi + yi, 2.0) == 0.0;
  return even_spot ? COLOR_CHECKER1 : COLOR_CHECKER2;
}


void mainImage(out vec4 frag_color, in vec2 frag_coord) {
  vec2 uv = (frag_coord + vec2(0.5, 0.5)) / iResolution.y / SCALE_UV - OFFSET_UV;

  float q = cos(SCALE_TIME * 3.1415 * iTime);
  float p = pow(SCALE_BEND, q);
  float d = getDistance(uv, p);

  float fac = smoothstep(LINE_HALF_WIDTH, 0.0, d);
  frag_color = vec4(mix(checker(uv), COLOR, fac), 1.0);
}

