    uv *= scale;
    uv.y += 0.5*iTime;
    vec2 id = floor(uv);
    vec2 xy = fract(uv)*2.0-1.0;
    for( float i = -3.0; i <= 3.0; i+=1.0 )
    {
    	for( float j = -3.0; j <= 3.0; j+=1.0 )
        {
            vec2 random = hash(id+vec2(i,j));
			vec2 center = random*vec2(0.5*sin(random.x*6.283 + iTime*1.5), 1.0) + vec2(i,j)*2.0 ;

            float lightness = max(0.0, 0.025/distance(xy, center)*(random.y*1.5+1.5)-0.07);
            //float lightness = max(0.0, exp2(-24.0*distance(xy, center))*(random.y*1.5+1.5));
            lightness *= step(random.x, density);

            col += lightness; 
