//background
vec3 background(vec3 rd, vec3 colA, vec3 colB)
{
    //radial lines
    float a = (atan(rd.x, rd.y)/6.2831853) + 0.5, //0->1
          l = floor(a * 24.0) / 24.0; //partition cells    
    vec3 pc = colB * 2.0 *
              S(0.46, 0.5, fract(a*24.0)) * S(0.54, 0.5, fract(a*24.0));
    //horizontal lines
    float dY = pow(abs(rd.y), 0.8), //problem with power - thanks iapafotoo
          cY = fract(dY*6.0-T*0.4),
          cYID = floor(dY*6.0-T*0.4),
          cYH = hash11(cYID) - 0.6,    
          tt = mod(T*cYH*4.0*sign(rd.y), 3.0) - 1.5,
          dX = length(tt - rd.x);
    pc += colB * 1.6 * S(0.02, 0.0, length(cY - 0.5)); //lines
    //noise
    pc *= (2.0 +n3D(rd*3.6+T)) * n3D(rd*5.0+T*0.3);
    //sparks
    pc += (cYH*sign(-rd.y)>0.0 ? step(tt, rd.x) : step(rd.x, tt)) * //clip
          colA * 6.0 * S(0.05, 0.0, length(cY - 0.5)) / //line 
          (1.0 + dX*dX*60.); //attenuation 
    //fade
    pc *= max(abs(rd.y*0.4), 0.);
    //center glow
    pc += colB / ((1.0 + abs(rd.x)*abs(rd.x)*8.0) * (1.0 + dY*dY*100.0));

    return pc;
}

vec4 renderScene(vec3 ro, vec3 rd, vec3 lp, vec3 colA, vec3 colB)
{
    float t = 0.0;
    vec3 pc = background(rd, colA, colB); 

    for (int i=ZERO; i<80; i++)
    {
        vec3 p = ro + rd*t;
        vec4 ns = map(p);
        //if (abs(ns.x)<EPS && ns.x==ns.y) break;
        pc += 0.3 * colA / (1.0 + ns.y*ns.y*200.0);
        float atn = 1.0 / (1.0 + length(p-ro)*length(p*ro)*0.0001);
        pc += step(ns.z, EPS) * step(ns.w, 0.4) * //clip 
              0.008 * colB / (1.0 + ns.z*ns.z*30.0) * atn;
        t += max(EPS*2.0, ns.x * 0.6);
        if (t>FAR) break;
    }    

    return vec4(pc, t);
}

vec3 camera(vec2 U, vec3 ro, vec3 la, float fl) 
{
    vec2 uv = (U - R*.5) / R.y;
    vec3 fwd = normalize(la-ro),
         rgt = normalize(vec3(fwd.z, 0., -fwd.x));
    return normalize(fwd + fl*uv.x*rgt + fl*uv.y*cross(fwd, rgt));
}

void mainImage(out vec4 C, in vec2 U)
{
    vec3 col = vec3(0),
         la = vec3(0),
         colA = rotHue(vec3(1,0,0), T*0.1),
         colB = rotHue(vec3(1,0,0), (T-3.0)*0.1),
         ro = vec3(0.0, sin(T*0.1)*2.0, -11.0 - sin(T*0.2)*2.0);

    ro.xz *= rot(sin((T+5.0)*0.3)*0.3);
    vec3 rd = camera(U, ro, la, 1.4);
    vec3 lp = vec3(3.0, 4.0, -2.0);

	vec4 scene = renderScene(ro, rd, lp, colA, colB);
    col = scene.xyz;

    vec4 sph = vec4(0.0, 0.0, 0.0, 3.0);
    vec2 si = sphIntersect(ro, rd, sph);
    float sd = sphDensity(ro,rd, sph, FAR);
    if (si.x>0.0)
    {

        col *= scene.y==2.0 ? 0.0 : 0.4;
        vec3 pN = ro + rd*si.x;
        vec3 pF = ro + rd*si.y;

        vec3 nN = sphNormal(pN, sph);
        vec3 nF = sphNormal(pF, sph) * -1.0;

        vec3 ldN = normalize(lp - pN);
        vec3 ldF = normalize(lp - pF);

        float specN = pow(max(dot(reflect(-ldN, nN), -rd), 0.0), 16.0);
        float specF = pow(max(dot(reflect(-ldF, nF), -rd), 0.0), 16.0);
        float fres = pow(clamp(dot(nN, rd) + 1.0, 0.0, 1.0), 2.0);

        col += colA * pow(sd, 4.0) * max(0.0, (sin(T*4.0)+0.5) * 0.6);

        col += vec3(1) * specN;  
        col += vec3(1) * specF*0.2;  

        //reflection
        vec3 rro = pN;
        vec3 rrd = reflect(rd, nN);
        vec4 rScene = renderScene(rro, rrd, lp, colA, colB);
        col += rScene.xyz * fres;
    }

    col += colA*2.0*snow(normalize(vec3(0.0, 0.0, -1.0)));

    C = vec4(col, 1.0);
}

#define PI 3.14159265359
#define rot(a) mat2(cos(a + PI*0.5*vec4(0,1,3,0)))

// uncomment to use procedural noise
//#define PROCEDURAL
// uncomment to use screen-space derivatives
//#define SCREENSPACE

vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

// anti-aliased and mipmapped nearest neighbor sampling
vec4 myTexture(vec3 uv, vec3 dpdx, vec3 dpdy) {
    vec3 res = vec3(32);
    uv = uv*res + 0.5;
    vec3 fl = floor(uv);
    vec3 fr = fract(uv);
    vec3 aa = (abs(dpdx)+abs(dpdy))*res*0.5;
    fr = smoothstep( vec3(0.5)-aa, vec3(0.5)+aa, fr);
    uv = (fl+fr-0.5) / res;
    return textureGrad(iChannel0, uv, dpdx, dpdy);
}

// https://research.nvidia.com/sites/default/files/pubs/2017-02_Hashed-Alpha-Testing/Wyman2017Hashed.pdf
const float hashScale = 1.0;
float hashedNoise(vec3 p, vec3 dpdx, vec3 dpdy) {
    float maxDeriv = max(length(dpdx), length(dpdy));
    float pixScale = 1.0 / (hashScale*maxDeriv);
    vec2 pixScales = exp2(floor(log2(pixScale))) * vec2(1,2);

    #ifdef PROCEDURAL
    vec2 alpha = vec2(hash33(floor(pixScales.x*p.xyz)).r, hash33(floor(pixScales.y*p.xyz)).r);
    #else
    float aa = myTexture(pixScales.x*p.xyz/32.0, pixScales.x*dpdx/32.0, pixScales.x*dpdy/32.0).r;
    float bb = myTexture(pixScales.y*p.xyz/32.0, pixScales.y*dpdx/32.0, pixScales.y*dpdy/32.0).r;
    vec2 alpha = vec2(aa, bb);
    #endif

    float lerpFactor = fract( log2(pixScale) );
    float x = mix( alpha.x, alpha.y, lerpFactor );
    float a = min( lerpFactor, 1.0-lerpFactor );
    vec3 cases = vec3( x*x/(2.0*a*(1.0-a)),(x-0.5*a)/(1.0-a),1.0-((1.0-x)*(1.0-x)/(2.0*a*(1.0-a))) );
    float alphaRes = (x < (1.0-a)) ? ((x < a) ? cases.x : cases.y) :cases.z;
    alphaRes = clamp(alphaRes, 0.0, 1.0);
    return alphaRes;
}

// https://iquilezles.org/articles/filteringrm
void calcDpDxy( in vec3 ro, in vec3 rd, in vec3 rdx, in vec3 rdy, in float t, in vec3 nor, 
                out vec3 dpdx, out vec3 dpdy ) {
    dpdx = t*(rdx*dot(rd,nor)/dot(rdx,nor) - rd);
    dpdy = t*(rdy*dot(rd,nor)/dot(rdy,nor) - rd);
}

float noise( in vec3 x ) {
    return textureLod(iChannel0, x/32.0, 0.0).r;
}

float height(vec2 p) {
   	return (
        noise(vec3(p, 0))*4.0 +
        noise(vec3(p*4.124, 10))*0.5 +
        noise(vec3(p*8.124, 100))*0.25)*0.3;
}

float de(vec3 p) {
    vec2 inPill = mod(p.xz, 3.0)-1.5;
    float pill = length(inPill)-0.1;
    return min(pill, p.y - height(p.xz));
}

vec3 getNormal(vec3 p) {
	vec3 e = vec3(0.0, 0.03, 0.0);
	return normalize(vec3(
		de(p+e.yxx)-de(p-e.yxx),
		de(p+e.xyx)-de(p-e.xyx),
		de(p+e.xxy)-de(p-e.xxy)));	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

	vec3 from = vec3(0, 1, -5);
	vec3 dir = normalize(vec3(uv*0.4, 1.0));

	vec2 mouse=(iMouse.xy - iResolution.xy*0.5) / iResolution.y * 2.5;
    if (iMouse.z < 0.5) mouse = vec2(0, -0.4);
	mat2 rotxz = rot(-mouse.x+iTime*0.2);
	mat2 rotxy = rot(mouse.y);
	dir.zy  *= rotxy;
	dir.xz  *= rotxz;
    from.zy *= rotxy;
	from.xz *= rotxz;

	float totdist = 0.0;
	for (int steps = min(iFrame, 0) ; steps < 200 ; steps++) {
		vec3 p = from + totdist * dir;
        float dist = de(p);
		totdist += dist*0.5;
        if (totdist < 0.0001 || totdist > 20.0) {
        	break;   
        }
	}

    vec3 p = from + totdist * dir;
    vec3 n = getNormal(p);
    n += (hash33(vec3(fragCoord, iFrame))-0.5)*0.1;
    n = normalize(n);
    vec3 dpdx = vec3(0);
    vec3 dpdy = vec3(0);
    #ifndef SCREENSPACE
    calcDpDxy(from, dir, dir+dFdx(dir), dir+dFdy(dir), totdist, n, dpdx, dpdy);
    #else
    dpdx = dFdx(p);
    dpdy = dFdy(p);
    #endif



    fragColor = vec4(hashedNoise(p, dpdx, dpdy));
    return;

}

// TODO:
// - [x] interpolation curve
// - [x] different direction
// - [x] aniti aliasing
// - [x] different color
// - [x] fit vertical size
// - [ ] offset by mouse position
// - [ ] use random color

#define AA 4.0
#define NUM_ROWS 4.0
#define SCALE NUM_ROWS * (sqrt(3.0) / 2.0)
#define COLOR1 vec3(1.0) * 0.15
#define COLOR2 vec3(0.0, 1.0, 1.0) * 0.95
#define COLOR3 vec3(1.0, 0.0, 1.0) * 0.95
#define COLOR4 vec3(1.0, 1.0, 0.0) * 0.95

float someStep(float t) {
    // Doubles step so it's more "sticky".
    return smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t));
}

float smoothMod(float s, float m) {
  return someStep(mod(s, m) / m) * m;
}

vec2 invSimplexFrame(vec2 xy, float state) {
  mat3 transf;
  if (state == 0.0) {
    transf = mat3(
              1.0,             0.0, 0.0,
        1.0 / 2.0, sqrt(3.0) / 2.0, 0.0,
              0.0,             0.0, 1.0);
  }
  if (state == 1.0) {
    transf = mat3(
            - 1.0,             0.0, 0.0,
      - 1.0 / 2.0, sqrt(3.0) / 2.0, 0.0,
              1.0,             0.0, 1.0);
  }
  if (state == 2.0) {
    transf = mat3(
      - 1.0 / 2.0, - sqrt(3.0) / 2.0, 0.0,
        1.0 / 2.0, - sqrt(3.0) / 2.0, 0.0,
        1.0 / 2.0,   sqrt(3.0) / 2.0, 1.0);
  }
  return vec2(inverse(transf) * vec3(xy, 1));
}

void mainImage(out vec4 frag_color, in vec2 frag_coord) {
  float state1;    // {0, 1, 2}
  bool  state2;    // {0, 1}
  float state3;    // [0, 2)
  bool  px_state1; // {0, 1}

  // Offset time so that first frame shows triangles for shadertoy browser.
  float t = iTime + 3.0;
  state1 = mod(t, 6.0) < 2.0 ? 0.0 :
           mod(t, 6.0) < 4.0 ? 1.0 :
                               2.0;
  state2 = mod(t, 4.0) < 2.0;
  state3 = smoothMod(t, 2.0);

  // Multi sampling
  vec3 color;
  for (float i = 0.0; i <= 1.0; i += 1.0 / AA) {
    for (float j = 0.0; j <= 1.0; j += 1.0 / AA) {

      // Rendering code of single uv
      vec2 uv = SCALE * (frag_coord + vec2(i, j)) / iResolution.y;
      vec2 st = invSimplexFrame(uv, state1);
      px_state1 = fract(st.x) + fract(st.y) > state3;
      vec3 c;
      if (state1 == 0.0) {
        c = px_state1 ? (state2 ? COLOR2 : COLOR1) : (state2 ? COLOR1 : COLOR3);
      }
      if (state1 == 1.0) {
        c = px_state1 ? (state2 ? COLOR3 : COLOR1) : (state2 ? COLOR1 : COLOR4);
      }
      if (state1 == 2.0) {
        c = px_state1 ? (state2 ? COLOR4 : COLOR1) : (state2 ? COLOR1 : COLOR2);
      }
      color += c;
    }
  }
  color /= pow((AA + 1.0), 2.0);

  frag_color = vec4(color, 1.0);
}

