//---Wavetable Viewer
// by Catzpaw 2020

//STATE (iChannel0)
vec4 peek(in ivec2 a){return texelFetch(iChannel0,a,0);}
const ivec2 txsel=ivec2(2,0);

//FONT (iChannel3)
vec2 cur=vec2(0);
float siz=24.;
float chr(int c){
    vec2 p=cur*siz/16.;
    vec4 t=p.x<.0||p.x>.0625||p.y<0.||p.y>.0625?vec4(0):
        textureGrad(iChannel3,p+fract(vec2(c,15-c/16)/16.),dFdx(p),dFdy(p));
    cur.x-=.5/siz;
    return length(t.yz)==0.?0.:t.x;
}
int imod(uint x,uint y){return int(x-y*(x/y));}
float puthex(uint n){int b=imod(n,16u)>9?55:48;return chr(b+imod(n,16u));}

//GRAPHICS
float rect(vec2 p,vec4 r){return (p.x>r.x)&&(p.y>r.y)&&(p.x<r.x+r.z)&&(p.y<r.y+r.w)?1.:0.;}

//MAIN
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=(fragCoord.xy-.5*iResolution.xy)/iResolution.x;
    vec3 col=vec3(0);
    float c=0.3-rect(uv,vec4(-2,-.2,4,.4))*.15;
    int t=int(peek(txsel).x);
    cur=vec2(.095,-.2)+uv;
    c+=chr(83);c+=chr(79);c+=chr(85);c+=chr(78);c+=chr(68);
    c+=chr(32);c+=chr(35);c+=puthex(uint(t));
    siz=32.;
    float l=float(wl[t]),x=-.5+.125/24.,y=0.;
    for(int i=0;i<int(wl[t]);i++){
        y=float((wt[i]>>(t*4))&15u);
        float h=(y-7.5)*.025,v=-1.;
        if(h<0.){v=rect(uv,vec4(x,h,.7/l,-h))*.4;}else{v=rect(uv,vec4(x,0,.7/l,h))*.4;}
        if(v>0.){col+=mix(vec3(0,1,1),vec3(1,0,1),abs(h)*4.);}
        cur=vec2(0.495-float(i)*(1./l),.235)+uv;
        c+=puthex(uint(y))*.1;
        x+=1./l;
    }
    fragColor=vec4(col+c,1);
}

