//---Scale Sequencer
// by Catzpaw 2020

//SETUP
const float base =432.;	//A tuning (Hz)
const float bpm  =135.;	//beats per minute
const float steps=240.;	//block length
const float start=1.;   //start count (beats)
const float fade =10.;  //fade out time (sec)
const float sec  =180.; //performance time (includes fade out time and start count)

//SCALE
const int forcescale=-1; //0-10:scale compulsion -1:disable...use pattern data
const uint[11] scl=uint[](
    0xcb975420u, //0:major
    0xca875320u, //1:natural minor
    0xcb875320u, //2:harmonic minor
    0xcb975320u, //3:melodic minor
    0xc9974320u, //4:major blues
    0xca965320u, //5:minor blues
    0x42c97420u, //6:major pentatonic
    0x53ca7530u, //7:minor pentatonic
    0xc9974420u, //8:major pentatonic (to heptatonic)
    0xca775330u, //9:minor pentatonic (to heptatonic)
    0xeca86420u);//10:whole tone
int scalenote(int sc,int y){
    sc=forcescale>=0?forcescale:sc;
    return y<0?-999:(int(scl[sc])>>(y&7)*4)&15;
}

//PATTERN
// melodic:scale(4),tonic(4),mode(4),seed1(8),seed2(12)
// percussive:note(2x16)
const uint[32] pat=uint[](
    //00-0B melody
    0x74000000u,0x74011171u,0x74012172u,0x74013173u,
    0x74014174u,0x74015165u,0x74016166u,0x74017167u,
    0x74028168u,0x74019119u,0x7401a11au,0x7401b11bu,
    //0C-0F backing
    0x74001434u,0x74001491u,0x74001002u,0x74001002u,
    //10-13 chord
    0x74004434u,0x74004491u,0x74004002u,0x74004002u,
    //14-17 bass
    0x74004434u,0x74004491u,0x74004002u,0x74000003u,
    //18-1F rhythm
    0x313aa13au,0x513aa186u,0x00000000u,0x00000000u,  //sd+hh
    0x91e121e1u,0x55e121e5u,0x00000000u,0x00000000u); //bd+conga

//SEQUENCE
// speed(2),algorithm(2),division(4),pattern1(8),key1(4),pattern2(8),key2(4)
//  >>>>algorithm:0mute 1,2,3note(no difference so far)
//  0123speed:0half
//  4567      1normal
//  89AB      2double
//  CDEF      3quad
const uint[16] seq1=uint[]( //snare+hihat
    0x50000180u,0x50000180u,0x50000180u,0x58180190u,
    0x50000180u,0x50000180u,0x50000180u,0x50000190u,
    0x50000180u,0x50000180u,0x50000180u,0x58180190u,
    0x50000180u,0x50000180u,0x50000180u,0x50000190u);
const uint[16] seq2=uint[]( //bassdrum+conga
    0x500001c0u,0x500001c0u,0x500001c0u,0x5e1c01d0u,
    0x500001c0u,0x500001c0u,0x500001c0u,0x581c01d0u,
    0x500001c0u,0x500001c0u,0x500001c0u,0x581c01d0u,
    0x500001c0u,0x500001c0u,0x500001c0u,0x500001d0u);
const uint[16] seq3=uint[]( //bass
    0x50000140u,0x50000140u,0x50000141u,0x50000165u,
    0x50000140u,0x50000140u,0x50000141u,0x50000176u,
    0x50000145u,0x50000143u,0x50000144u,0x50000160u,
    0x50000145u,0x50000143u,0x50000144u,0x50000176u);
const uint[16] seq4=uint[]( //chord
    0x50000100u,0x50000100u,0x50000101u,0x50000125u,
    0x50000100u,0x50000100u,0x50000101u,0x50000136u,
    0x50000105u,0x50000103u,0x50000104u,0x50000120u,
    0x50000105u,0x50000103u,0x50000104u,0x50000136u);
const uint[16] seq5=uint[]( //backing
    0x100000c0u,0x500000c0u,0x100000c1u,0x980d80e5u,
    0x100000c0u,0x500000c0u,0x100000c1u,0x980d00f6u,
    0x100000c5u,0x500000c3u,0x100000c4u,0x980d60e0u,
    0x100000c5u,0x500000c3u,0x100000c4u,0x980d00f6u);
const uint[16] seq6=uint[]( //melody
    0x10000010u,0x10000020u,0x10000031u,0x10000045u,
    0x10000010u,0x10000020u,0x10000031u,0x10000056u,
    0x10000065u,0x10000073u,0x10000084u,0x10000090u,
    0x10000065u,0x10000073u,0x100000A4u,0x100000B6u);

//BIT OPERATION
int imod(int x,int y){return int(x-y*(x/y));}
int imod(uint x,uint y){return int(x-y*(x/y));}
int get8(uint b,int l){return int(b>>l*4)&255;}
int get4(uint b,int l){return int(b>>l*4)&15;}
int get2(uint b,int l){return int(b>>l*2)&3;}

//PATTERN GENERATOR
float hash(float s){s=fract(s*.13);s*=s+33.33;return fract(s+s*s);}
float pgen(float s,float r){
    int p=0;
    for(int i=0;i<16;i+=2){p+=int(hash(s)*r)<<i;s+=1.;}
    return float(p);
}

//AMP
//  vec3 p( drive, pressure, volume)
vec2 amp(vec2 i,vec3 p){return clamp(sign(i)*pow(abs(i*p.x),vec2(1./p.y)),-1.,1.)*p.z;}

//FREQUENCY FROM MIDI NOTE NUMBER
float freq(float n){return pow(2.,(n-69.)/12.)*base*6.283;}

//ENVELOPE
float env_d(float x,float d){return max(0.,1.-x/max(d,1e-4));}

//CHIPTUNE INSTRUMENTS (freq,time)
float dutybass(float f,float x){return osc_duty(f*.5,.5)*env_d(x,.6);}
float tri4bass(float f,float x){return osc_tri4(f*.5);}
float c30chord(float f,float x){return osc_c30(f,6);}
float c30bell(float f,float x){return osc_c30(f,1)*env_d(x,.6);}
float c30lead(float f,float x){return osc_c30(f,4)*env_d(x,.5);}

//CHIPTUNE PERCUSSION (time)
float nessd(float x){return osc_nesnoise(x,11)*env_d(x,.2);}
float neshh(float x,float d){return osc_nesnoise(x,0)*env_d(x,d);}
float nesbd(float x){return osc_duty(20./(x*.5+.1),.5)*env_d(x,.2);}
float neslc(float x){return osc_duty(65./(x*.3+.1),.25)*env_d(x,.05);}
float neshc(float x){return osc_duty(75./(x*.3+.1),.25)*env_d(x,.05);}

//NOTE GENERATORS
int rnd1(float s,float s1,float s2){
    int r=int(mod(abs(sin(floor(s)*s1*s2)*s2),4.)-1.2);return r<0?-999:r;
}
vec2 bassline(float t,float l,uint b,float m,float s,int k){
    //pattern:scale(4),tonic(4),mode(4),seed1(8),seed2(12)
    int sc=get4(b,7),to=get4(b,6),mo=get4(b,5);
    float s1=float(get8(b,3))+16.,s2=float(b&4095u)+16.;
    float x=mod(t,l/m);
    int y=scalenote(sc,rnd1(s,s1,s2)*2+mo+k);
    if((s>=1.)&&(y<0)){y=scalenote(sc,rnd1(s-1.,s1,s2)*2+mo+k);x+=l/m;}
    return vec2(x,y+to);
}
vec4 triad(float t,float l,uint b,float m,float s,int k){
    //pattern:scale(4),tonic(4),mode(4),seed1(8),seed2(12)
    int sc=get4(b,7),to=get4(b,6),mo=get4(b,5);
    float s1=float(get8(b,3))+16.,s2=float(b&4095u)+16.;
    float x=mod(t,l/m);
    int y=scalenote(sc,mo+k);
    int z=scalenote(sc,mo+k+2);
    int w=scalenote(sc,mo+k+4);
    y+=rnd1(s,s1,s2)>1?-999:0;
    return vec4(x,y+to,z+to,w+to);
}
int rnd2(float s,float s1,float s2){
    int r=int(mod(abs(sin(floor(s)*s1*s2)*s2+s1),7.)-1.8);return r<0?-999:r;
}
vec2 melody(float t,float l,uint b,float m,float s,int k){
    //pattern:scale(4),tonic(4),mode(4),seed1(8),seed2(12)
    int sc=get4(b,7),to=get4(b,6),mo=get4(b,5);
    float s1=float(get8(b,3))+16.,s2=float(b&4095u)+16.;
    float x=mod(t,l/m);
    int y=scalenote(sc,rnd2(s,s1,s2)*2+mo+k);
    if((s>=1.)&&(y<0)){y=scalenote(sc,rnd2(s-1.,s1,s2)*2+mo+k);x+=l/m;}
    return vec2(x,y+to);
}

//PERFORMER SETUP
bool performerinit(in float t,inout uint b,inout float m,inout float s,inout int a,inout int k){
    //sequence:speed(2),algorithm(2),division(4),pattern1(8),key1(4),pattern2(8),key2(4)
    a=get2(b,14);if(a==0)return false;
    m=pow(2.,float(get2(b,15)-1))*16.;
    s=mod(t*bpm*m/steps,16.);
    int d=int(mod(t*bpm*16./steps,16.));
    k=d<get4(b,6)?get8(b,3):get8(b,0);
    b=d<get4(b,6)?pat[get8(b,4)]:pat[get8(b,1)];
    return true;
}

//PERFORMERS
vec2 playpercussion(float t,float l,uint b){
    float m=0.,s=0.;int a=0,k=0;if(!performerinit(t,b,m,s,a,k))return vec2(0);
    float x=mod(t,l/m);int y=get2(b,int(s));
    if((s>=1.)&&(y==0)){y=get2(b,int(s)-1);x+=l/m;}
    return vec2(x,y);
}
vec2 playbassline(float t,float l,uint b){
    float m=0.,s=0.;int a=0,k=0;if(!performerinit(t,b,m,s,a,k))return vec2(0);
    return bassline(t,l,b,m,s,k);
}
vec4 playchord(float t,float l,uint b){
    float m=0.,s=0.;int a=0,k=0;if(!performerinit(t,b,m,s,a,k))return vec4(0);
    return triad(t,l,b,m,s,k);
}
vec2 playbacking(float t,float l,uint b){
    float m=0.,s=0.;int a=0,k=0;if(!performerinit(t,b,m,s,a,k))return vec2(0);
    return bassline(t,l,b,m,s,k);
}
vec2 playmelody(float t,float l,uint b){
    float m=0.,s=0.;int a=0,k=0;if(!performerinit(t,b,m,s,a,k))return vec2(0);
    return melody(t,l,b,m,s,k);
}

//RENDERER
void render(float t,float l,inout vec2 o){
    float c=0.;     //render target
    vec2 n=vec2(0); //note info x:gate y:note
    vec4 nc=vec4(0); //chord info x:gate yzw:note
    float f=0.;     //freq*gate for melodic osc
    int b=int(t/l); //pattern offset now playing

    //TRACK 1 NES noise snaredrum,closehh,openhh
    n=playpercussion(t,l,seq1[imod(b,16)]);
    c+=(n.y==1.)?nessd(n.x)*.8:0.;
    c+=(n.y==2.)?neshh(n.x,.08)*.4:0.;
    c+=(n.y==3.)?neshh(n.x,.2)*.5:0.;

