    n=playbacking(t,l,seq5[imod(b,16)]);
    c+=(n.y>0.)?c30bell(freq(n.y+72.05)*n.x,n.x)*.3:0.;

    //TRACK 6 N163 melody
    n=playmelody(t,l,seq6[imod(b,16)]);
    c+=(n.y>0.)?c30lead(freq(n.y+48.05)*n.x,n.x)*.6:0.;

    o+=c; //output
}

//MASTER SECTION
#define MASTER_DRIVE    0.5
#define MASTER_PRESSURE 1.1
vec2 mainSound( in int samp,float time){
    float l=steps/bpm,vol=1.;
