//---Clickable UI
// by Catzpaw 2020

//STATE (iChannel0)
vec4 peek(ivec2 a){return texelFetch(iChannel0,a,0);}
void poke(ivec2 a,in vec4 v,inout vec4 f,in ivec2 p){f=(p==a)?v:f;}
bool chktx(ivec2 tx,ivec2 p){return (tx.x==p.x)&&(tx.y==p.y);}
const float txw=3.,txh=1.;
const ivec2 txstat=ivec2(0,0);
const ivec2 txlmo=ivec2(1,0);
const ivec2 txsel=ivec2(2,0);

//BUTTONS
bool chkrect(vec2 c,vec4 r,float d){return((c.x>r.x)&&(c.x<r.x+r.z)&&(c.y>r.y)&&(c.y<r.y+r.w));}
const vec4 b0=vec4(-1.00,-0.50,1.00,1.00);
const vec4 b1=vec4( 0.00,-0.50,1.00,1.00);

//MAIN
void mainImage(out vec4 fragColor,in vec2 fragCoord){

    //FRAME INIT
    if(fragCoord.x>=txw||fragCoord.y>=txh)discard;
    ivec2 fcd=ivec2(fragCoord-.5);
    fragColor=vec4(0);

    //LOAD STATE
    vec4 stat=peek(txstat),lmo=peek(txlmo),sel=peek(txsel);

    //FIRST FRAME
    if(iFrame==0)stat.x=-1.;
    if(stat.x<-.5){stat.x=1.;sel.x=0.;lmo=iMouse;}

    //UI OPERATION
    if(stat.x>.5){
        bool click=(iMouse.z>1e-3)&&(lmo.z<-1e-3);
        if(click){
            vec2 uv=(fragCoord.xy-.5*iResolution.xy)/iResolution.y;
            vec2 mo=(iMouse.xy-.5*iResolution.xy)/iResolution.y;
            //button:prev
            if(chkrect(mo,b0,8.)&&chktx(txsel,fcd)){sel.x=floor(mod(sel.x+7.,8.));}
            //button:next
            if(chkrect(mo,b1,8.)&&chktx(txsel,fcd)){sel.x=floor(mod(sel.x+1.,8.));}
        }
    }

    //STORE STATE
    poke(txstat,stat,fragColor,fcd);
    poke(txlmo,iMouse,fragColor,fcd);
    poke(txsel,sel,fragColor,fcd);
}

