//uncomment this line to give very pixel the same seed on each frame
//#define CONST_SEED



#define Pi 3.14159265359

#define MaxSteps 200
#define MaxBounces 50
#define MaxDist 10.0
#define MinDist .001
#define SPPPerFrame 2

#define LightIndex 1
#define PlaneIndex 2
#define SpheresIndex 3


vec3 CamPos = vec3(0, .75, -3);
vec3 CamRot = vec3(.3, 0., 0.);
float CamFocalLength = 1.;
float CamFocalDistance = 3.;
float CamAperture = .05;
float CamLensDistortion = .8; //positive = barrel, negative = pincushion, zero = none

vec3 LightPos = vec3(0., 1., 0.);
vec3 LightColor = vec3(.7, .5, .3) * 250.;
float LightRadius = .2;

vec3 SpheresColor = vec3(.7, .8, 1.);
float SpheresRadius = .1;
float SpheresIoR = 1.45;

vec3 PlaneColor = vec3(1.);


float seed;

float randomFloat(){
    return fract(sin(seed++)*43758.54536156);
}

vec2 roundTo(vec2 v, vec2 p){
    return p*floor(v/p+.5);
}

vec2 polar2Cartesian(vec2 pos){
    return vec2(cos(pos.x)*pos.y, sin(pos.x)*pos.y);
}

vec2 cartesian2Polar(vec2 pos){
    return vec2(atan(pos.y, pos.x), length(pos));
}

float vornoi(vec3 pos){

    vec2 center = polar2Cartesian(roundTo(cartesian2Polar(pos.xz), vec2(Pi*2./50., .5)));
    return length(vec3(center.x, 0., center.y) - pos);
}

float deSpheres(vec3 pos){
    return vornoi(pos) - SpheresRadius;
}

float dePlane(vec3 pos){
    return pos.y+SpheresRadius+2.*MinDist;
}

float deLight(vec3 pos){
    return length(pos-LightPos)-LightRadius;
}

float distanceEstimation(vec3 pos, out int materialIndex){
    float deS = deSpheres(pos);
    float deP = dePlane(pos);
    float deL = deLight(pos);
    float de = min(min(deS, deP), deL);
    if(deS == de)
        materialIndex = SpheresIndex;
    else if(deP == de)
        materialIndex = PlaneIndex;
    else
        materialIndex = LightIndex;
    return de;
}

float distanceEstimation(vec3 pos){
    int placeHolder;
    return distanceEstimation(pos, placeHolder);
}

mat3 rotationMatrix(vec3 rotEuler){
    float c = cos(rotEuler.x), s = sin(rotEuler.x);
    mat3 rx = mat3(1, 0, 0, 0, c, -s, 0, s, c);
    c = cos(rotEuler.y), s = sin(rotEuler.y);
    mat3 ry = mat3(c, 0, -s, 0, 1, 0, s, 0, c);
    c = cos(rotEuler.z), s = sin(rotEuler.z);
    mat3 rz = mat3(c, -s, 0, s, c, 0, 0, 0, 1);

    return rz * rx * ry;
}


vec3 ortho(vec3 v) {
  return abs(v.x) > abs(v.z) ? vec3(-v.y, v.x, 0.0)  : vec3(0.0, -v.z, v.y);
}

//could've used a simpler approach since it's only used for the plane (with a normal of [0,1,0])
vec3 getCosineWeightedSample(vec3 dir) {
	vec3 o1 = normalize(ortho(dir));
	vec3 o2 = normalize(cross(dir, o1));
	vec2 r = vec2(randomFloat(), randomFloat());
	r.x = r.x * 2.0 * Pi;
	r.y = pow(r.y, .5);
	float oneminus = sqrt(1.0-r.y*r.y);
	return cos(r.x) * oneminus * o1 + sin(r.x) * oneminus * o2 + r.y * dir;
}

//not the most efficient technique but I mean it's only used once per bounce
vec3 normalEstimation(vec3 pos){
  vec3 xDir = vec3(MinDist, 0, 0);
  vec3 yDir = vec3(0, MinDist, 0);
  vec3 zDir = vec3(0, 0, MinDist);
  return normalize(vec3(distanceEstimation(pos + xDir),
	  					distanceEstimation(pos + yDir),
  						distanceEstimation(pos + zDir))
				 - vec3(distanceEstimation(pos)));
}

float fresnel(vec3 dir, vec3 normal, float ior) {
  float cosi = dot(dir, normal);
  float etai = 1.0;
  float etat = ior;
  if (cosi > 0.0) {
    float tmp = etai;
    etai = etat;
    etat = tmp;
  }
  float sint = etai / etat * sqrt(max(0.0, 1.0 - cosi * cosi));
  if (sint >= 1.0) return 1.0;
  float cost = sqrt(max(0.0, 1.0 - sint * sint));
  cosi = abs(cosi);
  float sqrtRs = ((etat * cosi) - (etai * cost)) / ((etat * cosi) + (etai * cost));
  float sqrtRp = ((etai * cosi) - (etat * cost)) / ((etai * cosi) + (etat * cost));
  return (sqrtRs * sqrtRs + sqrtRp * sqrtRp) / 2.0;
}

bool trace(inout vec3 pos, in vec3 dir, in float dSign, out int materialIndex){

    for(int i = 0; i < MaxSteps; i++){
        float dist = distanceEstimation(pos, materialIndex);
        if(dSign*dist > MaxDist) return false;
        if(dSign*dist < MinDist) return true;
        pos += dir*dSign*dist;
    }
    return false;
}

vec3 pathTrace(vec3 pos, vec3 dir){

    vec3 abso = vec3(1.);

    float dSign = sign(distanceEstimation(pos));
    int materialIndex;
    for(int i = 0; i < MaxBounces; i++){
        if(!trace(pos, dir, dSign, materialIndex)) break;
        if(materialIndex == LightIndex) return i==0 ? vec3(0.) : LightColor*abso;
        vec3 normal = normalEstimation(pos);
        float dist = distanceEstimation(pos);
        if(materialIndex == PlaneIndex){
            abso *= PlaneColor;
            dir = getCosineWeightedSample(normal);
        }
        // if you're scared of branching close your eyes for a second
        if(materialIndex == SpheresIndex){
            if(randomFloat() > fresnel(dir, normal, SpheresIoR)){
            	abso *= SpheresColor;
                bool d = dot(dir, normal) < 0.;
                vec3 n = d ? normal : -normal;
                float etai = d ? 1./SpheresIoR : SpheresIoR;
               	dir = refract(dir, n, etai);
            } else {
                dir = reflect(dir, normal);
            }
        }
        dSign = dot(dir, normal) < 0. ? -1. : 1.;
        pos += dSign * 2. * MinDist * normal;
    }
    return vec3(0.);
}

//n-blade aperture
vec2 sampleAperture(int nbBlades, float rotation){

    float alpha = 2.0*Pi / float(nbBlades);
    float side = sin(alpha/2.0);

    int blade = int(randomFloat() * float(nbBlades));

    vec2 tri = vec2(randomFloat(), -randomFloat());
    if(tri.x+tri.y > 0.0) tri = vec2(tri.x-1.0, -1.0-tri.y);
    tri.x *= side;
    tri.y *= sqrt(1.0-side*side);

    float angle = rotation + float(blade)/float(nbBlades) * 2.0 * Pi;

    return vec2(tri.x * cos(angle) + tri.y * sin(angle),
                tri.y * cos(angle) - tri.x * sin(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    #ifdef CONST_SEED
    	seed = mod(iTime, 184.3158915);
    #else
    	seed = mod(fragCoord.x*.6868628+fragCoord.y*.6595975+iTime, 184.3158915);
    #endif


    vec2 uv = (fragCoord+vec2(randomFloat(), randomFloat())-iResolution.xy/2.0) / iResolution.y;


    vec3 focalPoint = vec3(uv * CamFocalDistance / CamFocalLength, CamFocalDistance);
    vec3 aperture = CamAperture * vec3(sampleAperture(6, 0.0), 0.0);
    vec3 rayDir = normalize(focalPoint-aperture);

    rayDir.xy /= pow(rayDir.z, CamLensDistortion);
    rayDir = normalize(rayDir);

    mat3 CamMatrix = rotationMatrix(CamRot);

    rayDir *= CamMatrix;
    aperture *= CamMatrix;

    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    if(iMouse.w > 0.) fragColor.a = 0.;
    for(int i = 0; i < SPPPerFrame; i++){
        fragColor.a++;
        fragColor.rgb = mix(fragColor.rgb, pathTrace(CamPos+aperture,rayDir), 1./fragColor.a);
    }
}

