            : l < O.y  
                ? O.z = O.y , O.y = l 
                : l < O.z  
                    ? O.z = l 
                    : l;
    }
    return 1.-2./(1./(O.y-O.x)+1./(O.z-O.x)); // Formula (c) Fabrice NEYRET - BSD3:mention author.
}


// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: https://developer.nvidia.com/gpugems/GPUGems3/gpugems3_ch01.html
float tex3D( in vec3 p, in vec3 n ){

    n = max(abs(n), 0.001);
    n /= dot(n, vec3(1));
	float tx = effect( p.yz);
    float ty = effect( p.zx);
    float tz = effect( p.xy);
    return (tx*n.x + ty*n.y + tz*n.z);

}
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

#define pmod(p, x) mod(p, x) - x*0.5

#define dmin(a, b) a.x < b.x ? a : b
#define dmax(a, b) a.x > b.x ? a : b
// iq fbm
float noise(in vec3 x) {
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f * f * (3. - 2. * f);
	vec2 uv = (p.xy + vec2(37., 17.) * p.z) + f.xy;
	vec2 rg = textureLod(iChannel0, (uv + .5) / 256., 0.).yx;
	return -1. + 2.4 * mix(rg.x, rg.y, f.z);
}

// Fbm
float fbm(vec3 p) {
	return noise(p * .06125) * .5 + noise(p * .125) * .25 + noise(p * .25) * .125;
}
#define modDist 7.
vec2 map(vec3 p){
	vec2 d = vec2(10e10);
    vec3 q = p;
	p.xz = pmod(p.xz, modDist);

    vec3 N = normalize(p);

    p.xz += (0.1 + sin(iTime*3. + p.y)*0.1)*N.xz * smoothstep(0.35,1.,exp(-abs(q.y*0.25)))*exp(abs(p.y)); 
    // ^ just some function graphing
    for(float i = 0.; i < 6.; i++){
    	p = abs(p);
    	p.y -= 2.4;
        p.xy *= rot(3.4 - exp(-i*5. )*0.14);
        #if VARIANT==2
        if(i < 5.){
        	p.z -= 1.2;
        }
        #else 
        	p.z -= 1.1;

        #endif
        p.y -= 1.35;
        p.xz *= rot(0.7);
    }

    // ^ IFS
    N = normalize(p);

    float dS = length(p) - 1.;

    float trabA = tex3D(p*5., N); 
    float trabB = tex3D(p*10., N);

    d = dmin(d, vec2(dS, 1.));
    d = dmax(d, vec2(-(dS - 0.5), 1.));
    d -= trabA*0.34;

    d = dmin(d, vec2(dS, 2.));
    //d = dmin(d, vec2(q.y + 6. - trabA*0.8, 1.));
    //d.x = length(p) - 1.+ fbm(p*10.)*0.1;
    //return d;
    d.x -= fbm(q*9.)*0.1;

    //d.x -= 0.1 + sin(iTime + p.y)*0.1;
    d.x *= 0.8;
    return d;
}
vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
	vec3 dir = normalize(lookAt - ro);
	vec3 right = normalize(cross(vec3(0,1,0), dir));
	vec3 up = normalize(cross( dir, right));
    return dir + uv.x*right + uv.y*up;
}

vec3 getNormal(vec3 p){
	vec2 t = vec2(0.001,0);
    return normalize(
    	map(p).x - vec3(
        	map(p - t.xyy).x,
        	map(p - t.yxy).x,
        	map(p - t.yyx).x
        )
    );
}
// cool sin - does nothing
float csin(float i, float p){
    return sin(i);
    p = 1.;
	float ni = mod(i/(2.*PI), 1.);
    ni = pow(ni, p);
}

vec3 glow = vec3(0);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(-0.,-0.,-0.);
    ro.y += sin(iTime*0.24 + sin(iTime*0.5)*0.8)*1.;
    //ro.x += modDist * 0.5;
    float T = iTime*0.4;
    ro.z += 1.7;
    ro.z += T;
    ro.x += csin( 1.*(T/modDist)*(PI) + PI, 1. )*modDist*2.;
    ro.x += csin( (0.5*T/modDist)*(PI), 1. )*modDist/4.;
    vec3 lookAt = ro + vec3(0.,0,5);
    lookAt.xz -= csin((T/modDist)*PI + csin(iTime, 1.)*0.25 + 0.5, 1. )*modDist*0.2;

    vec3 rd = getRd(ro, lookAt, uv);



	// local density/distance
	float ldensity = 0.;

	// accumulation color & density
	vec3 bg = vec3(.16, .12, .22)*0.6;
	vec4 sum = vec4(bg, 0.);

   	// total color
   	vec3 tc = vec3(0.);

    vec2 d = vec2(10e9);
    vec3 p = ro; float t = 0.; vec3 N;
    #define iters 80
    for (int i = 0; i <= iters; i++){
        if((d.x<=0.0001*t) || (t > 100.) || (sum.a > 1.) ){
		   break;
        }

    	d = map(p);

        d.x *= 0.7;
        glow += exp(-d.x*1.)*vec3(1.);
        //glow += d.x;

		// compute local density and weighting factor 
		const float h = .06;
		ldensity = (h - d.x) * step(d.x, h);
		vec4 c;

		#define pal(t,x,y,i,z) (t + x*sin(y*i + z))
		c.rgb = pal(0.5, 2.5, vec3(0.6,0.1,0.2) , 0.5- d.y,7.5 );
        //c.rgb =vec3(1.);
        //col.rgb = clamp(col.rgb, 0., 1.);
		c.a = ldensity;
        c.rgb *= c.a;
		sum += (1. - sum.a) * c;

		// from duke/las
		sum.a += .026;

      	d.x = max(d.x, 0.02);

        t += d.x;
    	p = ro + rd*t;
    }



    N = getNormal(p);
    vec3 L = normalize(vec3(1));
    vec3 H = normalize(L - rd);
    float diff = max(dot(N, L), 0.);
    float fres = pow(1. - max(dot(N, -rd), 0.), 5.);
    float spec = pow(max(dot(N, H),0.), 5.); 

    col += sum.xyz*1.;

    float s = (1. - sum.w);
    col -= N*s*0.7;
    col -= glow*0.02*pal(0.6,0.8,vec3(0.7,0.5,0.8),0.8,0.6)*0.09;

    //col *= 0.9;
    col = pow(col, mix(vec3(1),vec3(0.45), smoothstep(0., 1., t*0.03)));



    uv = fragCoord/R;
    uv -= 0.5;
    col *= 1. - mix(0.,1., pow(smoothstep(0.,1.,length(uv))*0.3, 0.6));

    fragColor = vec4(col,1.0);
}

