// Approximating pow(x,2.2), the usual gamma-to-linear
// mapping, through (2-1 - xln2)/(1-ln2), resulting
// in a 2x speed up (in my GPU)
//
// In blue, pow(x,2.2)
// In yellow, (2-1 - xln2)/(1-ln2)
//
// Note that GPUs implement exp2m1(x) = 2-1 in hardware,
// so this _should_ translate to EXP2M1, MAD, MUL, while
// regular pow(x,2.2) does in principle involve a log and
// an exponential.

// Set to 1 to measure performance, then go to line 63 and 64
#define DISPLAY_OR_MEASURE 0

float exp2m1( in float x ) { return exp2(x)-1.0; }

// Different approximations to pow(x,2.2)
float my_pow_22( float x )
{
	// By iq, f(x) = (2-1 - xln2)/(1-ln2) 
    return (exp2m1(x)-x*0.693147)*3.258891;

    // Same idea (one extra mul), but better match. By Marc B.Reynolds
    // return (exp2m1(0.718151*x)-0.503456*x)*7.07342;

    // By BigWIngs (see comments), probably best tradeof perf and accuracy
    // return x*x*(0.75 + 0.25*x);

    // By iq (see comments)
    // return x*(1.12*x - 0.12);

    // By Marc B.Reynolds (http://marc-b-reynolds.github.io/math/2019/12/10/GammaRamp.html)
    // return (0.5*x*x)*(sqrt(x)+1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
#if DISPLAY_OR_MEASURE==0
    float dx = 1.0/iResolution.y;
    vec2  p = ( vec2((iResolution.y-iResolution.x)/2.0,0.0)+fragCoord)/iResolution.y;

    vec3 col = vec3(0.0);
    if( p.x>0.0 && p.x<1.0 )
    {
        col = vec3( 0.2 + 0.02*mod(floor(p.x*10.0)+floor(p.y*10.0),2.0) );
        // yellow: pow(x,2.2), usual gamma-to-linear
        {
        float y = pow( p.x, 2.2 );
        float dy = pow(p.x-dx,2.2) - y;
        float di = abs(p.y-y)/sqrt(dx*dx+dy*dy);
        col = mix( col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 1.5, di ) );
        }
        // blue: approximation
        {
        float y = my_pow_22( p.x );
        float dy = my_pow_22(p.x-dx) - y;
        float di = abs(p.y-y)/sqrt(dx*dx+dy*dy);
        col = mix( col, vec3(0.0,0.4,0.8), 1.0-smoothstep( 0.0, 2.0, di ) );
        }
    }
#else    
    // measure performance
    float x = fragCoord.x/iResolution.x + min(iTime,0.1);

    vec3 col = vec3(0.0);

    float f = 0.0;
    uint num = 100000u+min(uint(iFrame),0u);
    for( uint i=0u; i<num; i++ )
    {
        float g = x+f*0.0000001;
      //f += pow(g, 2.2);  // gamma curve
        f += my_pow_22(g); // approximation
    }
    col.x = f/float(num);
#endif
    fragColor = vec4( col, 1.0 );
}

