vec2 hash(float n) { return fract(sin(vec2(n,n*7.))*43458.528); }

// https://iquilezles.org/articles/distfunctions2d
float sdLine( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdSphere( in vec2 p, float r ){
    return length(p)-r;
}

mat2 rotate(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s,c,-c,s);
}

float sdCharacter(vec2 uv,int charNum){
    float dist = 1e5;
    switch(charNum){
        case 1:
            // 'A'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.0,0.4),vec2(0.3,-0.4)));
            dist = min(dist ,sdLine(uv,vec2(0.0,0.0),vec2(0.15, 0.0)));
            break;
        case 2:
            // 'B'
            uv.y = abs(uv.y);

            uv.x += 0.1;
            uv.x *= 0.6;
            dist = min(dist ,abs(sdSphere(uv-vec2(0.0,0.2),0.2)));
            dist = max(dist ,-sdSphere(uv-vec2(-3.00,0.0),2.95));
            dist = min(dist ,sdLine(uv,vec2(-0.05,0.4),vec2(-0.05,-0.4)));
            break;
        case 3:
            // 'C'
            uv.x *= 1.2;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            dist = max(dist ,-sdSphere(uv*vec2(1.0,2.0)-vec2(1.05,0.0),0.85));
            break;
        case 4:
            // 'D'
            uv.x += 0.1;
            dist = min(dist ,abs(sdSphere(uv,0.40)));
            dist = min(dist ,sdLine(uv,vec2(-0.1,0.4),vec2(-0.1,-0.4)));
            dist = max(dist ,-sdSphere(uv-vec2(-1.9,0.0),1.80));
            break;
        case 5:
            // 'E'
            uv.y = abs(uv.y);
            dist = min(dist ,sdLine(uv,vec2(-0.2,0.35),vec2(0.2,0.35)));
            dist = min(dist ,sdLine(uv,vec2(-0.2,0.0),vec2(0.2,0.0)));
            dist = min(dist ,sdLine(uv,vec2(-0.2,0.0),vec2(-0.2,0.35)));
            break;
        case 6:
            // 'F'
            dist = min(dist ,sdLine(uv,vec2(-0.2,0.35),vec2(0.2,0.35)));
            dist = min(dist ,sdLine(uv,vec2(-0.2,0.0),vec2(0.15,0.0)));
            dist = min(dist ,sdLine(uv,vec2(-0.2,-0.35),vec2(-0.2,0.35)));
            break;
        case 7:
            // 'G'
            uv.x *= 1.2;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            dist = max(dist ,-sdSphere(uv*vec2(1.0,2.0)-vec2(1.05,0.0),0.85));
            dist = min(dist ,sdLine(uv,vec2(0.05,-0.1),vec2(0.35,-0.1)));
            dist = min(dist ,sdLine(uv,vec2(0.32,-0.1),vec2(0.32,-0.35)));
            break;
        case 8:
            // 'H'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.2,0.35),vec2(0.2,-0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.0,0.00),vec2(0.2, 0.00)));
            break;
        case 9:
            // 'I'
            uv.y = abs(uv.y);
            dist = min(dist ,sdLine(uv,vec2(0.12,0.35),vec2(-0.12,0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.00,0.00),vec2( 0.00,0.35)));
            break;
        case 10:
            // 'J'
            uv.x *= 2.05;
            uv.x += 0.02;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            dist = max(dist ,-sdSphere(uv-vec2(0.00,1.0),1.25));
            dist = min(dist ,sdLine(uv,vec2(0.32,0.35),vec2(0.32,-0.2)));
            break;
        case 11:
            // 'K'
            uv.x -= 0.02;
            uv.y = abs(uv.y);
            dist = min(dist ,sdLine(uv,vec2(-0.2,-0.35),vec2(-0.2,0.35)));
            dist = min(dist ,sdLine(uv,vec2(-0.2, 0.00),vec2( 0.18,0.35)));
            break;
        case 12:
            // 'L'
            dist = min(dist ,sdLine(uv,vec2(-0.2,-0.35),vec2(-0.2,0.35)));
            dist = min(dist ,sdLine(uv,vec2(-0.2,-0.35),vec2( 0.2,-0.35)));
            break;
        case 13:
            // 'M'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.00,-0.25),vec2(0.25,0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.25,-0.35),vec2(0.25,0.35)));
            break;
        case 14:
            // 'N'
            dist = min(dist ,sdLine(uv,vec2(-0.25,0.35),vec2(0.25,-0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.25,-0.35),vec2(0.25,0.35)));
            dist = min(dist ,sdLine(uv,vec2(-0.25,-0.35),vec2(-0.25,0.35)));
            break;
        case 15:
            // 'O'
            uv.x *= 1.2;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            break;
        case 16:
            // 'P'
            uv.x += 0.1;
            uv.x *= 0.6;
            dist = min(dist ,abs(sdSphere(uv-vec2(0.0,0.2),0.2)));
            dist = max(dist ,-sdSphere(uv-vec2(-3.00,0.0),2.95));
            dist = min(dist ,sdLine(uv,vec2(-0.05,0.4),vec2(-0.05,-0.4)));   
            break;
        case 17:
            // 'Q'
            uv.x *= 1.2;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            dist = min(dist ,sdLine(uv,vec2(0.35,-0.35),vec2(0.10,-0.15)));
            break;
        case 18:
            // 'R'
            uv.x += 0.1;
            uv.x *= 0.6;
            dist = min(dist ,abs(sdSphere(uv-vec2(0.0,0.2),0.2)));
            dist = max(dist ,-sdSphere(uv-vec2(-3.00,0.0),2.95));
            dist = min(dist ,sdLine(uv,vec2(-0.05,0.4),vec2(-0.05,-0.4))); 
            dist = min(dist ,sdLine(uv,vec2(0.02,0.0),vec2(0.20,-0.38)));
            break;
        case 19:
            // 'S'
            vec2 t = uv;
            float m1 = 1e5;
            float m2 = 1e5;
            uv -= vec2(0.0,0.2);
            m1 = min(m1 ,abs(sdSphere(uv-vec2(0.02,0.0),0.2)));
            uv *= rotate(2.0);
            m1 = max(m1 ,-sdSphere(uv*vec2(1.0,0.4)-vec2(0.2,0.0),0.17));        
            m2 = min(m2 ,abs(sdSphere(t-vec2(-0.02,0.0)-vec2(0.0,-0.2),0.2)));
            t  *= rotate(2.0);
            m2 = max(m2 ,-sdSphere(t*vec2(1.0,0.4)-vec2(-0.2,0.0),0.17));
            dist = min(m1,m2);
            break;
        case 20:
            // 'T'
            dist = min(dist ,sdLine(uv,vec2(0.25,0.35),vec2(-0.25,0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.00,-0.35),vec2( 0.00,0.35)));
            break;
        case 21:
            // 'U'
            uv.x = abs(uv.x);
            uv.x += 0.08;
            dist = min(dist ,abs(sdSphere(uv,0.38)));
            dist = max(dist ,-sdSphere(uv-vec2(0.00,1.),1.25));
            dist = min(dist ,sdLine(uv,vec2(0.32,0.35),vec2(0.32,-0.2)));
            break;
        case 22:
            // 'V'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.00,-0.35),vec2(0.3,0.35)));
            break;
        case 23:
            // 'W'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.0,0.25),vec2(0.15,-0.30)));
            dist = min(dist ,sdLine(uv,vec2(0.30,0.30),vec2(0.15,-0.30)));
            break;
        case 24:
            // 'X'
            uv = abs(uv);
            dist = min(dist ,sdLine(uv,vec2(0.0,0.0),vec2(0.25,0.35)));
            break;
        case 25:
            // 'Y'
            uv.x = abs(uv.x);
            dist = min(dist ,sdLine(uv,vec2(0.0,0.05),vec2(0.25,0.35)));
            dist = min(dist ,sdLine(uv,vec2(0.0,0.05),vec2(0.00,-0.35)));
            break;
        case 26:
            // 'Z'
            dist = min(dist ,sdLine(uv,vec2(-0.25,0.30),vec2(0.25,0.30)));
            dist = min(dist ,sdLine(uv,vec2(-0.25,-0.30),vec2(0.25,0.30)));
            dist = min(dist ,sdLine(uv,vec2(-0.25,-0.30),vec2(0.25,-0.30)));
            break;

    }
    return dist;
}

