vec4 Particle(float i) { return texelFetch(iChannel0, ivec2(int(i),0),0);}

float map(vec2 uv,Word word){
    float dist = 1e5;
    uv *= 3.8;
    uv.x += CHAR_WIDTH*(float(word.len)-1.0)/2.0;
    for(int i=0;i<word.len;i++){
    	dist = min(dist ,sdCharacter(uv,word.charsIndex[i]));
        uv.x -= CHAR_WIDTH;
    }
    return dist;             
}

vec2 normal(vec2 uv,Word wd){
    vec2 p = vec2(1e-4,0.0);
	return normalize(vec2(map(uv,wd)-map(uv-p.xy,wd),
                          map(uv,wd)-map(uv-p.yx,wd)));
}

vec2 sdfForce(vec2 p,Word wd){
	return 256.0*normal(p.xy,wd)/(map(p.xy,wd)+100.0);
}

void mainImage( out vec4 Output, in vec2 uv )
{
    vec4 res;
    float id = floor(uv.x);
    if(iFrame < 5){
    	// initialize        
        float k = iResolution.x/iResolution.y;
        res = vec4(1.0*hash(id*2.723+8.213)-0.5,0.0,0.0);
        res.x *= k;
    }
    else{
        float d;
        vec4 p = Particle(id);
        vec2 sumF;

        // define the word to display 
        int charsIndex[12];
        charsIndex[0] = 19;
        charsIndex[1] = 8;
        charsIndex[2] = 1;
        charsIndex[3] = 4;
        charsIndex[4] = 5;
        charsIndex[5] = 18;
        charsIndex[6] = 20;
        charsIndex[7] = 15;
        charsIndex[8] = 25;
        Word wd = Word(9,charsIndex);

        /*
        int talon[12];
        talon[0] = 20;
        talon[1] = 1 ;
        talon[2] = 12;
        talon[3] = 15;
        talon[4] = 14;
        Word td = Word(5,talon);
		*/

        // test random force
        sumF -= (6.0*hash(iTime+id*0.1232+0.3532)-3.0);

      	#ifdef INTERACTIVE
        if(iMouse.z>0.5)
        {
            vec2 mousePos = iMouse.xy/iResolution.xy-0.5;
            mousePos.x *= iResolution.x/iResolution.y;
            vec2 diff = p.xy-mousePos;
            float l = length(diff)+0.001;
            sumF += diff/l/l/l*0.05;
        }
        #endif

        // move according to sdf
        sumF -= sdfForce(p.xy,wd);

    	// Friction    
        sumF -= p.zw*RESIST/dt;

        // update position and velocity
        p.zw += (sumF * dt);
        p.xy += (p.zw * dt + sumF * dt * dt);
        res = p;
    }
    // x,y : position 
    // z,w : velocity
    Output = res;
}

