//saving and diffusing the velocity trails 

float sdtrail(vec2 p)
{
    float de = 1e10;
    for(float x = -2.; x <= 2.; x+=1.)
        for(float y = -2.; y <= 2.; y+=1.)
        {
            vec4 particle = texel(ch0, p+vec2(x,y));
   		    vec2 pvel = pspeed*vec2(cos(particle.z), sin(particle.z));
            de = min(de, sdLine(p, particle.xy, particle.xy - dt*pvel));
        }

    return de;
}

float trail(vec2 p)
{
    return gauss(sdtrail(p), prad);
}

void mainImage( out vec4 Q, in vec2 p )
{
    Q = texel(ch1, p);

    //diffusion equation
    Q += 0.1*dt*Laplace(ch1, p);

    vec4 particle = texel(ch0, p);

    vec2 pvel = pspeed*vec2(cos(particle.z), sin(particle.z));
    float distr = exp(-0.02*particle.w) * trail(p);



    //velocity depositing
    Q += dt*vec4(1., pvel.x, pvel.y, 1.)*distr;

    //decay
    Q += -dt*decay*Q;

    if(iFrame < 1) Q = vec4(0);
}

