#define outerW 17.



vec2 sdMain(vec3 p){
	vec2 d = vec2(10e10);

    float repDistY = 8.;
    float qRepDistY = 30.;
    float idY = floor(p.y/repDistY);
    float rA = r11(idY);
    float rB = r11(idY + 0.2);
    float rC = r11(idY + 0.6);    

    float repsXZ = 2. + floor(rA*1.99);

    vec3 q = p;
    q.y = pmod(q.y, qRepDistY);

    p.xz *= rot(rA*PI*30.);


    q = abs(q);
    q.x -= outerW;
    vec3 v = q;
    vec3 s = vec3(0.9, 10., 1.6);
    // WallBox

    q.x += 0.5;

    d = dmin(d, sdBox(q, s), 5.);
    q = abs(q);
    for(int i = 0; i < 4; i++){
    	q = abs(q);
    	q.xz *= rot(0.25*PI);
    }
    q.xy *= rot(0.1*PI);    
    q.x += 1.4;
    // WallBox
    d = dmax(d, -sdBox(q, s*0.9), 1.);

    d = dmin(d, sdBox(v, s*0.9), 2.);

    vec3 polarQ = vec3(
        length(q),
    	atan(q.y,q.x),
        acos(p.z/length(p))
    );

    // Outer
    d = dmin(d, -(length(p.xz) - outerW) - T1(vec2(p.y*.5, 5.*atan(p.z, p.x)/tau)).x*7.95, 1.)  ;

    // Center
    d = dmin(d,   max(abs(p.x)-1.,abs(p.z) -1.), 3.);
    //d.x = min(d.x, length(p.xz) - 1.);

    vec3 n = p;
    float pId = floor(3.*atan(p.x,p.z)/tau);
    pModPolar(p.xz, repsXZ);
    pModPolar(n.xz, repsXZ);
    p.y = pmod(p.y, repDistY);

    p.xz *= rot(0.5*PI);

    // Lines
    p.y -= repDistY*0.5;
    p.y += (1. + 3.*sin(pId*5214.))*sin((length(p.xz)/outerW)*PI);
    d = dmin(d, length(p.yx) - 0.1, 4.) ;

    // Pipes
    q.y -= 9.6;
    q.x += 19.5;

    q.xz *= rot(0.2);
    //q.y -= 2.*sin((length(q.xz)/outerW)*PI);
    d = dmin(d, length(q.yx) - 0.5, 6.) ;

    d.x *= 0.74;

    return d;
}

