Object trace(vec3 ro, vec3 rd) {
    vec3 p = ro + rd*0.2;
    float t = 0.;
    for (int i = 0; i < MARCH_ITERATIONS; i++) {
    	Object obj = map(p);

        if (obj.d < 0.001) {
            obj.d = t;
        	return obj;
            break;
        }

        if (t > 300.) {
            obj.didHit = false;
            return obj;
        	break;
        }

        t += obj.d;
        p = ro + rd*t;
    }


}

vec3 getNormal(vec3 p) {
	vec2 t = vec2(0.001,0.);    
    return normalize(vec3(
    	map(p + t.xyy).d - map(p - t.xyy).d,
    	map(p + t.yxy).d - map(p - t.yxy).d,
    	map(p + t.yyx).d - map(p - t.yyx).d
    ));
}

