image_code:
#define PI 3.141592
#define MAX_STEPS 150
#define MAX_DIST 100.0
#define SURF_DIST .01

#define GROUND 0
#define SKY 1
#define SPHERE 2
#define SHELL 3
#define BOX 4

#define SKY_COLOR vec3(0.1, 0.1, 0.15)

struct Obj {
	int type;
    float d;
    vec3 hitPos;
};

float rand(vec2 uv) {
    return fract(sin(dot(uv.xy, vec2(12.9898,78.233)))*
        43758.5453123);
}

float rand2(float x){
    return fract(sin(x)*43758.5453123);
}

mat2 rotation(float angle) {
	float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    return length(max(abs(p)-s, 0.0));
}

float sdSphere(vec3 p, vec4 s) {
    return length(p-s.xyz)-s.w;
}

// Value noise
// from the book of shader
float noise(in vec2 uv) {

    vec2 i = floor(uv);
    vec2 f = fract(uv);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth
    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

/*
 * Fractal Brownian Motion adds different iterations of noise (octaves) 
 * by incrementing the frequencies in regular steps and decrease the amplitude 
 * of the noise. It makes the noise more granular and add more fine details.
*/
float fbm(in vec2 uv, int octaves) {

    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;

    // Loop of octaves
    for (int i = 0; i < octaves; i++) {
        value += amplitude * noise(uv);
        uv *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

Obj getDist(vec3 p) {	

    // distance to the ground
    float height = fbm(vec2(p.x, p.z), 7); 
    float planeDist = p.y - height;

    // distance to the surface of sphere
    vec4 s = vec4(4, 1., 4, 0.8);
    float sphereDist =  sdSphere(p, s);

     // distance to the surface of shell
    vec3 plane = vec3(2, 2, 1.5);
    plane.xz *= rotation(2.45);

    float slice = dot(p, normalize(plane));   
    vec4 s2 = vec4(0.8, 0.93, -2.6, 0.6);

    float shellDist = sdSphere(p, s2);
    shellDist = max(abs(shellDist)-.03, slice);

    // distance to the surface of the twisted box
    vec3 bp = p-vec3(-3,1,1);
    bp.xz *= rotation(bp.y*2.6);

    float boxDist = sdBox(bp, vec3(0.2, 3.0, 0.2)) * 0.4;

    Obj obj = Obj(GROUND, planeDist, p);

    if (sphereDist < obj.d) {
    	obj = Obj(SPHERE, sphereDist, p);
    }

    if (shellDist < obj.d) {
    	obj = Obj(SHELL, shellDist, p);
    }

    if (boxDist < obj.d) {
    	obj = Obj(BOX, boxDist, p);
    }

    return obj;
}

Obj rayMarch(vec3 ro, vec3 rd) {

    float t = 0.0;

    Obj hitObj = Obj(SKY, t, ro + rd);
    for (int i = 0; i < MAX_STEPS; i++) {

        vec3 p = ro + rd*t;
        hitObj = getDist(p);      
        t += hitObj.d;

        // the ray has marched far enough but hit nothing. 
        // Render the pixel as a part of the sky.
        if (t > MAX_DIST) {
        	hitObj = Obj(SKY, t, p);
            break;
        }

        // the ray has marched close enough to an object
        if (abs(hitObj.d) < SURF_DIST) {
            hitObj = Obj(hitObj.type, t, p);
        	break;
        }

        hitObj.d = t;
    }

    return hitObj;
}

vec3 getNormal(vec3 p) 
{
	float d = getDist(p).d;

    // use offset samples to compute normal
    vec2 e = vec2(0.01, 0);    
    vec3 n = d - vec3(
        getDist(p-e.xyy).d,
        getDist(p-e.yxy).d,
        getDist(p-e.yyx).d);

    return normalize(n);
}

float getLight(vec3 p, vec3 lightPos) {

    // compute the light vector and its normal   
    lightPos.xz += vec2(sin(iTime*0.5), cos(iTime*0.5))*1.4;
    vec3 l = normalize(lightPos-p);
    vec3 n = getNormal(p);

    // calculate diffuse lightning
    // dot product gives values between -1 to 1 hence the range
    // is clamped to 0 to 1
    float dif = clamp(dot(n, l), 0.0, 1.0);

    // Add shadow
    // raymarch to the direction of light. If the ray hits something that's
    // closer than the light there's an object between and shadow can be added
    float d = rayMarch(p+n*SURF_DIST*2.0, l).d;

    if(d < length(lightPos-p)) {
        dif *= 0.1;
    }

    return dif;
}

// From the book Texturing & Modeling: A Procedural Approach
vec3 star(vec2 pos, float npoints, float rmin, float rmax)
{
    // calculates how wide the angle between two points of the star is
    float starAngle = 2.0*PI/npoints;

    // polar coordinates of the given point
    float angle = atan(pos.x, pos.y) + PI;
	float radius = length(pos);

    // transforms the coordinates into new feature space in which
    // the range between two points is set to 0 to 1. If the point lies 
    // in the upper half of the "V" shape the point is reflected to the
    // lower half through the center line. This is done to avoid checking 
    // both edges and is possible due to symmetry.
    angle = mod(angle, starAngle) / starAngle;
    if (angle >= 0.5) {
		angle = 1.0 - angle;
    }

    // the tip of a point of the star
    vec3 p0 = vec3(rmax, 0, 0);
    // the point lying in the middle of the two points at radius rmin
    // from the center
	vec3 p1 = rmin * vec3(cos(starAngle/2.0) - 0.45, sin(starAngle/2.0) - 0.45, 0.0);

    // vector from the tip of the star point to the point at rmin radius
    // from the centre 
    vec3 d0 = p1 - p0; 
    // vector from the the tip to the current position
    vec3 d1 = radius * vec3(cos(angle), sin(angle), 0) - p0;

    // cross product will point along the positive z-axis or along the negative
    // z-axis. The direction is determined by whether the first vector is 
    // to the left or to the right of the second vector. Hence, the direction
    // of the cross product tells which side of the edge d0 and if it's inside
    // the star or not.
    vec3 direction = cross(d0, d1);

	float in_out = smoothstep(0.0, 0.03, direction.z);
	vec3 bgColor = vec3(0.0);    

    vec3 col = mix(bgColor, vec3(1.0), in_out / length(pos));

    return col;
}

// Analytically box-filtered checkerboard by iq
float checkersTextureGradBox(in vec2 p, in vec2 ddx, in vec2 ddy) {

    // filter kernel
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;

    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)/2.0)-0.5)-abs(fract((p+0.5*w)/2.0)-0.5))/w;

    // xor pattern
    return 0.5 - 0.5*i.x*i.y;
}

vec2 texCoords(in vec3 pos, in vec4 sc0) {
    vec3 q = normalize(pos - sc0.xyz);
    vec2 matuv = vec2(atan(q.x,q.z), acos(q.y )) * sc0.w;

    return 2.0*matuv;
}

vec3 fog(vec3 color, float d, vec3 camDir, vec3 sunDir, float extintion, float inscattering) {

    float sunAmount = max(dot(camDir, sunDir), 0.0);
    vec3 fogColor = mix(vec3(0., 0., 0.), vec3(1.0, 0.9, 0.7), pow(sunAmount, 8.0));
    return (color * exp(-d * extintion)) + (fogColor * (1.0 - exp(-d * inscattering)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // normalize coordinates ranging from -0.5 to 0.5 and fix aspect ratio
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

    vec3 col = SKY_COLOR;

    // camera
    vec3 ro = vec3(.5, 2, -8);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    Obj hitObj = rayMarch(ro, rd);

    vec3 LIGHT_DIR = vec3(-1, 4, -6);

    if (hitObj.type == SKY) {

        // add one pixel wide star
        float twinkle = dot(length(sin(uv + iTime*0.05)), 
                            length(cos(uv*vec2(22.67)-iTime*0.05)));
        twinkle = sin(twinkle*10.0)*0.5+0.5;
        col += vec3(pow(rand(uv), 780.0)) * twinkle;

        // add the stars with points    
        vec3 starColor = vec3(1.0000,0.5161, 0.0000);
		vec3 bgColor = vec3(0.0);

    	float rmin = 15.36 / iResolution.x*1.4;
    	float rmax = 0.12;

		col += star(uv-vec2(0.4, 0.3), 4.0, rmin, rmax);
        col += star(uv-vec2(0.7, 0.4), 4.0, rmin, rmax);
        col += star(uv-vec2(0.6, 0.2), 4.0, rmin, rmax);
        col += star(uv+vec2(0.2, -0.25), 4.0, rmin, rmax);
        col += star(uv+vec2(0.15, -0.4), 4.0, rmin, rmax);
        col += star(uv+vec2(0.66, -0.11), 4.0, rmin, rmax);
        col += star(uv+vec2(0.06, -0.11), 4.0, rmin, rmax);
        col += star(uv+vec2(0.7, -0.35), 4.0, rmin, rmax);

        // add moon

        // first, change the background to solid color
        float moon = step(0.01, length(uv-vec2(0.20,0.25))-0.09);
        col *= moon;
        col += (1.0-moon) * SKY_COLOR;


        // add two circles almost on top of each other
        moon = smoothstep(0.001, -0.009, length(uv-vec2(0.20,0.25))-0.09);
        col += vec3(moon);      

        moon = smoothstep(-0.01, 0.06, length(uv-vec2(0.26, 0.26))-0.095);
        col *= moon;
        col += (1.0-moon) * SKY_COLOR;


        // add mountains
        uv *= 10.0;
    	float i = floor(uv.x);
  	    float f = fract(uv.x);

    	float s = rand2(uv.x);

    	float pct = smoothstep(-0.01, 0.01, s-uv.y);

    	pct = mix(rand2(i), rand2(i +1.0), smoothstep(0., 1.0, f))*1.;

        float mountain = smoothstep(-0.01, 0.01, pct-uv.y);
    	col *= (1.0-mountain);

        vec3 colb = vec3(0.02);
        vec3 colt = vec3(0.07);

        vec3 mountain_col = mix(colb, colt, uv.y);
        col += mountain*mountain_col;

    } else if (hitObj.type == SPHERE || 
               hitObj.type == BOX ||
               hitObj.type == SHELL) {

    	vec4 sc0 = vec4(5.5, 1.5, 10.5, 11.5);

        if (hitObj.type == SHELL) {
        	sc0 = vec4(.5, 1.5, 1.5, 11.5);
        }

        // add checkerboard texture
      	vec2 uvw = texCoords(hitObj.hitPos, sc0) - uv;

      	vec2 ddx_uv = dFdx( uvw );
      	vec2 ddy_uv = dFdy( uvw );

