vec4 fbmd( in vec3 x )
{
      float scale  = 1.5+3.*sin(iTime/100.);

    float a = 0.0;
    float b = 0.5;
	float f = 1.0;
    vec3  d = vec3(0.0);
    for( int i=0; i<3; i++ )
    {
        vec4 n = noised(f*x*scale)*noised(f*x*scale).x;
        a += b*n.x;           // accumulate values		
        d += b*n.yzw*f*scale; // accumulate derivatives
        b *= 0.5;             // amplitude decrease
        f *= 2.8;             // frequency increase
    }

	return vec4( a, d );
}



