vec3 GetNormal( vec3 pos, float sampleSpacing )
{
    vec2 d = vec2(-1,1)*sampleSpacing;

    return normalize(
        map( pos + d.xxx ).x*d.xxx +
        map( pos + d.yyx ).x*d.yyx +
        map( pos + d.yxy ).x*d.yxy +
        map( pos + d.xyy ).x*d.xyy
        );
}


vec2 GlowMarch(vec3 p, vec3 o)
{
	float d		= 1000.0;
	float z		= 0.0;
	float acc	= 0.0;

	for(int i=0; i<100; i++)
	{
	     	d= map(p + o*z).x;

		acc	+= max(3. - d,- 0.);
            z	+= max(0.1, d)*d/100.;
	}

	return vec2(acc/1560., z);
}

vec2 Trace( vec3 start, vec3 dir, float minT, float maxT, float pixelScale )
{
    float epsilon = pixelScale*1.;

    float t = minT;
    float m ;
    float dist;
    for ( int i=0; i < 500; i++ ) // this can be really big as long as epsilon is high enough
    {
        dist = map( start + t*dir ).x;
         m =map( start + t*dir ).y;;
        if ( abs(dist) < epsilon*t || t > maxT ) return vec2(t,m);
        t += dist;
    }

    if ( dist >0.1 && t < maxT )
    {
        t += dist;
        float dist2 = map( start + t*dir ).x;

        // try to guess where the surface might be, by extrapolating as if SDF is planar here
        t += max(0.,dist2*dist/(dist-dist2)); //(dist-dist2)/dist = change in df / distance stepped, so divide DF by this to find distance we should step

        return vec2 (t,m);
    }

    return vec2(maxT,m); // todo: maybe return best-guess t when we run out of iterations?
}


