#define PI  3.14159265359
#define PI2 6.28318530718

#define COLOR1 vec3(0.9, 0.9, 0.8)
#define COLOR2 vec3(0.05, 0.05, 0.05)
#define LIGHTCOLOR  vec3(1., 1., 1.)

#define ARMS 10.
#define DENSITY 3.
#define ANGLE_SPEED 3.
#define ANGLE_TEMPO 2.
#define SPEED 2.
#define PULSE_SPEED 4.
#define SMOOTH 0.04

float smoothstepCheckerboard(in vec2 uv) {
    // Shift, as fract gives 0..1 and we will smooth
    // around 0.25 and 0.75.
    uv = fract(uv + 0.25);
    float sm2 = SMOOTH * 0.5;
    // p01 oscillates between 0 and 1.
    vec2 p01 =
        smoothstep(0.25 - sm2, 0.25 + sm2, uv) -
        smoothstep(0.75 - sm2, 0.75 + sm2, uv);
    // pn11 oscillates between -1 and 1.
    vec2 pn11 = (p01 - 0.5) * 2.;
    // This results in smoothed 0..1 transitions.
    // We take advantage of the fact that multiplication
    // of -1/1 behaves like XOR. Then we rescale
    // -1..1 result of the multiplication to 0..1.
    return 0.5 - 0.5 * pn11.x * pn11.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2
        R = iResolution.xy,
        // Cartesian [-1, 1] along shorter axis.
        uv = (2. * fragCoord - R) / min(R.x, R.y);
    float
        T = iTime,
        // Angle [-PI, PI] and radius.
        a = atan(uv.x, uv.y),
        r = length(uv),
        // Twisted angle
        w = a - sin(1. / r) * r * ANGLE_SPEED * sin((T - 1./r) / ANGLE_TEMPO);

    vec2 polar = vec2(
        w * ARMS / PI2,
        1. / r * DENSITY + T * SPEED 
    );

    // vec3 checker = mix(COLOR1, COLOR2, smoothCheckerboard(polar));
    vec3 checker =
         mix(COLOR1, COLOR2,
         smoothstepCheckerboard(polar));
    // Light in the center.
    float light = smoothstep(0.7 + 0.3 * sin(T * PULSE_SPEED), 0.2, r);

    vec3 color = mix(checker, LIGHTCOLOR, light);
    // Approximate output gamma correction.
    fragColor = vec4(pow(color, vec3(1./2.2)), 1.);
}

const int MAX_DIST = 300;
const float EPSI = 0.009;

float random(vec2 p){
	return(fract(sin(p.x*431.+p.y*707.)*7443.));
}

float noise(vec2 uv){
	vec2 id = floor(uv*10.);
    vec2 lc = smoothstep(0.,1.,fract(uv*10.));

    float a = random(id);
    float b = random(id + vec2(1.,0.));
    float c = random(id + vec2(0.,1.));
    float d = random(id + vec2(1.,1.));

    float ud = mix(a,b,lc.x);
    float lr = mix(c,d,lc.x);
    float fin = mix(ud,lr,lc.y);
    return fin;
}

float octaves(vec2 uv){
    float amp = 0.5;
    float f = 0.;
    for(int i =1; i<5;i++){
    	f+=noise(uv)*amp;
        uv*=2.;
        amp*=0.5;
    }
    return f;
}
float SDF(vec3 p){
    vec3 spherePos = vec3(8,6,25);
    float sphere = length(p-spherePos)-1.;
    float water = p.y+8.+octaves((p.xz/30.)+(iTime/10.)+sin(length(p.xz*2.))*.04);
    float mindst = min(water,sphere);
    return mindst;
}

float rayMarcher(vec3 ro, vec3 rd){
	float tot = 0.;
    for(int i=0;i<MAX_DIST;i++){
    	vec3 p = ro+rd*tot;
        float diff = SDF(p);
        tot+=diff;
        if(diff<EPSI || tot>float(MAX_DIST)){
        	tot = float(i)/float(MAX_DIST-50);
            break;
        }
    }
    return tot;
}

