//voronoi particle tracking 

//loop the vector
vec2 loop_d(vec2 pos)
{
	return mod(pos + size*0.5, size) - size*0.5;
}

//loop the space
vec2 loop(vec2 pos)
{
	return mod(pos, size);
}


void Check(inout vec4 U, vec2 pos, vec2 dx)
{
    vec4 Unb = texel(ch0, loop(pos+dx));
    //check if the stored neighbouring particle is closer to this position 
    if(length(loop_d(Unb.xy - pos)) < length(loop_d(U.xy - pos)))
    {
        U = Unb; //copy the particle info
    }
}

void CheckRadius(inout vec4 U, vec2 pos, float r)
{
    Check(U, pos, vec2(-r,0));
    Check(U, pos, vec2(r,0));
    Check(U, pos, vec2(0,-r));
    Check(U, pos, vec2(0,r));
}

void mainImage( out vec4 U, in vec2 pos )
{
    vec2 muv = iMouse.xy/size;

    //this pixel value
    U = texel(ch0, pos);

    //check neighbours 
    CheckRadius(U, pos, 1.);
    CheckRadius(U, pos, 2.);
    CheckRadius(U, pos, 3.);
    CheckRadius(U, pos, 4.);
    CheckRadius(U, pos, 5.);

    U.xy = loop(U.xy);

    //cell cloning 
   // if(length(U.xy - pos) > 7.)
    //	U.xy += 1.*(hash22(pos)-0.5);

    //syncronizing the particles with the flow

    vec2 vel0 = pspeed*vec2(cos(U.z), sin(U.z)) + 0.1*(hash22(U.xy+iTime)-0.5);
    vec4 F =  pixel(ch1, U.xy);
    vec2 vel1 = F.yz;
    float dangl = 0.1*F.x*angle_between(vel0,vel1);
    U.z += dt*(dangl+0.1*(hash12(U.xy+iTime)-0.5)+0.4*sin(U.w));
   // U.w += dt*0.4;

    vec2 pvel = pspeed*vec2(cos(U.z), sin(U.z)) + 1.*(hash22(U.xy+iTime)-0.5);;

    //update the particle
    U.xy += dt*pvel;

    U.xy = loop(U.xy);


    if(iFrame < 1 || (iMouse.z > 0. && length(iMouse.xy - pos) < 30.) || (length(size.xy*0.5 - pos) < 30.))
    {
        U.xy = vec2(pdens*round(pos.x/pdens),pdens*round(pos.y/pdens));
        U.zw = 2.*PI*(hash22(U.xy) - 0.5);
    }
}

